import React, {useState, useEffect, useCallback} from 'react';
import { useTranslation } from 'react-i18next';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { useForm } from 'react-hook-form';
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { InputTextController } from '../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../components/PromotionComponent/DropdownController';

import { UomService } from "../../service/UomService";
import { SupplierService } from "../../service/SupplierService";
import { GeoService } from "../../service/GeoService";
import { Divider } from 'primereact/divider';

export const SupplierNewDialog = (props) => {
  const { display, toast , isCreateSuccess, setLoading, setValue, handleHide, setIsCreateSuccess } = props;
  const { t } = useTranslation('common');
  const [ currencyUoms, setCurrencyUoms] = useState([]);

  const [ countries, setCountries ] = useState([]);
  const [ stateProvinces, setStateProvinces ] = useState([]); 
  const [ districts, setDistricts ] = useState([]); 
  const [ wards, setWards ] = useState([]); 

  const msgError = t('valid.validFieldRequire');

  const defaultValues = {
    partyId: '',
    partyName: '',
    currencyUomId: {name: 'Vietnamese Dong', code: 'VND'},
    telecomNumber: '',
    emailAddress: '',
    partyTypeEnumId: {name: 'Organization', code: 'PtyOrganization'},

    countryGeoId: null,
    stateProvinceGeoId: null,
    districtId: null,
    wardId: null,
    tarAddress: '',
  };

  const schema = yup.object().shape({
    partyName: yup.string().required(msgError),
    currencyUomId: yup.object().nullable().required(msgError),
    emailAddress: yup.string().nullable().email(t('valid.invalidFormat')),
    telecomNumber: yup.string().nullable(),
    countryGeoId: yup.object().nullable().required(msgError),
    stateProvinceGeoId: yup.object().nullable().required(msgError),
    districtId: yup.object().nullable().required(msgError),
    wardId: yup.object().nullable().required(msgError),
    tarAddress: yup.string().required(msgError)
});

  const { control, formState: { errors, isSubmitting }, handleSubmit, reset} = useForm({ defaultValues, resolver: yupResolver(schema) });
  
  const supplierService = new SupplierService();
  const geoService = new GeoService();
  useEffect(() => {
    init();
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  const init = useCallback(async () => {
    const uomService = new UomService();
    const currencyUom = await uomService.getCurrencyMeasure();
    setCurrencyUoms(currencyUom?.uomList.map(item => ({
      name: item.description,
      code: item.uomId
    })));

    const countries = await geoService.getCountries([]);
    setCountries(countries.geoList.map(item => ({
      name: item.geoName,
      code: item.geoId
    })));
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const changeCountry = useCallback(async (e) => {
    const data = await geoService.getGeoRegionList(e.value.code, []);
    setStateProvinces(data.map(item => ({
      name: item.geoName,
      code: item.geoId
    })));
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const changeStateProvinces = useCallback(async (e) => {
    const data = await geoService.getGeoRegionList(e.value.code, []);
    setDistricts(data.map(item => ({
          name: item.geoName,
          code: item.geoId
      })));
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const changeDistrict = useCallback(async (e) => {
    const data = await geoService.getGeoRegionList(e.value.code, []);
      setWards(data.map(item => ({
        name: item.geoName,
        code: item.geoId
      })));
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const onSubmit = useCallback(async (data) => {
    try {
      setLoading(true);
      const params = {
        ...data,
        countryGeoId: data.countryGeoId?.code,
        districtId: data.districtId?.code,
        stateProvinceGeoId: data.stateProvinceGeoId?.code,
        wardId: data.wardId?.code,
        currencyUomId: data.currencyUomId?.code,
        partyTypeEnumId: data.partyTypeEnumId?.code,
      };
      console.log(params);
      const res = await supplierService.createSupplier(params);
      setLoading(false);
      if (res.partyId) {
        handleHide();
        setIsCreateSuccess(!isCreateSuccess);
      } else {
        return;
      }
    } catch (error) {
      toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error.response.data.errors, life: 5000 });
    }
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const renderFooter = () => {
    return (
      <>
        <Button label={t("save")} icon="pi pi-check" disabled={isSubmitting}
          onClick={handleSubmit(onSubmit)}/>
      </>
    );
  }
  const handleHideDialog = () => {
    handleHide();
    reset();
  }
  const getFormErrorMessage = (name) => {
    return errors[name] && <small className="p-error">{errors[name]?.message}</small>
  };

  return (
    <>
      <Dialog header={t("vendor.create")} visible={display} onHide={handleHideDialog}  
        style={{width: '65vw'}} footer={renderFooter} position={'top'} modal
      >
        <form>
          <div className="p-grid">
            <div className="p-col-12">
              <div className="p-fluid">
                <div className="p-grid">
                  <div className="p-col-12 p-md-6">
                    <InputTextController id='partyId' label={t('vendor.id')}
                      control={control} getFormErrorMessage={getFormErrorMessage}/>
                    <InputTextController id='partyName' label={t('vendor.name')} checkErr
                      control={control} getFormErrorMessage={getFormErrorMessage} />
                    <DropdownController id='currencyUomId' label={t('acc.newApInvoice.currencyUOM')} checkErr
                      options={currencyUoms} control={control} getFormErrorMessage={getFormErrorMessage} 
                      filter showClear filterBy="name"/>
                    <InputTextController id='telecomNumber' label={t('vendor.telecomNumber')}
                      control={control} getFormErrorMessage={getFormErrorMessage}/>
                    <InputTextController id='emailAddress' label={t('vendor.emailAddress')}
                      control={control} getFormErrorMessage={getFormErrorMessage} />
                  </div>  
                  <div className="p-col-12 p-md-6">
                    <DropdownController id='countryGeoId' label={t('vendor.countryGeo')} checkErr 
                      callback={changeCountry} options={countries} control={control} 
                      getFormErrorMessage={getFormErrorMessage} filter showClear filterBy="name"
                    />
                    <DropdownController id='stateProvinceGeoId' label={t('vendor.stateProvinceGeo')} checkErr 
                      callback={changeStateProvinces} options={stateProvinces} control={control} 
                      getFormErrorMessage={getFormErrorMessage} filter showClear filterBy="name"
                    />
                    <DropdownController id='districtId' label={t('vendor.district')} checkErr 
                      callback={changeDistrict} options={districts} control={control} 
                      getFormErrorMessage={getFormErrorMessage} filter showClear filterBy="name"
                    />
                    <DropdownController id='wardId' label={t('vendor.ward')} checkErr
                      options={wards} control={control} getFormErrorMessage={getFormErrorMessage} 
                      filter showClear filterBy="name"
                    />
                    <InputTextController id='tarAddress' label={t('vendor.tarAddress')} checkErr
                        control={control} getFormErrorMessage={getFormErrorMessage} 
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      </Dialog>
    </>
  );
}
