import React, { useCallback, useEffect, useState } from 'react';

import { useTranslation } from 'react-i18next';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { SalesReportService } from '../../service/SalesReportService';

export const SalesOrderInWeek = (props) => {
    const { height } = props;
    const [ t ] = useTranslation();

    const [data, setData] = useState([]);

    const init = useCallback(async () => {
        let endDate = new Date();
        let day = endDate.getDay();
        let diff = endDate.getDate() - day + (day === 0 ? -6 : 1);
        let startDate = new Date(endDate.setDate(diff));

        let params = {
            startDate: startDate.toISOString().slice(0,10), 
            endDate: endDate.toISOString().slice(0,10),
        }

        const salesReportService = new SalesReportService();

        let res = await salesReportService.olapChartTxtTorOrderStatus(params);
        setData(res);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        init();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const colSeqBodyTemplate = (rowData) => {
        return (
            <>
                {t('status:OrderHeader.' + rowData.statusId)}
            </>
        );
    }

    return (
        <div className="card height-100">
            <h5>{t('common:report.olapChartTxtTorOrderStatus')}</h5>
            <DataTable value={data} responsiveLayout="scroll" style={{height: height}} scrollable scrollHeight={height}>
                <Column field="statusId" body={colSeqBodyTemplate}></Column>
                <Column field="orders"></Column>
            </DataTable>
        </div>
    );
};
