import React, { useState, useContext } from 'react';
import { useTranslation } from 'react-i18next';
import { useHistory } from 'react-router-dom';
import { useParams } from "react-router-dom";

import { Button } from 'primereact/button';

import { CreateOrderHeader } from '../components/CreateOrderHeader';
import { CreateOrderProductList } from '../components/CreateOrderProductList';

import { OrderService } from '../service/OrderService';

import { ToastContext } from "../App";

export const CreateOrder = () => {
    const [ t ] = useTranslation();
    const toast = useContext(ToastContext);
    const history = useHistory();

    let { id } = useParams();

    const orderService = new OrderService();

    const [customerPartyId, setCustomerPartyId] = useState('');
    const [estimatedDeliveryDate, setEstimatedDeliveryDate] = useState('');
    const [facilityId, setFacilityId] = useState('');
    const [otherPartyOrderId, setOtherPartyOrderId] = useState('');
    const [priority, setPriority] = useState('');
    const [productStoreId, setProductStoreId] = useState('');
    const [shipBeforeDate, setShipBeforeDate] = useState('');
    const [vendorPartyId, setVendorPartyId] = useState('');

    const [orderItems, setOrderItems] = useState([]);
    const [grandTotal, setGrandTotal] = useState(0);
    
    const saveOrder = () => {
        if (productStoreId === '') {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: t('common:order.notBeEmpty', {field: t('common:productStore')}), life: 5000 });
            return;
        }

        if (vendorPartyId === '') {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: t('common:order.notBeEmpty', {field: t('common:vendor.id')}), life: 5000 });
            return;
        }

        if (facilityId === '') {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: t('common:order.notBeEmpty', {field: t('common:facility.id')}), life: 5000 });
            return;
        }

        let data = {
            'customerPartyId': customerPartyId,
            'estimatedDeliveryDate': estimatedDeliveryDate,
            'facilityId': facilityId,
            'otherPartyOrderId': otherPartyOrderId,
            'priority': priority,
            'productStoreId': productStoreId,
            'shipBeforeDate': shipBeforeDate,
            'vendorPartyId': vendorPartyId,
            'orderItems': orderItems,
            'salesChannelEnumId': 'ScWeb'
        }

        if (id) {
            data.orderId = id;
            orderService.editOrder(data).then(data => {
                if (data.orderId) {
                    history.push("/sales/order/detail/" + data.orderId);
                }
            })
        } else {
            orderService.createOrder(data).then(data => {
                if (data.orderId) {
                    history.push("/sales/order/detail/" + data.orderId);
                }
            });
        }
    }

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <h5>{t('common:order.createSaleOrder')}</h5>
                    <CreateOrderHeader 
                        setCustomerPartyId={setCustomerPartyId} 
                        setEstimatedDeliveryDate={setEstimatedDeliveryDate} 
                        setFacilityId={setFacilityId} 
                        setOtherPartyOrderId={setOtherPartyOrderId} 
                        setPriority={setPriority} 
                        setProductStoreId={setProductStoreId} 
                        setShipBeforeDate={setShipBeforeDate} 
                        setVendorPartyId={setVendorPartyId} 
                        id={id}
                    />
                    <CreateOrderProductList setOrderItems={setOrderItems} setGrandTotal={setGrandTotal} grandTotal={grandTotal} id={id} />

                    <div className="p-grid">
                        <div className="p-col-12 p-md-6"></div>
                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <div className="p-col-12 p-md-8">
                                    <h4>{t('common:order.grandTotal')}</h4>
                                </div>
                                <div className="p-col-12 p-md-4">
                                    {grandTotal}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6"></div>
                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <div className="p-col-12 p-md-8"></div>
                                <div className="p-col-12 p-md-4">
                                    <Button label={t('common:save')} className="p-button-primary p-mr-2 p-mb-2" onClick={saveOrder}/>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
