import React, { useCallback, useContext, useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';

import { InputTextController } from '../../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../../components/PromotionComponent/DropdownController';
import { CalendarController } from '../../../components/PromotionComponent/CalendarController';
import { PasswordController } from '../../../components/PromotionComponent/PasswordController';

import { CommonService } from '../../../service/CommonService';
import { UserProfileContext } from '../../../App';

export const GeneralInformation = (props) => {
    const { control, errors } = props;
    const [ t ] = useTranslation();
    const userProfile = useContext(UserProfileContext);

    const [userGroups, setUserGroups] = useState([]);
    const [listProductStoreId, setListProductStoreId] = useState([]);

    const init = useCallback(async () => {
        const commonService = new CommonService();
        let data = await commonService.getUserGroupList('UgtGsourcesUsers');
        setUserGroups(data.map(elm => {
            return {
                name: t('userGroup:' + elm.userGroupId),
                code: elm.userGroupId
            }
        }));

        data = await commonService.getProductStores(userProfile.info.partyId);
        setListProductStoreId(data.productStoreList.map((elm) => {
            return {
                name: elm.productStoreId,
                code: elm.productStoreId
            }
        }));
    }, [userProfile.info.partyId, t]);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name]?.message}</small>
    };
    
    const genders = [
        {name: t('common:male'), code: 'M'},
        {name: t('common:female'), code: 'F'}
    ];

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <InputTextController id='partyCode' label={t('common:salesman.id')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='fullName' label={t('common:salesman.fullName')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <DropdownController id='gender' label={t('common:salesman.gender')} 
                                options={genders} control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <CalendarController id='birthDate' label={t('common:salesman.birthDate')} showIcon monthNavigator yearNavigator yearRange="1950:2030"
                                showButtonBar control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <DropdownController id='userGroupId' label={t('common:salesman.userGroup')} 
                                options={userGroups} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='productStoreId' label={t('common:productStore')} 
                                options={listProductStoreId} control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            <InputTextController id='username' label={t('common:userAccount.username')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <PasswordController id='newPassword' label={t('common:userAccount.password')} checkErr type="password"
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <PasswordController id='newPasswordVerify' label={t('common:userAccount.passwordVerify')} checkErr type="password"
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
