import React, { useCallback, useEffect, useState } from 'react';

import { useTranslation } from 'react-i18next';

import { Button } from 'primereact/button';

import { BarChart } from '../../components/Report';

import { SalesReportService } from '../../service/SalesReportService';

import { DialogDate } from './Dialog';

export const StoreValueBarChart = (props) => {
    const { options, cycles, height } = props;

    const [data, setData ] = useState({});
    const [display, setDisplay] = useState(false);

    const [option, setOption] = useState();
    const [cycle, setCycle] = useState({});
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);

    const [ t ] = useTranslation('common');

    const init = useCallback(async () => {

        setOption({ name: t('report.option'), code: 'option' });
        setCycle({ name: t('report.week'), code: 'week' });

        let endDate = new Date();
        let startDate = new Date(endDate.getFullYear(), endDate.getMonth(), 1);
        setToDate(endDate);
        setFromDate(startDate);

        const salesReportService = new SalesReportService();

        let params = {
            startDate: startDate.toISOString().slice(0,10), 
            endDate: endDate.toISOString().slice(0,10),
            dateType: 'week'
        }
        let res = await salesReportService.olapChartColTorProdStoreMini(params);
        res.datasets[0].label = t('report.salesValue');
        setData(res);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        init();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const search = useCallback(async () => {
        const salesReportService = new SalesReportService();

        let params = {
            startDate: fromDate.toISOString().slice(0,10), 
            endDate: toDate.toISOString().slice(0,10),
            dateType: cycle.code,
        }
        let res = await salesReportService.olapChartColTorProdStoreMini(params);
        res.datasets[0].label = t('report.salesValue');
        setData(res);
        setDisplay(false);
    }, [fromDate, toDate, cycle, t]);

    return (
        <>
            <div className="card height-100">
                <div className="card-header">
                    <h5>{t('report.olapChartColTorProdStoreMini')}</h5>
                    <Button icon="pi pi-cog" onClick={() => setDisplay(true)} />
                </div>
                <BarChart data={data} height={height} />
            </div>

            <DialogDate
                cycles={cycles} 
                search={search} options={options} 
                display={display} setDisplay={setDisplay} 
                
                option={option} setOption={setOption} 
                cycle={cycle} setCycle={setCycle} 
                fromDate={fromDate} setFromDate={setFromDate} 
                toDate={toDate} setToDate={setToDate} 
                
            />
        </>
    );
};
