import { Const, ServiceHandle } from '../utilities';

export class SupplierService {
    getSuppliers(params) {
        const _params = {...params};
        _params.roleTypeId = 'Supplier';
        return ServiceHandle.get(Const.API.Mantle.Parties + '/search', {params: _params}).then(res => {
          return {
            totalRecords: res.data.documentListCount,
            suppliers: res.data.documentList
          }
        });
    }

    createSupplier(params) {
      return ServiceHandle.post(Const.API.Suppliers, params).then((res) => res.data);
    }
  

}
