import React, { useCallback} from 'react';
import { useTranslation } from 'react-i18next';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import moment from 'moment';

export const ProductVendorList = (props) => {
  const {vendorProducts, vendorProductSelected, setVendorProductSelected} = props;

  const { t, i18n } = useTranslation('common');
  const numberBodyTemplate = useCallback((data, props) => {
    return (
        <p style={{paddingRight: '0.75rem'}}>{data[props.field]}</p>
    );
  }, []);

  const currencyBodyTemplate =  useCallback((data, props) => {
    const numberFormat = (data, props) =>
      new Intl.NumberFormat(i18n.language === "vi" ? "vi-VN" : "en-US", {
        style: "currency",
        currency: data.priceUomId,
      }).format(data[props.field]);
    const value = data[props.field] != null ? numberFormat(data, props) : "";
    return (
      <>
        <p style={{paddingRight: '0.75rem'}}>{value}</p>
      </>
    );
  }, [i18n.language]);

  const bodyTemplate = useCallback((data, props) => {
    return (
      <>
          <span>{data[props.field]}</span>
      </>
    );
}, []);

  const bodyDateTemplate = useCallback((data, props) => {
    return (
      <>
          <span>{data[props.field] ? moment(data[props.field]).format('DD/MM/YYYY HH:mm:ss') : ''}</span>
      </>
    );
}, []);

  return (
  <>
    <DataTable
      value={vendorProducts}
      resizableColumns
      columnResizeMode="fit"
      showGridlines
      dataKey="id"
      selectionMode="single"
      selection={vendorProductSelected} onSelectionChange={e => setVendorProductSelected(e.value)}
      emptyMessage={t("common:dataIsEmpty")}
    >
      <Column
        bodyStyle={{ padding: "0 0.75rem" }}
        field="vendorPartyName"
        header={t("vendor.label")}
        body={bodyTemplate}
      />
      <Column
        headerStyle={{ backgroundColor: "#f8f9fa", width: "7rem" }}
        field="minQuantity"
        header={t("product.minQuantity")}
        body={numberBodyTemplate}
      />
      <Column
        headerStyle={{ backgroundColor: "#f8f9fa", width: "9rem" }}
        bodyStyle={{ padding: "0", textAlign: 'right' }}
        field="purchasePrice"
        header={t("product.purchasePrice")}
        body={currencyBodyTemplate}
      />
      <Column
        headerStyle={{ backgroundColor: "#f8f9fa", width: "9rem" }}
        bodyStyle={{ padding: "0", textAlign: 'right' }}
        field="quantityUomId"
        header={t("product.unitPacking")}
        body={bodyTemplate}
      />
      <Column 
        field="fromDate" 
        headerStyle={{ backgroundColor: "#f8f9fa", width: "11rem" }} 
        header={t('common:productPrice.fromDate')} 
        body={bodyDateTemplate}/>
    </DataTable>
  </>
  );
}
