import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import React, {useEffect, useRef, useState} from "react";
import {useTranslation} from "react-i18next";
import {InputText} from "primereact/inputtext";
import {Dialog} from "primereact/dialog";
import {Dropdown} from "primereact/dropdown";
import {InputNumber} from "primereact/inputnumber";
import {InvoiceReceivableService} from "../../service/accounting/InvoiceReceivableService";
import {Button} from "primereact/button";
import {Toast} from "primereact/toast";
import {InvoiceItemTypeService} from "../../service/accounting/InvoiceItemTypeService";
import { v4 as uuidv4 } from 'uuid';

export const InvoiceDetail = () => {
    // const [invoices, setInvoices] = useState(null);
    const {t} = useTranslation('common');
    // const [tableData, setTableData] = useState(null);
    const [invoiceItemTypes, setInvoiceItemTypes] = useState(null);
    // const [loading, setLoading] = useState(false);
    const toast = useRef(null);
    const [dialog, setDialog] = useState(false);
    // const [submitted, setSubmitted] = useState(false);
    // const [parties, setParties] = useState(null);
    const [products, setProducts] = useState(null);
    // const [invoiceTypes, setInvoiceTypes] = useState(null);
    const [value1, setValue1] = useState('');
    const [itemTypeEnumId, setItemTypeEnumId] = React.useState('');
    const [description, setDescription] = React.useState('');
    const [productId, setProductId] = React.useState('');
    const [quantity, setQuantity] = React.useState(0);
    const [price, setPrice] = React.useState(0);


    // useEffect(() => {
    //     const invoiceTypeService = new InvoiceTypeService();
    //     invoiceTypeService.getInvoiceTypes().then(data => {
    //         setTableData(data);
    //     });
    // }, []);

    useEffect(() => {
        const invoiceItemTypeService = new InvoiceItemTypeService();
        invoiceItemTypeService.getInvoiceItemTypes().then(data => {
            setInvoiceItemTypes(data);
        });
    }, []);

    useEffect(() => {
        const invoiceReceivableService = new InvoiceReceivableService();
        // invoiceReceivableService.getInvoices().then(data => {
        //     setInvoices(data);
        // });
        // invoiceReceivableService.getInvoiceTypes().then(data => {
        //     setInvoiceTypes(data);
        // });
        // invoiceReceivableService.getListPartyId().then(data => {
        //     setParties(data.partyIdList);
        // });
