import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import {useTranslation} from "react-i18next";
import {FacilityService} from "../../service/FacilityService";
import {Button} from "primereact/button";
import {Dialog} from "primereact/dialog";
import {Dropdown} from "primereact/dropdown";
import {InputNumber} from "primereact/inputnumber";
import {ServiceHandle} from "../../utilities";
import Const from "../../utilities/Const";
import {getTokenCookie} from "../../TokenCookie";
import {ProductService} from "../../service/ProductService";
// import {EnumService} from "../../service/EnumService";

export const InStockConfig = () => {
    const {t} = useTranslation('common');
    const [tableData, setTableData] = useState(null);
    // const [loading, setLoading] = useState(false);
    const toast = useRef(null);
    const [dialog, setDialog] = useState(false);
    const [facilities, setFacilities] = useState(null);
    const [products, setProducts] = useState(null);
    const [value1, setValue1] = useState('');
    const [value2, setValue2] = useState('');
    const [value3, setValue3] = useState('');
    const [value4, setValue4] = useState('');
    const [value5, setValue5] = useState('');
    const [value6, setValue6] = useState('');
    // const [invoiceTypes, setInvoiceTypes] = useState(null);
    // const [submitted, setSubmitted] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const dt = useRef(null);

    useEffect(() => {
        const facilityService = new FacilityService();
        facilityService.getListProductFacility().then(data => {
            setTableData(data.resultList);
            setTotalRecords(0); //fake
        });

        facilityService.getAllFacilities().then(data => {
           setFacilities(data);
        });
        const productService = new ProductService();
        productService.getAllProducts().then(data => {
            setProducts(data.productList);
        });
    }, []);

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    // let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const bodyTemplate = (data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    };

    const bodyTemplateForDates = (data, props) => {
        return (
          <>
              <span className="p-column-title">{props.header}</span>
              {data[props.field]} {" (Ngày)"}
          </>
        );
    };

    const openNew = () => {
        setDialog(true);
    }

    const hideDialog = () => {
        // setSubmitted(false);
        setDialog(false);
    }

    const saveInvoice = () => {
        // setSubmitted(true);
        ServiceHandle.post(Const.API.SetProductFacility_Log,
          {
              productId: value1.productId,
              facilityId: value2.facilityId,
              thresholdsDate: value3,
              thresholdsSale: value4,
              minimumStock: value5,
              thresholdsQuantity: value6
          },
          {
              headers: {
                  moquiSessionToken: getTokenCookie()
              }
          },
        )
          .then(function (response) {
                //
          })
          .catch(function (error) {
                //
          });
        toast.current.show({severity: 'success', summary: t('logistics.inStockConfig.addNewSuccessTitle'), detail: t('logistics.inStockConfig.noticeSuccess'), life: 3000});
        setDialog(false);
    }

    const dialogFooter = (
      <>
          <Button label={t('logistics.inStockConfig.btnCancel')} icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
          <Button label={t('logistics.inStockConfig.btnSave')} icon="pi pi-check" className="p-button-text" onClick={saveInvoice} />
      </>
    );

    return (
        <div>
            <div className="p-col-12">
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-9 p-md-9">
                            <h3>{t('logistics.inStockConfig.title')}</h3>
                        </div>
                        <div className="p-col-3 p-md-3">
                            <div className="p-grid">
                                <Button label={t('logistics.inStockConfig.btnAddNew')} icon="pi pi-plus" className="p-button-outlined" onClick={openNew} />&nbsp;&nbsp;
                                <Button label={t('acc.invReceivable.btnDelete')} icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/>&nbsp;&nbsp;
                                <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} />
                            </div>
                        </div>
                    </div>

                    <DataTable ref={dt} value={tableData} paginator className="p-datatable-customers"
                               dataKey="facilityId" rowHover emptyMessage={t('logistics.dateWithFacilityConfig.emptyMessage')}
                               selectionMode="single" onSelectionChange={(e) => setSelectedItem(e.value)}
                               filters onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows}
                               totalRecords={totalRecords} onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi"
                               lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                               resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>


                        <Column field="productId" header={t('logistics.inStockConfig.productCode')} body={bodyTemplate} filter/>
                        <Column field="productName" header={t('logistics.inStockConfig.productName')} body={bodyTemplate} filter/>
                        <Column field="facilityName" header={t('logistics.inStockConfig.facility')} body={bodyTemplate} filter/>
                        <Column field="thresholdsSale" header={t('logistics.inStockConfig.thresholdsDate')} body={bodyTemplateForDates} filter/>
                        <Column field="thresholdsDate" header={t('logistics.inStockConfig.dateWarning')} body={bodyTemplateForDates} filter/>
                        <Column field="minimumStock" header={t('logistics.inStockConfig.minQuantity')} body={bodyTemplate} filter/>
                        <Column field="thresholdsQuantity" header={t('logistics.inStockConfig.maxQuantity')} body={bodyTemplate} filter/>
                    </DataTable>
                </div>

                <Dialog visible={dialog} style={{ width: '450px'}} header={t('logistics.inStockConfig.addNewTitle')} modal className="p-fluid" footer={dialogFooter} onHide={hideDialog}>
                    <div className="p-field">
                        <label htmlFor="dropdown1">{t('logistics.inStockConfig.inputProduct')}<span style={{color: 'red'}}>*</span></label>
                        <Dropdown id="dropdown1" options={products} value={value1} onChange={(e) => setValue1(e.value)} appendTo={document.body} optionLabel="productName"></Dropdown>
                    </div>
                    <div className="p-field">
                        <label htmlFor="dropdown2">{t('logistics.inStockConfig.facility')}<span style={{color: 'red'}}>*</span></label>
                        <Dropdown id="dropdown2" options={facilities} value={value2} onChange={(e) => setValue2(e.value)} appendTo={document.body} optionLabel="facilityName"></Dropdown>
                    </div>
                    <div className="p-field">
                        <label htmlFor="inputNumber1">{t('logistics.inStockConfig.thresholdsDate')}</label>
                        <InputNumber id="inputNumber1" value={value3} onChange={(e) => setValue3(e.value)}/>
                    </div>
                    <div className="p-field">
                        <label htmlFor="inputNumber2">{t('logistics.inStockConfig.dateWarning')}</label>
                        <InputNumber id="inputNumber2" value={value4} onChange={(e) => setValue4(e.value)}/>
                    </div>
                    <div className="p-field">
                        <label htmlFor="inputNumber3">{t('logistics.inStockConfig.minQuantity')}</label>
                        <InputNumber id="inputNumber3" value={value5} onChange={(e) => setValue5(e.value)}/>
                    </div>
                    <div className="p-field">
                        <label htmlFor="inputNumber4">{t('logistics.inStockConfig.maxQuantity')}</label>
                        <InputNumber id="inputNumber4" value={value6} onChange={(e) => setValue6(e.value)}/>
                    </div>
                </Dialog>
            </div>
        </div>
    )
}
