import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import React from 'react';
import { useTranslation } from 'react-i18next';

export const ShortcutHelp = (props) => {
  const { onHide } = props;

  const { t } = useTranslation('common');

  const renderFooter = (
    <div>
      <Button label={t('close')} icon="pi pi-times" onClick={onHide} className="p-button-text" />
    </div>
  );

  return (
    <Dialog header={t('pos.shortcutList')} {...props} style={{ width: '60vw' }} footer={renderFooter} onHide={onHide}>
      <div className="p-col p-pt-3">
        <div className="p-grid">
          <div className="p-col-12 p-md-6">
            <div className="p-mb-3 p-text-bold">F1 : {t('pos.searchProduct')}</div>
            <div className="p-mb-3 p-text-bold">F2 : {t('pos.searchCustomer')}</div>
            <div className="p-mb-3 p-text-bold">F3 : {t('pos.payment')}</div>
            <div className="p-mb-3 p-text-bold">F5 : {t('pos.holdOrder')}</div>
            <div className="p-mb-3 p-text-bold">F7 : {t('pos.openOrder')}</div>
          </div>
          <div className="p-col-12 p-md-6">
            <div className="p-mb-3 p-text-bold">F10 : {t('pos.cancelOrder')}</div>
            <div className="p-mb-3 p-text-bold">+,- : {t('pos.addOrReduceQuantity')}</div>
            <div className="p-mb-3 p-text-bold">Shift + Delete : {t('pos.deleteProduct')}</div>
            <div className="p-mb-3 p-text-bold">Ctrl + L : {t('pos.listPromotionForStore')}</div>
            <div className="p-mb-3 p-text-bold">Ctrl + H : {t('pos.salesOrderHistory')}</div>
          </div>
        </div>
      </div>
    </Dialog>
  );
};
