import React, { useCallback, useContext, useState } from 'react';
import { useTranslation } from 'react-i18next';

import { GeneralInformation } from './GeneralInformation';
import { ContactInformation } from './ContactInfomation';

import { Steps } from 'primereact/steps';
import { Button } from 'primereact/button';

import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { CustomerService } from '../../../service/CustomerService';
import { ToastContext } from '../../../App';

export const PersonalCreateCustomer = () => {
    const [t] = useTranslation('common');
    const toast = useContext(ToastContext);
    const customerService = new CustomerService();
    const history = useHistory();

    const [activeIndex, setActiveIndex] = useState(0);

    const schema = yup.object().shape({
        fullName: yup.string().required(t('valid.validFieldRequire')),
        partyTypeEnumId: yup.object().nullable().required(t('valid.validFieldRequire')),
        productStoreId: yup.object().nullable().required(t('valid.validFieldRequire')),

        countryGeoId: yup.object().nullable().required(t('valid.validFieldRequire')),
        stateProvinceGeoId: yup.object().nullable().required(t('valid.validFieldRequire')),
        districtId: yup.object().nullable().required(t('valid.validFieldRequire')),
        wardId: yup.object().nullable().required(t('valid.validFieldRequire')),
        tarAddress: yup.string().required(t('valid.validFieldRequire')),
    });

    const defaultValues = {
        partyId: '',
        fullName: '',
        taxInformation: '',
        partyTypeEnumId: {name: 'Person', code: 'PtyPerson'},
        productStoreId: null,
        description: '',

        // currencyUomId: null,
        officeSiteName: '',
        salesmanId: null,
        routeId: null,
        unitId: null,

        countryGeoId: null,
        stateProvinceGeoId: null,
        districtId: null,
        wardId: null,
        tarAddress: '',
        telecomNumber: '',
        emailAddress: '',

        latitude: '',
        longitude: ''
    }
    const { control, formState: { errors, isSubmitting }, handleSubmit, setError, clearErrors, setValue } = useForm({ defaultValues, resolver: yupResolver(schema) });

    const items = [
        { label: t('customer.generallInformation') },
        { label: t('customer.contactInfomation') },
        { label: t('customer.confirmation') }
    ];

    const onSubmit = useCallback(async (data) => {
        try {
            let params = {
                ...data,
                countryGeoId: data.countryGeoId?.code,
                districtId: data.districtId?.code,
                partyTypeEnumId: data.partyTypeEnumId?.code,
                productStoreId: data.productStoreId?.code,
                stateProvinceGeoId: data.stateProvinceGeoId?.code,
                wardId: data.wardId?.code
            };
            let res = await customerService.createPersonalCustomer(params);
            if (res.partyId) {
                history.push("/sales/personalcustomer/detail/" + res.partyId)
            }
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error.response.data.errors, life: 5000 });
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const validate = (name) => {
        let flag = true;
        try {
          yup.reach(schema, name).validateSync(control._formValues[name]);
          clearErrors(name);
        } catch (error) {
          setError(name, {
            type: "manual",
            message: error.message,
          });
          flag = false;
        }
    
        return flag;
    }

    const next = (e) => {
        let flag = true;

        flag = validate('fullName') ? flag : false;
        flag = validate('partyTypeEnumId') ? flag : false;
        flag = validate('productStoreId') ? flag : false;

        if (activeIndex < 2 && flag) {
          setActiveIndex(activeIndex + 1);
        } 
    }
    
    const previous = (e) => {
        if (activeIndex > 0) {
            setActiveIndex(activeIndex - 1);
        }
    }

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <h5>{t('customer.create')}</h5>
                    <Steps model={items} activeIndex={activeIndex} />

                    {activeIndex === 0 && <GeneralInformation control={control} errors={errors} />}
                    {activeIndex === 1 && <ContactInformation control={control} errors={errors} setValue={setValue} />}

                    <div className="p-grid">
                        <div className="p-col-12 p-md-6"></div>
                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <div className="p-col-12 p-md-6"></div>
                                <div className="p-col-12 p-md-3">
                                    <Button disabled={activeIndex === 0} label={t('previous')} className="p-button-primary p-mr-2 p-mb-2" onClick={(e) => previous(e)} />
                                </div>
                                <div className="p-col-12 p-md-3">
                                    {activeIndex === 0 && <Button disabled={activeIndex === 2} label={t('next')} className="p-button-primary p-mr-2 p-mb-2" onClick={(e) => next(e)} />}

                                    {activeIndex === 1 && <Button disabled={isSubmitting} label={t('save')} className="p-button-primary p-mr-2 p-mb-2" onClick={handleSubmit(onSubmit)} />}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};
