import React, { useCallback, useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';

import { TextWithLabel } from '../../../components/LabelWithText';

import { CommonService } from '../../../service/CommonService';
import moment from 'moment';

export const GeneralInformation = (props) => {
    const { employee, partyId } = props;
    const [ t ] = useTranslation();

    const [userGroups, setUserGroups] = useState('');
    const [storeNames, setStoreNames] = useState('');

    const init = useCallback(async () => {
        const commonService = new CommonService();
        let data = await commonService.getUserGroupMember(partyId);
        let groupIdArr = data.userGroups.map(elm => t('userGroup:' + elm.userGroupId));
        setUserGroups(groupIdArr.toString());

        data = await commonService.getProductStores(partyId);
        let storeNameArr = data.productStoreList.map(elm => elm.storeName);
        setStoreNames(storeNameArr.toString());
    }, [partyId, t]);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <TextWithLabel label={t('common:salesman.id')} text={employee?.partyCode} />

                            <TextWithLabel label={t('common:salesman.fullName')} text={employee?.fullName} />

                            <TextWithLabel label={t('common:salesman.gender')} text={employee?.gender} />

                            <TextWithLabel label={t('common:salesman.birthDate')} text={
                                employee?.birthDate ? moment(employee.birthDate).format('DD/MM/YYYY') : ''
                            } />

                            <TextWithLabel label={t('common:salesman.userGroup')} text={userGroups} />

                            <TextWithLabel label={t('common:productStore')} text={storeNames} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            <TextWithLabel label={t('common:customer.telecomNumber')} text={employee?.telecomNumber} />

                            <TextWithLabel label={t('common:customer.emailAddress')} text={employee?.emailAddress} />

                            <TextWithLabel label={t('common:customer.countryGeoId')} text={employee?.countryGeoName} />

                            <TextWithLabel label={t('common:customer.stateProvinceGeoId')} text={employee?.stateProvinceGeoName} />

                            <TextWithLabel label={t('common:customer.districtId')} text={employee?.countyGeoName} />

                            <TextWithLabel label={t('common:customer.wardId')} text={employee?.wardName} />                    

                            <TextWithLabel label={t('common:customer.tarAddress')} text={employee?.postalAddress} /> 
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
