import React, { useState, useEffect, useContext } from 'react';
import { useTranslation } from 'react-i18next';
import moment from 'moment';
import { useParams } from "react-router-dom";

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';

import { ToastContext } from "../../../App";
import { UserProfileContext } from "../../../App";
import { POSService } from '../../../service/POSService';
import { PromotionService } from '../../../service/PromotionService';
import { Loading } from '../../../components/Loading';

export const PromotionDetail = () => {
    const { t } = useTranslation('common');
    const toast = useContext(ToastContext);
    const { id } = useParams();
    const userProfile = useContext(UserProfileContext);

    const [promoDetail, setPromoDetail] = useState(null);
    const [promoRules, setPromoRules] = useState(null);
    const [loading, setLoading] = useState(false);

    const posService = new POSService();
    const promotionService = new PromotionService();

    const ruleNameTemplate = (rowData) => {
        let parameterName = rowData.parameterName;
        let temp = parameterName.replace(new RegExp('^.*\\.', 'g'), '');
        temp = temp.replace(new RegExp('_\\d+$', 'g'), '');
        let ruleName = t('promotion.' + temp);

        let index = parameterName.indexOf('_');
        if (index > -1) {
            let num = parseInt(parameterName.substring(index + 1)) + 1;
            ruleName += ' ' + num;
        }

        return (
            <React.Fragment>
                <span>{ruleName}</span>
            </React.Fragment>
        );
    };

    const ruleValueTemplate = (rowData) => {
        return (
            <React.Fragment>
                <span>{rowData.parameterValue}</span>
            </React.Fragment>
        );
    };

    useEffect(() => {
        setLoading(true);
        let params = {
            storePromotionId: id
        };

        posService.getPromoDetail(params).then((data) => {
            setPromoDetail(data.promoDetail);
            setPromoRules(data.promoRules);
        })
            .catch(err => {
                toast.current.show({ severity: 'error', summary: 'Error Message', detail: err.response.data.errors, life: 5000 });
            }).finally(() => {
                setLoading(false);
            });
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const expired = (e) => {
        setLoading(true);
        let params = {
            storePromotionId: id
        };

        promotionService.expiredPromotion(params).then((data) => {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: t('common:message.' + data.message), life: 5000 });
        })
        .catch(err => {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: err.response.data.errors, life: 5000 });
        }).finally(() => {
            setLoading(false);
        });
    }

    return (
        <>
            <div className="p-grid">
                <div className="p-col-12">
                    <div className="card p-fluid">
                        <div className="p-grid">
                            <div className="p-col-12 p-md-8">
                                <h5>{t('promotion.promotionDetai')}</h5>
                            </div>
                            <div className="p-col-12 p-md-4">
                                <div className="p-grid">
                                    <div className="p-col-12 p-md-3"></div>
                                    <div className="p-col-12 p-md-5">
                                        {userProfile.roles.includes('ADMIN') && <Button label={t('common:approve')} icon="pi pi-check" className="p-button-text p-button-success" onClick={() => console.log("Approve")} />}
                                    </div>
                                    <div className="p-col-12 p-md-4">
                                        {userProfile.roles.includes('ADMIN') && <Button label={t('common:expired')} icon="pi pi-times" className="p-button-text p-button-danger" onClick={(e) => expired(e)} />}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="p-grid">
                            <div className="p-col-12 p-md-6">
                                <div className="p-mb-3 p-text-bold">
                                    {t('promotion.serviceRegister')}:&nbsp;
                                    {promoDetail?.description ? promoDetail?.description : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.promoCode')}:&nbsp;
                                    {promoDetail?.storePromotionId ? promoDetail?.storePromotionId : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.promoName')}:&nbsp;
                                    {promoDetail?.itemDescription ? promoDetail?.itemDescription : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.startDate')}:&nbsp;
                                    {promoDetail?.fromDate ? moment(promoDetail.fromDate).format('DD/MM/YYYY HH:mm:ss') : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.endDate')}:&nbsp;
                                    {promoDetail?.thruDate ? moment(promoDetail.thruDate).format('DD/MM/YYYY HH:mm:ss') : ''}
                                </div>
                            </div>
                            <div className="p-col-12 p-md-6">
                                <div className="p-mb-3 p-text-bold">
                                    {t('promotion.sequenceNum')}:&nbsp;
                                    {promoDetail?.sequenceNum ? promoDetail?.sequenceNum : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.requestVoucher')}:&nbsp;
                                    {promoDetail?.requireCode ? (promoDetail?.requireCode === 1 ? t('yes') : t('no')) : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.limitOnOrder')}:&nbsp;
                                    {promoDetail?.useLimitPerOrder ? promoDetail?.useLimitPerOrder : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.limitOnCustomer')}:&nbsp;
                                    {promoDetail?.useLimitPerCustomer ? promoDetail?.useLimitPerCustomer : ''}
                                </div>
                                <div className="p-mb-3 p-text-bold">
                                    {t('pos.limitOnPromo')}:&nbsp;
                                    {promoDetail?.useLimitPerPromotion ? promoDetail?.useLimitPerPromotion : ''}
                                </div>
                            </div>
                        </div>
                        <div className="p-grid">
                            <DataTable selectionMode="single" dataKey="id" showGridlines value={promoRules}>
                                <Column field="code" header={t('pos.lawName')} body={ruleNameTemplate}></Column>
                                <Column field="name" header={t('pos.value')} body={ruleValueTemplate}></Column>
                            </DataTable>
                        </div>
                    </div>
                </div>
            </div>
            <Loading visible={loading} onHide={() => setLoading(false)} />
        </>
    );
};
