import React, {useEffect} from 'react';
import { TabView, TabPanel } from 'primereact/tabview';

import { useParams } from "react-router-dom";
import { useTranslation } from 'react-i18next';

import { GeneralInformation } from './GeneralInformation';
import { Price } from './Price';
import { Supplier } from './Supplier';
import { Promotion } from './Promotion';

export const ProductDetail = () => {
    const { t } = useTranslation('common');
    const { id } = useParams();

    useEffect(() => {
        document.title = t('title.productDetail');

        return () => {
            document.title = t('title.homepage');
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card">
                <h4>{t('title.productDetail')}</h4>
                <TabView>
                    <TabPanel header={t('product.generalInformation')}>
                        <GeneralInformation productId={id} />
                    </TabPanel>
                    <TabPanel header={t('product.price')}>
                        <Price productId={id} />
                    </TabPanel>
                    <TabPanel header={t('product.supplier')}>
                        <Supplier productId={id} />
                    </TabPanel>
                    <TabPanel header={t('product.promotion')}>
                        <Promotion productId={id} />
                    </TabPanel>
                    {/* <TabPanel header={t('product.uomConversion')}>
                        {t('product.uomConversion')}
                    </TabPanel> */}
                </TabView>
                </div>
            </div>
        </div>
    );
}
