import React, { useEffect, useRef, useState, useCallback } from 'react';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { useTranslation } from 'react-i18next';
import moment from 'moment';

import { ProductService } from '../../../service/ProductService';
import { OrganizationService } from '../../../service/OrganizationService';

export const Supplier = (props) => {

    const { t } = useTranslation();
    const productService = new ProductService();
    const organizationService = new OrganizationService();

    const dt = useRef(null);
    const [ priceVendor, setPriceVendor ] = useState(null);

    const { productId } = props;

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        let params = {
            productId: productId,
            isVendor: true
        };
        const price = await productService.getProductPrice(params);
        
        if (price.productPrice) {
            let newarr = []
            for (const elm of price.productPrice) {
                params = {
                    partyId: elm.vendorPartyId
                }
                const vendor = await organizationService.getOrganization(params);

                newarr.push({
                    ...elm, 
                    ...vendor.organization
                }); 
            }
            setPriceVendor(newarr);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const bodyTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    }, []);

    const bodyDateTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field] ? moment(data[props.field]).format('DD/MM/YYYY HH:mm:ss') : ''}
            </>
        );
    }, []);

    return(
        <>
            <div className="card">
                <div className="p-grid p-mt-2 table-demo">
                    <div className="p-col-12 p-md-12">
                        <h5>{t('common:product.basePrice')}</h5>
                        <DataTable value={priceVendor} className="p-datatable-customers" dataKey="productId" ref={dt}>
                            <Column field="partyId" header={t('common:vendor.id')} body={bodyTemplate}></Column>
                            <Column field="organizationName" header={t('common:vendor.label')} body={bodyTemplate}></Column>
                            <Column field="minQuantity" header={t('common:product.minQuantity')} body={bodyTemplate}></Column>
                            <Column field="price" header={t('common:product.purchasePrice')} body={bodyTemplate}></Column>
                            <Column field="priceUomId" header={t('common:acc.newArInvoice.currencyUOM')} body={bodyTemplate}></Column>
                            <Column field="fromDate" header={t('common:productPrice.fromDate')} body={bodyDateTemplate}></Column>
                            <Column field="thruDate" header={t('common:productPrice.thruDate')} body={bodyDateTemplate}></Column>
                        </DataTable>
                    </div>
                </div>
            </div>
        </>
    );
}
