import React, { useCallback, useContext, useState, useEffect, useMemo } from 'react';
import { useTranslation } from 'react-i18next';

import { InputTextController } from '../../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../../components/PromotionComponent/DropdownController';
import { CalendarController } from '../../../components/PromotionComponent/CalendarController';
import { PasswordController } from '../../../components/PromotionComponent/PasswordController';

import { CommonService } from '../../../service/CommonService';
import { SalesmanService } from '../../../service/SalesmanService';

import { UserProfileContext } from '../../../App';
import { ToastContext } from '../../../App';

export const GeneralInformation = (props) => {
    const { control, errors, partyId, setLoading, setValue } = props;
    const [ t ] = useTranslation();
    const userProfile = useContext(UserProfileContext);
    const toast = useContext(ToastContext);

    const [userGroups, setUserGroups] = useState([]);
    const [listProductStoreId, setListProductStoreId] = useState([]);

    const genders = useMemo(() => {
        return [
            {name: t('common:male'), code: 'M'},
            {name: t('common:female'), code: 'F'}
        ]
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        setLoading(true);
        try {
            const salesmanService = new SalesmanService();
            let data = await salesmanService.getSaleEmployeeDetail({partyId: partyId});

            setValue('partyCode', data.employee.partyCode);
            setValue('fullName', data.employee.fullName);
            setValue('gender', genders.find(elm => elm.code === data.employee.gender));
            setValue('birthDate', new Date(data.employee.birthDate));
            setValue('partyCode', data.employee.partyCode);

            setValue('username', data.userAccount.username);

            // usergroup
            const commonService = new CommonService();
            data = await commonService.getUserGroupList('UgtGsourcesUsers');
            let userGroups = data.map(elm => {
                return {
                    name: t('userGroup:' + elm.userGroupId),
                    code: elm.userGroupId
                }
            });

            data = await commonService.getUserGroupMember(partyId);
            let groupId = data.userGroups[0].userGroupId;

            setUserGroups(userGroups);
            setValue('userGroupId', userGroups.find(elm => elm.code === groupId));

            // get product store
            data = await commonService.getProductStores(userProfile.info.partyId);
            let productStores = data.productStoreList.map((elm) => {
                return {
                    name: elm.storeName,
                    code: elm.productStoreId
                }
            });
            data = await commonService.getProductStores(partyId);
            let storeId = data.productStoreList[0].productStoreId;

            setListProductStoreId(productStores);
            setValue('productStoreId', productStores.find(elm => elm.code === storeId));
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error, life: 5000 });
        } finally {
            setLoading(false);
        }
    }, [userProfile.info.partyId, t, toast, setLoading, partyId, genders, setValue]);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name]?.message}</small>
    };

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <InputTextController id='partyCode' label={t('common:salesman.id')} checkErr disabled
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='fullName' label={t('common:salesman.fullName')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <DropdownController id='gender' label={t('common:salesman.gender')} 
                                options={genders} control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <CalendarController id='birthDate' label={t('common:salesman.birthDate')} showIcon monthNavigator yearNavigator yearRange="1950:2030"
                                showButtonBar control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <DropdownController id='userGroupId' label={t('common:salesman.userGroup')} 
                                options={userGroups} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='productStoreId' label={t('common:productStore')} 
                                options={listProductStoreId} control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            <InputTextController id='username' label={t('common:userAccount.username')} disabled
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <PasswordController id='newPassword' label={t('common:userAccount.password')} type="password"
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        
                            <PasswordController id='newPasswordVerify' label={t('common:userAccount.passwordVerify')} type="password"
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
