import React, {useRef, useState} from 'react';
import {Toast} from 'primereact/toast';
import {Message} from 'primereact/message';
import {InputText} from 'primereact/inputtext';
import {Button} from 'primereact/button';
import {getTokenCookie} from "../../TokenCookie";
import { ServiceHandle } from '../../utilities';

export const AddCustomer = () => {
    const [firstName, setFirstName ] = useState('');
    const [lastName, setLastName] = useState('');
    const [salutation, setSalutation] = useState('');
    const toast = useRef();
    // const message = useRef();

    // const addSuccessMessage = () => {
    //     message.current.show({severity: 'success', content: 'Message Detail'});
    // };

    // const addInfoMessage = () => {
    //     message.current.show({severity: 'info', content: 'Message Detail'});
    // };

    // const addWarnMessage = () => {
    //     message.current.show({severity: 'warn', content: 'Message Detail'});
    // };

    // const addErrorMessage = () => {
    //     message.current.show({severity: 'error', content: 'Message Detail'});
    // };

    const showSuccess = () => {
        // save.
        ServiceHandle.post('rest/s1/mantle/parties/person',

            {
                // firstName: 'Hung',
                // lastName: 'Duong Danh'

                firstName: firstName,
                lastName: lastName,
                salutation: salutation
            },
            {
                headers: {
                    moquiSessionToken: getTokenCookie()
                }
            },
        )
            .then(function (response) {
                // console.log(response);
            })
            .catch(function (error) {
                // console.log(error);
            });
        // return ServiceHandle.get('http://localhost:8080/rest/s1/mantle/InvoiceFindView',{},{headers:{moquiSessionToken:getTokenCookie()}})
        //     .then(res => res.data);
        toast.current.show({severity: 'success', summary: 'Thông báo', detail: 'Thêm mới khách hàng thành công', life: 3000});
    };

    // const showInfo = () => {
    //     toast.current.show({severity: 'info', summary: 'Info Message', detail: 'Message Detail', life: 3000});
    // };

    // const showWarn = () => {
    //     toast.current.show({severity: 'warn', summary: 'Warn Message', detail: 'Message Detail', life: 3000});
    // };

    // const showError = () => {
    //     toast.current.show({severity: 'error', summary: 'Error Message', detail: 'Message Detail', life: 3000});
    // };

    return (
        <div className="p-grid messages-demo">
            <div className="p-col-12 p-lg-8">
                <div className="card">
                    <h5>Thêm mới khách hàng cá nhân</h5>
                    <div className="p-field p-grid p-align-start">
                        <label htmlFor="firstName" className="p-col-fixed">Tên</label>
                        <div className="p-col">
                            <InputText id="firstName" value={firstName} onChange={(e) => setFirstName(e.target.value)} required className="p-invalid"></InputText>
                            <Message severity="error" text="Cần nhập vào Tên gọi"/>
                        </div>
                    </div>

                    <div className="p-field p-grid p-align-start">
                        <label htmlFor="lastName" className="p-col-fixed">Họ và tên đệm</label>
                        <div className="p-col">
                            <InputText id="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} required className="p-invalid"></InputText>
                            <Message severity="error" text="Cần nhập vào Họ và tên đệm"/>
                        </div>
                    </div>

                    <div className="p-field p-grid p-align-start">
                        <label htmlFor="salutation" className="p-col-fixed">Xưng hô</label>
                        <div className="p-col">
                            <InputText id="salutation" value={salutation} onChange={(e) => setSalutation(e.target.value)} required className="p-invalid"></InputText>
                            {/*<Message severity="error" text="Cần nhập vào Họ và tên đệm"/>*/}
                        </div>
                    </div>

                    {/*<div className="p-field p-grid">*/}
                    {/*    <label htmlFor="email" className="p-col-fixed">Email</label>*/}
                    {/*    <div className="p-col">*/}
                    {/*        <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="p-invalid"></InputText>*/}
                    {/*        <Message severity="error" />*/}
                    {/*    </div>*/}
                    {/*</div>*/}
                    <div className="p-field p-grid">
                        <Toast ref={toast}/>
                        <div className="p-col">
                            <Button type="button" onClick={showSuccess} label="Lưu" className="p-button-success p-mr-2"/>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    );
}
