import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import {InvoicePayableService} from "../../service/accounting/InvoicePayableService";
import Moment from "react-moment";
import {useTranslation} from "react-i18next";
import {Dialog} from "primereact/dialog";
import {Dropdown} from "primereact/dropdown";
import {Calendar} from "primereact/calendar";
import {InputNumber} from "primereact/inputnumber";
import {ServiceHandle} from "../../utilities";
import {getTokenCookie} from "../../TokenCookie";
import Const from "../../utilities/Const";

// Hóa đơn phải trả.
export const PayableInvoices = () => {
    const {t} = useTranslation('common');
    const [tableData, setTableData] = useState(null);
    const dt = useRef(null);
    const [dialog, setDialog] = useState(false);
    // const [loading, setLoading] = useState(false);
    const [parties, setParties] = useState(null);
    // const [submitted, setSubmitted] = useState(false);
    const [invoiceTypes, setInvoiceTypes] = useState(null);
    const [value1, setValue1] = useState('');
    const [value2, setValue2] = useState('');
    const [value3, setValue3] = useState('');
    const [value4, setValue4] = useState('');
    const [value5, setValue5] = useState('');
    const toast = useRef(null);
    // const [selectedDate, setSelectedDate] = useState(null);
    const [selectedItem, setSelectedItem] = useState(null);

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    // let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();

    useEffect(() => {
        const invoicePayableService = new InvoicePayableService();
        invoicePayableService.getInvoicesPayable().then(data => {
            setTableData(data);
            setTotalRecords(data.length);
        });
        invoicePayableService.getInvoiceTypes().then(data => {
            setInvoiceTypes(data);
        });
        invoicePayableService.getListPartyId().then(data => {
           setParties(data.partyIdList);
        });
    }, [lazyParams]);

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    // const filterDate = (value, filter) => {
    //     if (filter.toISOString() == value) {
    //         return true;
    //     }
    //     if (value === undefined || value === null) {
    //         return false;
    //     }
    // }

    // const onDateChange = (e) => {
    //     //dt.current.filter(e.value.toISOString(), 'createdDate', 'custom');
    //     dt.current.filter(e.value, 'createdDate', 'custom');
    //     setSelectedDate(e.value);
    // }

    // const dateFilter = <Calendar value={selectedDate} onChange={onDateChange} dateFormat="dd/mm/yy" className="p-column-filter" placeholder="Ngày tạo" showClear filter/>;

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const hideDialog = () => {
        // setSubmitted(false);
        setDialog(false);
    }

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    const timeTemplate = (data, props) => {
        return (
            <>
                <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
            </>
        );
    };

    const saveInvoice = () => {
        // setSubmitted(true);
        ServiceHandle.post(Const.API.InvoiceCreateNew,
            {
                fromPartyId: value1,
                toPartyId: value2,
                invoiceDate: value3.toISOString(),
                invoiceTypeEnumId: value4.enumId,
                referenceNumber: value5
            },
            {
                headers: {
                    moquiSessionToken: getTokenCookie()
                }
            },
        )
            .then(function (response) {
                //console.log(response);
            })
            .catch(function (error) {
                //console.log(error);
            });
        toast.current.show({severity: 'success', summary: t('acc.payableInvoice.noticeHeader'), detail: t('acc.payableInvoice.noticeSuccess'), life: 3000});
        setDialog(false);
    }

    const openNew = () => {
        setDialog(true);
    }

    const dialogFooter = (
        <>
            <Button label={t('acc.payableInvoice.btnCancel')} icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label={t('acc.payableInvoice.btnSave')} icon="pi pi-check" className="p-button-text" onClick={saveInvoice} />
        </>
    );

    return (
        <div>
            <div className="p-col-12">
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-9">
                            <h3>{t('acc.payableInvoice.title')}</h3>
                        </div>
                        <div className="p-col-12 p-md-3">
                            <div className="p-grid">
                                <Button label={t('acc.payableInvoice.addNew')} icon="pi pi-plus" className="p-button-outlined" onClick={openNew} /> &nbsp;&nbsp;
                                <Button label="Xóa" icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/> &nbsp;&nbsp;
                                <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} /> &nbsp;&nbsp;
                            </div>
                        </div>
                    </div>

                    <DataTable ref={dt} value={tableData} paginator className="p-datatable-customers"
                               dataKey="invoiceId" rowHover emptyMessage={t('acc.payableInvoice.noPayableInvoiceFound')}
                               selectionMode="single" onSelectionChange={(e) => setSelectedItem(e.value)}
                               filters onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows}
                               totalRecords={totalRecords} onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi"
                               lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                               resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="fromRoleTypeId" header={t('acc.payableInvoice.fromRoleTypeId')} sortable body={bodyTemplate} filter/>
                        <Column field="toRoleTypeId" header={t('acc.payableInvoice.toRoleTypeId')} sortable body={bodyTemplate} filter/>
                        <Column field="invoiceId" header={t('acc.payableInvoice.invoiceId')} sortable body={bodyTemplate} filter/>
                        <Column field="invoiceTypeEnumId" header={t('acc.payableInvoice.invoiceTypeEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="fromPartyId" header={t('acc.payableInvoice.fromPartyId')} sortable body={bodyTemplate} filter/>
                        <Column field="statusId" header={t('acc.payableInvoice.statusId')} sortable body={bodyTemplate} filter/>
                        <Column field="invoiceDate" header={t('acc.payableInvoice.invoiceDate')} sortable body={timeTemplate} filter/>
                        <Column field="description" header={t('acc.payableInvoice.description')} sortable body={bodyTemplate} filter/>
                        <Column field="currencyUomId" header={t('acc.payableInvoice.currencyUomId')} sortable body={bodyTemplate} filter/>
                        <Column field="invoiceTotal" header={t('acc.payableInvoice.invoiceTotal')} sortable body={bodyTemplate} filter/>
                        <Column field="appliedPaymentsTotal" header={t('acc.payableInvoice.appliedPaymentsTotal')} sortable body={bodyTemplate} filter/>
                        <Column field="unpaidTotal" header={t('acc.payableInvoice.unpaidTotal')} sortable body={bodyTemplate} filter/>
                    </DataTable>
                </div>

                <Dialog visible={dialog} style={{ width: '450px'}} header={t('acc.payableInvoice.addNewDialogHeader')} modal className="p-fluid" footer={dialogFooter} onHide={hideDialog}>
                    <div className="p-field">
                        <label htmlFor="dropdown1">{t('acc.invReceivable.fromPartyLabel')}</label>
                        <Dropdown id="dropdown1" options={parties} value={value1} onChange={(e) => setValue1(e.value)} appendTo={document.body}></Dropdown>
                    </div>
                    <div className="p-field">
                        <label htmlFor="dropdown2">{t('acc.invReceivable.toPartyLabel')}</label>
                        <Dropdown id="dropdown2" options={parties} value={value2} onChange={(e) => setValue2(e.value)} appendTo={document.body}></Dropdown>
                    </div>
                    <div className="p-field">
                        <label htmlFor="calendar">{t('acc.invReceivable.invoiceDateLabel')}</label>
                        <Calendar id="calendar" showIcon value={value3} onChange={(e) => setValue3(e.value)} appendTo={document.body}></Calendar>
                    </div>
                    <div className="p-field">
                        <label htmlFor="dropdown4">{t('acc.invReceivable.invoiceTypeLabel')}</label>
                        <Dropdown id="dropdown4" options={invoiceTypes} value={value4} onChange={(e) => setValue4(e.value)} optionLabel="description" appendTo={document.body}></Dropdown>
                    </div>
                    <div className="p-field">
                        <label htmlFor="dropdown5">{t('acc.invReceivable.invoiceNoLabel')}</label>
                        <InputNumber id="dropdown5" value={value5} onChange={(e) => setValue5(e.value)}/>
                    </div>
                </Dialog>
            </div>
        </div>
    )
}
