import React, { useRef, useContext} from 'react';
import { Button } from 'primereact/button';
import { Menu } from 'primereact/menu';
import { UserProfileContext } from '../App';

const MenuBreadcrumb = (props) => {
    const userProfile = useContext(UserProfileContext);
    const menu = useRef();

    if (userProfile.roles.length === 0 || props.model.length === 0){
        return(<div/>);
    }

    const toggleMenu = (event) => {
        menu.current.toggle(event);
    };

    props.model.forEach(menuItem => {
        menuItem.command = (e) => {
            let ind = props.model.findIndex((item) => item.label === e.item.label);
            props.setActiveIndex(ind);
        }
    });
    
    return(
        <div>
            <Menu ref={menu} model={props.model} popup/>
            {
            (typeof(props.model[props.activeIndex]) != 'undefined') &&
            <Button type="button" label={props.model[props.activeIndex].label} icon="pi pi-angle-down" iconPos="right" onClick={toggleMenu} style={{ width: 'auto' }} />
            }
        </div>

    );
}

export default MenuBreadcrumb;
