import React, { useCallback, useContext, useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useParams } from "react-router-dom";
import { useHistory } from 'react-router-dom';

import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';

import { ToastContext } from '../../../App';
import { UserProfileContext } from "../../../App";

import { SalesmanService } from '../../../service/SalesmanService';

import { GeneralInformation } from './GeneralInformation';
import { Loading } from '../../../components/Loading';

export const SalesmanDetail = () => {
    const [t] = useTranslation('common');
    const { id } = useParams();
    const toast = useContext(ToastContext);
    const userProfile = useContext(UserProfileContext);
    const history = useHistory();

    const [employee, setEmployee] = useState(null);
    const [loading, setLoading] = useState(false);

    const init = useCallback(async () => {
        setLoading(true);
        try {
            const salesmanService = new SalesmanService();
            let data = await salesmanService.getSaleEmployeeDetail({partyId: id});
            setEmployee(data.employee);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error, life: 5000 });
        } finally {
            setLoading(false);
        }
    }, [id, t, toast]);

    useEffect(() => {
        init();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const edit = useCallback((e) => {
        history.push("/sales/salesman/edit/" + id)
    }, [id, history]);

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-8">
                            <h5>{t('salesman.detail')}</h5>
                        </div>
                        <div className="p-col-12 p-md-4">
                            <div className="p-grid">
                                <div className="p-col-12 p-md-8"></div>
                                <div className="p-col-12 p-md-4">
                                    {
                                        userProfile.roles.includes('ADMIN') && 
                                        <Button label={t('common:edit')} icon="pi pi-pencil" className="p-button-text" onClick={(e) => edit(e)} />
                                    }
                                </div>
                            </div>
                        </div>
                    </div>


                    <TabView>
                        <TabPanel header={t('customer.generalInformation')}>
                            <GeneralInformation employee={employee} partyId={id} />
                        </TabPanel>
                        {/* <TabPanel header={t('customer.representative')}>
                            <h5>{t('customer.representative')}</h5>
                        </TabPanel> */}
                    </TabView>
                    
                    <Loading visible={loading} onHide={() => setLoading(false)} />
                </div>
            </div>
        </div>
  );
};
