import React, {useEffect, useRef, useState} from 'react';
import {Toast} from 'primereact/toast';
import {Message} from 'primereact/message';
import {InputText} from 'primereact/inputtext';
import {Button} from 'primereact/button';
import {getTokenCookie} from "../../TokenCookie";
import {Calendar} from "primereact/calendar";
import {Dropdown} from "primereact/dropdown";
import { ServiceHandle } from '../../utilities';
import {AcctgTransService} from "../../service/accounting/AcctgTransService";
import {useTranslation} from "react-i18next";
// import {useParams} from "react-router-dom";

export const EditInvoiceItem = () => {

    // const { id } = useParams();

    // console.log(id);
    // return (
    //     <div>
    //         <p>Lorem Ipsum</p>
    //         <p>Id: {id}</p>
    //     </div>
    // );

    const {t} = useTranslation('common');
    const [cities, setCustomer1] = useState(null);

    // const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    // const [salutation, setSalutation] = useState('');
    // const [countries, setCountries] = useState([]);
    const toast = useRef();
    // const message = useRef();
    const [value2, setValue2] = useState(''); // text-box
    const [value8, setValue8] = useState(''); // drop-down list.
    // const [filteredCountries, setFilteredCountries] = useState(null);

    useEffect(() => {
        const glaccountService = new AcctgTransService();
        glaccountService.getList().then(data => {
            setCustomer1(data);
        });
    }, []);

    // const addSuccessMessage = () => {
    //     message.current.show({severity: 'success', content: 'Message Detail'});
    // };

    // const addInfoMessage = () => {
    //     message.current.show({severity: 'info', content: 'Message Detail'});
    // };

    // const addWarnMessage = () => {
    //     message.current.show({severity: 'warn', content: 'Message Detail'});
    // };

    // const addErrorMessage = () => {
    //     message.current.show({severity: 'error', content: 'Message Detail'});
    // };

    const showSuccess = () => {
        // save.
        ServiceHandle.post('rest/s1/mantle/parties/person',
            {
                // firstName: firstName,
                lastName: lastName,
                // salutation: salutation
            },
            {
                headers: {
                    moquiSessionToken: getTokenCookie()
                }
            },
        )
            .then(function (response) {
                // console.log(response);
            })
            .catch(function (error) {
                // console.log(error);
            });
        // return ServiceHandle.get('http://localhost:8080/rest/s1/mantle/InvoiceFindView',{},{headers:{moquiSessionToken:getTokenCookie()}})
        //     .then(res => res.data);
        toast.current.show({severity: 'success', summary: 'Thông báo', detail: 'Thêm mới khách hàng thành công', life: 3000});
    };

    // const searchCountry = (event) => {
    //     // in a real application, make a request to a remote url with the query and
    //     // return filtered results, for demo we filter at client side
    //     const filtered = [];
    //     const query = event.query;
    //     for (let i = 0; i < countries.length; i++) {
    //         const country = countries[i];
    //         if (country.name.toLowerCase().indexOf(query.toLowerCase()) === 0) {
    //             filtered.push(country);
    //         }
    //     }
    //     setFilteredCountries(filtered);
    // };

    // const showInfo = () => {
    //     toast.current.show({severity: 'info', summary: 'Info Message', detail: 'Message Detail', life: 3000});
    // };

    // const showWarn = () => {
    //     toast.current.show({severity: 'warn', summary: 'Warn Message', detail: 'Message Detail', life: 3000});
    // };

    // const showError = () => {
    //     toast.current.show({severity: 'error', summary: 'Error Message', detail: 'Message Detail', life: 3000});
    // };

    return (
        <div className="p-grid messages-demo">
            <div className="p-col-12 p-lg-8">
                <h5>Thêm mới Nghiệp vụ kế toán phát sinh</h5>
                <div className="p-field p-grid p-align-start">
                    <div className="p-col">
                        <label htmlFor="firstName" className="p-col-fixed">Loại nghiệp vụ kế toán phát sinh<span style={{color: 'red'}}>*</span></label>
                    </div>
                    <div className="p-col">
                        <Dropdown id="dropdown" options={cities} value={value8} onChange={(e) => setValue8(e.value)} optionLabel="description"></Dropdown>
                    </div>
                    <div className="p-col">
                        <Message severity="error" text="Cần nhập Loại nghiệp vụ KT phát sinh"/>
                    </div>
                </div>

                <div className="p-field p-grid p-align-start">
                    <div className="p-col">
                        <label htmlFor="lastName" className="p-col-fixed">Đối tượng kế toán<span style={{color: 'red'}}>*</span></label>
                    </div>
                    <div className="p-col">
                        <InputText id="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} required className="p-invalid"></InputText>
                    </div>
                    <div className="p-col">
                        <Message severity="error" text="Cần nhập vào Đối tượng kế toán"/>
                    </div>
                </div>

                <div className="p-field p-grid p-align-start">
                    <div className="p-col">
                        <label htmlFor="salutation" className="p-col-fixed">Ngày phát sinh<span style={{color: 'red'}}>*</span></label>
                    </div>
                    <div className="p-col">
                        <Calendar id="calendar" className="p-invalid" showIcon value={value2} onChange={(e) => setValue2(e.value)}></Calendar>
                    </div>
                    <div className="p-col">
                        <Message severity="error" text="Cần nhập vào Ngày phát sinh"/>
                    </div>
                </div>
                <div className="p-field p-grid">
                    <Toast ref={toast}/>
                    <div className="p-col">
                        <Button type="button" onClick={showSuccess} label={t('acc.editInvoice.btnSave')} className="p-button-success p-mr-2"/>
                    </div>
                </div>
            </div>
        </div>
    );
}
