import React, {useState, useEffect} from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

import {useHistory} from 'react-router-dom';
import {useTranslation} from 'react-i18next';
import {Button} from 'primereact/button';
import {PartyService} from '../../../../service/PartyService';

const EmployeeList = () => {
  const history = useHistory();
  const {t} = useTranslation('common');

  const [loading, setLoading] = useState(false);
  const [employeeList, setEmployeeList] = useState({});
  const [totalRecords, setTotalRecords] = useState(0);
  const [selectedEmployee, setSelectedEmployee] = useState([]);

  const [lazyParams, setLazyParams] = useState({
    first: 0,
    rows: 10,
    page: 0,
    sortField: null,
    sortOrder: null,
    filters: {},
  });

  useEffect(() => {
    let params = {
      roleTypeId: 'Shipper',
      partyTypeEnumId: 'PtyPerson',
    };
    
    let partyService = new PartyService();
    setLoading(true);
    partyService.search({...params,...transformRequest(), }).then((data) => {
      setEmployeeList(data.documentList);
      setTotalRecords(data.totalRecords);
      
    });
    setLoading(false);
  },[lazyParams]);// eslint-disable-line react-hooks/exhaustive-deps

  const transformRequest = () => {
    let params = {
        pageIndex: lazyParams.page,
        pageSize: lazyParams.rows
    }
    // sort object
    if(lazyParams.sortOrder){
        params['orderByField'] = (lazyParams.sortOrder === -1? "-":"") + lazyParams.sortField // simple for single sort
    }
    // filters object
    if(lazyParams.filters){
        for (let [key, value] of Object.entries(lazyParams.filters)) {
            params[key] = value.value ; 
        }
    }
    return params;
}


  const onSort = (event) => {
    let _lazyParams = { ...lazyParams, ...event };
    setLazyParams(_lazyParams);
  }

  const onFilter = (event) => {
      let _lazyParams = { ...lazyParams, ...event };
      _lazyParams['first'] = 0;
      setLazyParams(_lazyParams);
  }

  const onPage = (event) => {
      setLazyParams(event);
  }

  const rowIndexBodyTemplate = (rowData, props) => {
    let index = parseInt(props.rowIndex + 1);
    return (
      <React.Fragment>
        <span>{index}</span>
      </React.Fragment>
    );
  };

  const idBodyTemplate = (rowData) => {
    return (
      <React.Fragment>
        <a href={`#/logistics/shipper/detail/${rowData.partyId}`} style={{color: '#2196f3'}}>
          {' '}
          {rowData.partyId}{' '}
        </a>
      </React.Fragment>
    );
  };

  const roleBodyTemplate = (rowData) => {
    return (
      <React.Fragment>
        <span>{rowData.roles.map((item) => item.role).join(', ')}</span>
      </React.Fragment>
    );
  };
  const telecomBodyTemplate = (rowData) => {
    return (
      <React.Fragment>
        <span>
          {rowData?.contactMechs
            ?.filter((item) => item.contactMechTypeEnumId === 'CmtTelecomNumber')
            .map((item) => item.contactNumber)
            .join(', ')}
        </span>
      </React.Fragment>
    );
  };

  return (
          <div className="card">
            <div className="p-d-flex p-jc-between">
              <div>
                {' '}
                <h5>{t('title.employeeList')}</h5>
              </div>
              <div>
                <Button
                  label={t('homepage.logistics.addNew')}
                  icon="pi pi-plus"
                  className="p-mr-2 p-mb-2 "
                  onClick={() => history.push('/logistics/shipper/create')}
                />
              </div>
            </div>

      <DataTable
        value={employeeList}
        showGridlines
        paginator
        responsiveLayout="scroll"
        lazy
        paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
        currentPageReportTemplate="Showing {first} to {last} of {totalRecords}"
        rows={lazyParams.rows} rowsPerPageOptions={[10, 25, 50, 100]}
        totalRecords={totalRecords}
        loading={loading}
        onPage={onPage}
        onSort={onSort}
        sortField={lazyParams.sortField}
        sortOrder={lazyParams.sortOrder}
        onFilter={onFilter}
        filters={lazyParams.filters}
        selection={selectedEmployee}
        onSelectionChange={(e) => setSelectedEmployee(e.value)}
      >
        <Column header="#" body={rowIndexBodyTemplate} headerStyle={{width: '4rem'}} />
        <Column field="partyId" filter sortable header={t('shipper.partyId')} body={idBodyTemplate}></Column>
        <Column field="combinedName" filter sortable header={t('shipper.combinedName')}></Column>
        <Column header={t('shipper.phoneNumber')} body={telecomBodyTemplate}></Column>
        <Column header={t('shipper.position')} body={roleBodyTemplate}></Column>
      </DataTable>
    </div>
  );
};

export default EmployeeList;
