import React from "react";
import { useTranslation } from "react-i18next";
import Moment from "react-moment";

import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { ColumnGroup } from "primereact/columngroup";
import { Row } from "primereact/row";

const titleStatus = {
  position: "absolute",
  color: "#FFF",
  background: "rgba(0,0,0,0.3)",
  right: 0,
  padding: "5px 20px",
  marginTop: "-4px",
  display: "block",
};

const titleOrder = {
  textAlign: "center",
  fontWeight: "bold",
  padding: 0,
  lineHeight: "20px",
  marginBlock: 0,
  textTransform: "uppercase",
};

export const OrderGeneral = (props) => {
  const { orderItems, orderDetail } = props;
  const [t, i18n] = useTranslation();

  const dateTimeFormat = (value) => (
    <span>
      {value != null && <Moment format="DD/MM/YYYY HH:mm">{value}</Moment>}
    </span>
  );
  const dateTimeFormatBetween = (fromDate, thruDate) => (
    <>
      {fromDate != null && (
        <Moment format="DD/MM/YYYY HH:mm">{fromDate}</Moment>
      )}
      <span>&nbsp;-&nbsp;</span>
      {thruDate != null && (
        <Moment format="DD/MM/YYYY HH:mm">{thruDate}</Moment>
      )}
    </>
  );

  const colSeqBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        {rowData.orderItemSeqId}
      </>
    );
  };

  const colIdBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        {rowData.productId}
      </>
    );
  };

  const colDescBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        {rowData.itemDescription}
      </>
    );
  };

  const colQtyBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        {rowData.quantity}
      </>
    );
  };

  const currencyItemTotalTemplate = (data, props) => {
    const locale = i18n.language === "vi" ? "vi-VN" : "en-US";
    const currency =
      orderDetail?.currencyUomId != null ? orderDetail.currencyUomId : "VND";
    const value = new Intl.NumberFormat(locale, {
      style: "currency",
      currency: currency,
    }).format(data.quantity * data.unitAmount);
    return (
      <>
        <span>{value}</span>
      </>
    );
  };

  const currencyBodyTemplate = (data, props) => {
    const numberFormat = (data, props) =>
      new Intl.NumberFormat(i18n.language === "vi" ? "vi-VN" : "en-US", {
        style: "currency",
        currency: orderDetail?.currencyUomId != null ? orderDetail.currencyUomId : "VND",
      }).format(data[props.field]);
    const value = data[props.field] != null ? numberFormat(data, props) : "";
    return (
      <>
        <span>{value}</span>
      </>
    );
  };

  const renderGrandTotal = (grandTotal) => {
    const locale = i18n.language === "vi" ? "vi-VN" : "en-US";
    const currency =
      orderDetail?.currencyUomId != null ? orderDetail.currencyUomId : "VND";
    const value = new Intl.NumberFormat(locale, {
      style: "currency",
      currency: currency,
    }).format(grandTotal);
    return (
      <>
        <span>{value}</span>
      </>
    );
  };

  let footerGroup = (
    <ColumnGroup>
      <Row>
        <Column
          footer={`${t("common:order.grandTotal")}`}
          colSpan={5}
          footerStyle={{
            backgroundColor: "#f8f9fa",
            textAlign: "right",
            textTransform: "uppercase",
          }}
        />
        <Column
          footer={renderGrandTotal(
            orderDetail?.grandTotal != null ? orderDetail.grandTotal : 0
          )}
          colSpan={1}
          footerStyle={{ backgroundColor: "#f8f9fa", textAlign: 'right' }}
        />
      </Row>
    </ColumnGroup>
  );

  return (
    <div className="p-grid">
      <div className="p-col-12">
        <div
          className="p-fluid"
          style={{ position: "relative", marginTop: "10px" }}
        >
          <div style={titleStatus}>{orderDetail?.status.description}</div>
          <h3 style={titleOrder}>
            {t("common:homepage.purchasing.PurchasingOrder")}
          </h3>
          <div className="p-grid" style={{ marginTop: "10px" }}>
            <div className="p-col-12 p-md-6">
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:order.id")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.otherPartyOrderId === null
                    ? orderDetail.orderId
                    : orderDetail.otherPartyOrderId}
                </div>
              </div>
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:order.entryDate")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.entryDate === null
                    ? ""
                    : dateTimeFormat(orderDetail.entryDate)}
                </div>
              </div>
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:vendor.label")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.vendor === null
                    ? ""
                    : `${orderDetail.vendor.organizationName} [${orderDetail.vendor.partyId}]`}
                </div>
              </div>

              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:order.orderName")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.orderName === null
                    ? ""
                    : orderDetail.orderName}
                </div>
              </div>
            </div>
            <div className="p-col-12 p-md-6">
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:order.enteredByParty")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.enteredByParty === null
                    ? ""
                    : `${orderDetail.enteredByParty.lastName || ""} ${
                        orderDetail.enteredByParty.middleName || ""
                      } ${orderDetail.enteredByParty.firstName || ""} [${
                        orderDetail.enteredByParty.partyId
                      }]`}
                </div>
              </div>
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:priority")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null ? "" : orderDetail.priority}
                </div>
              </div>
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:receiveToFacility")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail === null
                    ? ""
                    : orderDetail.facility === null
                    ? ""
                    : `${orderDetail.facility.facilityName} [${orderDetail.facility.facilityId}]`}
                </div>
              </div>
              <div className="p-field p-grid">
                <label className="p-col-12 p-mb-2 p-md-3 p-mb-md-0">
                  {t("common:order.deliveryTime")}
                </label>
                <div className="p-col-12 p-md-9">
                  {orderDetail != null
                    ? dateTimeFormatBetween(
                        orderDetail.shipAfterDate,
                        orderDetail.shipBeforeDate
                      )
                    : ""}
                </div>
              </div>
            </div>
          </div>
          <div className="p-grid">
            <div className="p-col-12 p-mb-12 p-md-12">
              <DataTable
                footerColumnGroup={footerGroup}
                showGridlines
                emptyMessage={t("common:dataIsEmpty")}
                value={orderItems}
                dataKey="productId"
                className="datatable-responsive"
              >
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa", width: "4rem" }}
                  field="orderItemSeqId"
                  header="#"
                  body={colSeqBodyTemplate}
                ></Column>
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa", width: "10rem" }}
                  field="productId"
                  header={t("common:product.id")}
                  body={colIdBodyTemplate}
                ></Column>
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa" }}
                  field="productName"
                  header={t("common:product.name")}
                  body={colDescBodyTemplate}
                ></Column>
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa", width: "12rem" }}
                  field="unitAmount"
                  header={t("common:product.price")}
                  body={currencyBodyTemplate}
                  bodyStyle={{textAlign: 'right'}}
                ></Column>
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa", width: "8rem" }}
                  field="quantity"
                  header={t("common:product.quantity")}
                  body={colQtyBodyTemplate}
                  bodyStyle={{textAlign: 'right'}}
                ></Column>
                <Column
                  headerStyle={{ backgroundColor: "#f8f9fa", width: "10rem" }}
                  field="amount"
                  header={t("common:order.itemTotal")}
                  body={currencyItemTotalTemplate}
                  bodyStyle={{textAlign: 'right'}}
                />
              </DataTable>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
