import React, {
    useState,
    useEffect,
    useRef
} from 'react';
import {
    classNames
} from 'primereact/utils';
import {
    DataTable
} from 'primereact/datatable';
import {
    Column
} from 'primereact/column';
import {
    InputText
} from 'primereact/inputtext';
import {
    Button
} from 'primereact/button';
import {
    Dropdown
} from 'primereact/dropdown';
import {
    Calendar
} from 'primereact/calendar';
import {
    MultiSelect
} from 'primereact/multiselect';
import {
    ProgressBar
} from 'primereact/progressbar';

import {
    SupplierService
} from '../../service/SupplierService'
export const SupplierProduct = () => {

    const dt = useRef(null);
    const [loading, setLoading] = useState(false);
    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortOrder: -1,
        sortField: 'productId'

    });
    const [supplierProducts, setupplierProducts] = useState([]);

    useEffect(() => {
        const supplierService = new SupplierService();

        function transformRequest() {
            let params = {
                pageIndex: lazyParams.page,
                pageSize: lazyParams.rows
            };
            // sort object
            if (lazyParams.sortOrder) {
                params['orderByField'] = (lazyParams.sortOrder === -1 ? "-" : "") + lazyParams.sortField; // simple for single sort
            }
            // filters object
            if (lazyParams.filters) {
                for (let [key, value] of Object.entries(lazyParams.filters)) {
                    params[key] = value.value; // equal for all

                    // matchMode
                }
            }
            return params;
        }

        const loadLazyData = async () => {
            setLoading(true);

            let res = await supplierService.getSupplierProducts(transformRequest());
            setTotalRecords(res.totalRecords);
            setAssets(res.assetList);
            setLoading(false);
        }
        loadLazyData();
    }, [lazyParams]);


    return ( <
        div className = "datatable-crud" >
        <
        div className = "card" >
        <
        DataTable ref = {
            dt
        }
        lazy selectionMode = "multiple"
        paginator first = {
            lazyParams.first
        }
        rows = {
            lazyParams.rows
        }
        rowsPerPageOptions = {
            [10, 25, 50, 100]
        }
        currentPageReportTemplate = "Showing {first} to {last} of {totalRecords}"
        paginatorTemplate = "FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown" >
        <
        Column field = ""
        header = {
            ('STT')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Mã sản phẩm')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Tên sản phẩm')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Mã NCC')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Nhà cung cấp')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('SL tối thiểu')
        }
        filter sortable / >
        <
        Column field = ""
        header = {
            ('Mã đơn vị chính')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Giá đơn vị mua')
        }
        sortable filter / >
        <
        Column field = ""
        header = {
            ('Đơn vị tiền tệ')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Từ ngày')
        }
        filter / >
        <
        Column field = ""
        header = {
            ('Tới ngày')
        }
        filter / >
        <
        /DataTable> <
        /div> <
        /div>
    )
}
