import React from 'react';
import { Chart } from 'primereact/chart';

export const PieChart = (props) => {
    const { data } = props;

    const lightOptions = {
        plugins: {
            legend: {
                labels: {
                    color: '#495057'
                }
            }
        }
    };

    return (
        <div className="p-d-flex p-jc-center">
            <Chart type="pie" data={data} options={lightOptions} style={{ position: 'relative', width: '60%' }} />
        </div>
    )
}
