import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { useTranslation } from "react-i18next";

export const SupplierDataTable = (props) => {
  const { 
    supplierList,
    lazyParams, 
    onPage, 
    onSort, 
    loading, 
    totalRecords, 
    dt, 
    selectedSupplier, 
    setSelectedSupplier
  } = props;
  const { t } = useTranslation('common');

  const linkTemplate = (data, props) => {
    return (
      <>
        <a
          href={`#/purchasing/supplier/detail/${data[props.field]}`}
          style={{ color: "#2196f3" }}
        >
          {data[props.field]}
        </a>
      </>
    );
  };

  const textTemplate = (data, props) => (
    <span>{data[props.field]}</span>
  );

  const addressTemplate = (data, props) => {
    const address = data?.contactMechs?.filter( item => item.contactMechPurposeId === 'PostalPrimary')[0];
    return(
      <span>{address?.address1}</span>
    )
  }

  const telecomNumberTemplate = (data, props) => {
    const telecomNumber = data?.contactMechs?.filter( item => item.contactMechPurposeId === 'PhonePrimary')[0];
    return(
      <span>{telecomNumber?.contactNumber}</span>
    )
  }

  const emailTemplate = (data, props) => {
    const email = data?.contactMechs?.filter( item => item.contactMechPurposeId === 'EmailPrimary')[0];
    return(
      <span>{email?.infoString}</span>
    )
  }

  return (
    <>
      <DataTable
        ref={dt}
        value={supplierList}
        dataKey="partyId"
        paginator
        lazy
        first={lazyParams.first}
        rows={lazyParams.rows}
        totalRecords={totalRecords}
        rowsPerPageOptions={[5, 10, 20, 50, 100]}
        onPage={onPage}
        onSort={onSort}
        sortField={lazyParams.sortField}
        sortOrder={lazyParams.sortOrder}
        rowHover
        selection={selectedSupplier}
        paginatorTemplate="RowsPerPageDropdown CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink"
        currentPageReportTemplate="{first} - {last} of {totalRecords}"
        onSelectionChange={(e) => setSelectedSupplier(e.value)}
        className="p-datatable"
        loading={loading}
        paginatorPosition="bottom"
        resizableColumns
        columnResizeMode="fit"
        showGridlines
        emptyMessage={t("common:dataIsEmpty")}
      >
        <Column
          headerStyle={{ width: "12rem" }}
          field="partyId"
          header={t("vendor.id")}
          sortable
          body={linkTemplate}
        ></Column>
        <Column
          headerStyle={{ width: "15rem" }}
          field="combinedName"
          header={t("vendor.name")}
          sortable
          body={textTemplate}
        />
        
        <Column
          field="address1"
          header={t("vendor.address")}
          body={addressTemplate}
        />

        <Column
          header={t("vendor.telecomNumber")}
          body={telecomNumberTemplate}
        />
         <Column
          header={t("vendor.emailAddress")}
          body={emailTemplate}
        />
      </DataTable>
    </>
  );
};
