import React, { useEffect, useRef, useState, useCallback } from 'react';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { useTranslation } from 'react-i18next';
import moment from 'moment';

import { PromotionService } from '../../../service/PromotionService';

export const Promotion = (props) => {
    const { t } = useTranslation();
    const promotionService = new PromotionService();

    const dt = useRef(null);
    const [ promotions, setPromotions ] = useState(null);

    const { productId } = props;

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        let params = {
            productId: productId
        };
        const data = await promotionService.getPromotionFromProductId(params);
        
        setPromotions(data.promotions);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const bodyTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    }, []);

    const bodyDateTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field] ? moment(data[props.field]).format('DD/MM/YYYY HH:mm:ss') : ''}
            </>
        );
    }, []);

    return(
        <>
            <div className="card">
                <div className="p-grid p-mt-2 table-demo">
                    <div className="p-col-12 p-md-12">
                        <h5>{t('common:product.basePrice')}</h5>
                        <DataTable value={promotions} className="p-datatable-customers" dataKey="productId" ref={dt}>
                            <Column field="storePromotionId" header={t('common:pos.promoCode')} body={bodyTemplate}></Column>
                            <Column field="itemDescription" header={t('common:pos.promoName')} body={bodyTemplate}></Column>
                            <Column field="storeName" header={t('common:productStore')} body={bodyTemplate}></Column>
                            <Column field="description" header={t('common:product.description')} body={bodyTemplate}></Column>
                            <Column field="fromDate" header={t('common:productPrice.fromDate')} body={bodyDateTemplate}></Column>
                            <Column field="thruDate" header={t('common:productPrice.thruDate')} body={bodyDateTemplate}></Column>
                        </DataTable>
                    </div>
                </div>
            </div>
        </>
    );
}
