import React, { useState, useEffect, useCallback, useContext } from 'react';
import { useTranslation } from 'react-i18next';

import { InputTextController } from '../../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../../components/PromotionComponent/DropdownController';

import { GeoService } from '../../../service/GeoService';
import { SalesmanService } from '../../../service/SalesmanService';

import { ToastContext } from '../../../App';

export const ContactInformation = (props) => {
    const { control, errors, partyId, setLoading, setValue } = props;
    const [t] = useTranslation('common');
    const toast = useContext(ToastContext);

    const geoService = new GeoService();

    const [ rCountries, setRCountries ] = useState([]);
    const [ rStateProvinces, setRStateProvinces ] = useState([]); 
    const [ rDistricts, setRDistricts ] = useState([]); 
    const [ rWards, setRWards ] = useState([]); 

    const [ cCountries, setCCountries ] = useState([]);
    const [ cStateProvinces, setCStateProvinces ] = useState([]); 
    const [ cDistricts, setCDistricts ] = useState([]); 
    const [ cWards, setCWards ] = useState([]); 

    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name]?.message}</small>
    };

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        setLoading(true);
        try {
            let data = await geoService.getCountries([]);

            let countries = data.geoList.map((elm) => {
                return {
                    name: elm.geoName,
                    code: elm.geoId
                }
            });
            setRCountries(countries);
            setCCountries(countries);

            const salesmanService = new SalesmanService();
            let saleEmployeeDetail = await salesmanService.getSaleEmployeeDetail({partyId: partyId});

            setValue('currentCountryGeoId', countries.find(elm => elm.code === saleEmployeeDetail.employee.countryGeoId));
            data = await geoService.getGeoRegionList(saleEmployeeDetail.employee.countryGeoId, []);
            let stateProvinces = data.map((elm) => {
                return {
                    name: elm.geoName,
                    code: elm.geoId
                }
            });
            setCStateProvinces(stateProvinces);

            setValue('currentStateProvinceGeoId', stateProvinces.find(elm => elm.code === saleEmployeeDetail.employee.stateProvinceGeoId));
            data = await geoService.getGeoRegionList(saleEmployeeDetail.employee.stateProvinceGeoId, []);
            let districts = data.map(elm => {
                return {
                    name: elm.geoName,
                    code: elm.geoId
                };
            });
            setCDistricts(districts);

            setValue('currentDistrictId', districts.find(elm => elm.code === saleEmployeeDetail.employee.countyGeoId));
            data = await geoService.getGeoRegionList(saleEmployeeDetail.employee.countyGeoId, []);
            let wards = data.map((elm) => {
                return {
                    name: elm.geoName,
                    code: elm.geoId
                }
            });
            setCWards(wards);

            setValue('currentWardId', wards.find(elm => elm.code === saleEmployeeDetail.employee.wardId));
            setValue('currentTarAddress', saleEmployeeDetail.employee.postalAddress);

            setValue('telecomNumber', saleEmployeeDetail.employee.telecomNumber);
            setValue('emailAddress', saleEmployeeDetail.employee.emailAddress);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error, life: 5000 });
        } finally {
            setLoading(false);
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [partyId]);

    const changeRCountry = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setRStateProvinces(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeRStateProvinces = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setRDistricts(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeRDistrict = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setRWards(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeCCountry = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setCStateProvinces(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeCStateProvinces = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setCDistricts(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeCDistrict = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setCWards(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <h6>{t('permanentResidence')}</h6>
                            </div>
                        
                            <DropdownController id='residentCountryGeoId' label={t('customer.countryGeoId')} callback={changeRCountry} 
                                options={rCountries} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='residentStateProvinceGeoId' label={t('customer.stateProvinceGeoId')} callback={changeRStateProvinces} 
                                options={rStateProvinces} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='residentDistrictId' label={t('customer.districtId')} callback={changeRDistrict} 
                                options={rDistricts} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='residentWardId' label={t('customer.wardId')} 
                                options={rWards} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='residentTarAddress' label={t('customer.tarAddress')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='telecomNumber' label={t('customer.telecomNumber')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <h6>{t('currentResidence')}</h6>
                            </div>
                            <DropdownController id='currentCountryGeoId' label={t('customer.countryGeoId')} callback={changeCCountry} 
                                options={cCountries} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='currentStateProvinceGeoId' label={t('customer.stateProvinceGeoId')} callback={changeCStateProvinces} 
                                options={cStateProvinces} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='currentDistrictId' label={t('customer.districtId')} callback={changeCDistrict} 
                                options={cDistricts} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='currentWardId' label={t('customer.wardId')} 
                                options={cWards} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='currentTarAddress' label={t('customer.tarAddress')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                            
                            <InputTextController id='emailAddress' label={t('customer.emailAddress')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
