import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import {GLAccountService} from "../../service/accounting/GLAccountService";
import {useTranslation} from "react-i18next";
import Moment from "react-moment";
import {Button} from "primereact/button";

export const GLAccounts = () => {
    const {t} = useTranslation('common');
    const dt = useRef(null);
    const [tableData, setTableData] = useState(null);
    const [loading, setLoading] = useState(false);
    // const toast = useRef(null);
    const [selectedItem, setSelectedItem] = useState(null);
    // const [selectedDate, setSelectedDate] = useState(null);

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }
    const glaccountService = new GLAccountService();

    useEffect(() => {
        loadLazyData();
    }, [lazyParams]); // eslint-disable-line react-hooks/exhaustive-deps

    const loadLazyData = () => {
        setLoading(true);
        if (loadLazyTimeout) {
            clearTimeout(loadLazyTimeout);
        }
        loadLazyTimeout = setTimeout(() => {
            glaccountService.getGLAccounts(transformRequest()).then(data => {
                setTotalRecords(data.totalRecords);
                setTableData(data.glAccountList);
                setLoading(false);
            });
        }, Math.random() * 1000 + 250);
    }

    const transformRequest = () => {
        let params = {
            pageIndex: lazyParams.page,
            pageSize: lazyParams.rows
        }
        if(lazyParams.sortOrder){
            params['orderByField'] = (lazyParams.sortOrder === -1? "-":"") + lazyParams.sortField
        }
        if(lazyParams.filters){
            for (let [key, value] of Object.entries(lazyParams.filters)) {
                params[key] = value.value ;
            }
        }
        return params;
    }

    // const tableHeader = (
    //     <div className="table-header">
    //         {t('acc.glAccount.title')}
    //     </div>
    // );

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    // const onDateChange = (e) => {
    //     //dt.current.filter(e.value.toISOString(), 'createdDate', 'custom');
    //     dt.current.filter(e.value, 'createdDate', 'custom');
    //     setSelectedDate(e.value);
    // }

    // const dateFilter = <Calendar value={selectedDate} onChange={onDateChange} dateFormat="dd/mm/yy" className="p-column-filter" placeholder="Ngày tạo" showClear filter/>;

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const timeTemplate = (data, props) => {
        return (
            <>
                <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
            </>
        );
    };

    return (
        <div>
            <div className="p-col-12">
                {/*<Toast ref={toast}/>*/}
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-9 p-md-9">
                            {/*<h3>{t('acc.trans.dataGridHeader')}</h3>*/}
                            <h3>Danh mục tài khoản</h3>
                        </div>
                        <div className="p-col-3 p-md-3">
                            <div className="p-grid">
                                <Button label={t('acc.trans.addNew')} icon="pi pi-plus" className="p-button-outlined" onClick={null} />&nbsp;&nbsp;
                                <Button label={t('acc.invReceivable.btnDelete')} icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/>&nbsp;&nbsp;
                                <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} />
                            </div>
                        </div>
                    </div>
                    <DataTable ref={dt} value={tableData} paginator className="p-datatable-customers" dataKey="glAccountId"
                               emptyMessage={t('acc.glAccount.noGLAccountFound')} loading={loading}
                               selectionMode="single" onSelectionChange={(e) => setSelectedItem(e.value)}
                               filters onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows} totalRecords={totalRecords} onPage={onPage}
                               rowsPerPageOptions={[10, 25, 50, 100]}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi" lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder} rowHover resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="glAccountId" header={t('acc.glAccount.glAccountId')} sortable body={bodyTemplate} filter/>
                        <Column field="glAccountClassEnumId" header={t('acc.glAccount.glAccountClassEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="parentGlAccountId" header={t('acc.glAccount.parentGlAccountId')} sortable body={bodyTemplate} filter/>
                        <Column field="isDebit" header={t('acc.glAccount.isDebit')} sortable body={bodyTemplate} filter/>
                        <Column field="lastUpdatedStamp" header={t('acc.glAccount.lastUpdatedStamp')} sortable body={timeTemplate} filter/>
                        <Column field="accountCode" header={t('acc.glAccount.accountCode')} sortable body={bodyTemplate} filter/>
                        <Column field="description" header={t('acc.glAccount.description')} sortable body={bodyTemplate} filter/>
                    </DataTable>
                </div>
            </div>
        </div>
    )
}
