import React, { useCallback } from 'react';

import { useTranslation } from 'react-i18next';

import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';

import { DialogCommon } from './DialogCommon';

export const DialogDate = (props) => {
    const { display, setDisplay, search } = props;

    const { options, cycles } = props;
    
    const { option, setOption } = props;
    const { cycle, setCycle } = props;
    const { fromDate, setFromDate } = props;
    const { toDate, setToDate } = props;

    const [ t ] = useTranslation('common');

    const renderFooter = useCallback(() => {
        return (
            <div>
                <Button label={t('search')} icon="pi pi-check" onClick={() => search()} autoFocus />
            </div>
        );
    }, [search, t]);

    return (
        <Dialog header={t('report.option')} visible={display} style={{ width: '50vw' }} footer={renderFooter()} onHide={() => setDisplay(false)}>
            <div className="p-grid">
                <DialogCommon
                    options={options} cycles={cycles}
                    option={option} setOption={setOption}
                    cycle={cycle} setCycle={setCycle}
                    fromDate={fromDate} setFromDate={setFromDate}
                    toDate={toDate} setToDate={setToDate}
                />
            </div>
        </Dialog>
    );
};
