import React, { useState, useContext } from "react";

import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Button } from "primereact/button";
import { InputNumber } from "primereact/inputnumber";
import { ColumnGroup } from "primereact/columngroup";
import { Row } from "primereact/row";
import { UserProfileContext } from "../App";
import { useTranslation } from "react-i18next";

export const PurchasingCreateOrderProductList = (props) => {
  const {
    onChangeUnitAmountEdit,
    handleUpdateOrderItem,
    removeOrderItem,
    onChangeQuantity,
    orderItems,
    grandTotal,
    handleSetIsTableEditing,
  } = props;
  const [t, i18n] = useTranslation();
  const userProfile = useContext(UserProfileContext);
  const [editingCellRows, setEditingCellRows] = useState([]);

  const indexBodyTemplate = (rowData, props) => {
    let index = parseInt(props.rowIndex + 1);
    return (
      <>
        <span>{index}</span>
      </>
    );
  };

  const numberBodyTemplate = (data, props) => {
    return (
        <p style={{paddingRight: '0.75rem'}}>{data[props.field]}</p>
    );
  };

  const colIdBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        <span>{rowData.productId}</span>
      </>
    );
  };

  const colDescBodyTemplate = (rowData) => {
    return (
      <>
        <span className="p-column-title">{rowData.header}</span>
        <span>{rowData.productName}</span>
      </>
    );
  };

  const currencyBodyTemplate = (data, props) => {
    const numberFormat = (data, props) =>
      new Intl.NumberFormat(i18n.language === "vi" ? "vi-VN" : "en-US", {
        style: "currency",
        currency: data.priceUomId,
      }).format(data[props.field]);
    const value = data[props.field] != null ? numberFormat(data, props) : "";
    return (
      <>
        <p style={{paddingRight: '0.75rem'}}>{value}</p>
      </>
    );
  };

  const actionBodyTemplate = (rowData) => {
    return (
      <div className="actions">
        <Button
          icon="pi pi-trash"
          className="p-button-rounded p-button-warning"
          onClick={() => removeOrderItem(rowData.productId)}
        />
      </div>
    );
  };

  const inputCurrencyEditor = (props, field) => {
    return (
      <InputNumber
        min={0}
        showButtons
        value={props.rowData[field]}
        onValueChange={(e) => onChangeUnitAmountEdit(props, e.target.value)}
        mode="decimal"
        locale={i18n.language === "vi" ? "vi-VN" : "en-US"}
        onFocus={onFocus}
      />
    );
  };

  const inputNumberEditor = (props, field) => {
    return (
      <InputNumber
        showButtons
        min={0}
        value={props.rowData[field]}
        onValueChange={(e) => onChangeQuantity(props, e.target.value)}
        mode="decimal"
        locale={i18n.language === "vi" ? "vi-VN" : "en-US"}
        onFocus={onFocus}
      />
    );
  };

  const onBeforeEditorHide = (e) => {
    handleSetIsTableEditing(false);
  };

  const onFocus = (e) => {
    handleSetIsTableEditing(true);
  };

  const onEditorInit = (e) => {
    const { rowIndex: index, field, rowData } = e.columnProps;
    let _editingCellRows = [...editingCellRows];
    if (!editingCellRows[index]) {
      _editingCellRows[index] = { ...rowData };
    }
    _editingCellRows[index][field] = orderItems[index][field];
    setEditingCellRows(_editingCellRows);
  };

  const onEditorCancel = (e) => {
    const { rowIndex: index, field } = e.columnProps;
    let products = [...orderItems];
    let _editingCellRows = [...editingCellRows];
    products[index][field] = _editingCellRows[index][field];
    delete _editingCellRows[index][field];
    setEditingCellRows(_editingCellRows);
    handleUpdateOrderItem(products);
  };

  const onEditorSubmit = (e) => {
    const { rowIndex: index, field } = e.columnProps;
    let _editingCellRows = [...editingCellRows];
    delete _editingCellRows[index][field];
    setEditingCellRows(_editingCellRows);
  };

  const currencyValidator = (e) => {
    const { rowData, field } = e.columnProps;
    const val = rowData[field];
    if (val == null) return true;
    return val >= 0;
  };

  const positiveIntegerValidator = (e) => {
    const { rowData, field } = e.columnProps;
    const val = rowData[field];
    if (val == null) return false;
    return val >= 0;
  };

  const renderGrandTotal = (grandTotal) => {
    const locale = i18n.language === "vi" ? "vi-VN" : "en-US";
    const currency =
      userProfile.info.currencyUomId != null
        ? userProfile.info.currencyUomId
        : "VND";
    const value = new Intl.NumberFormat(locale, {
      style: "currency",
      currency: currency,
    }).format(grandTotal);
    return (
      <>
        <span>{value}</span>
      </>
    );
  };

  let footerGroup = (
    <ColumnGroup>
      <Row>
        <Column
          footer={t("common:order.grandTotal")}
          colSpan={6}
          footerStyle={{ backgroundColor: "#f8f9fa", textTransform: 'uppercase', textAlign: "right" }}
        />
        <Column
          footer={renderGrandTotal(grandTotal)}
          colSpan={1}
          footerStyle={{ backgroundColor: "#f8f9fa", textAlign: "right" }}
        />
        <Column
          footer={''}
          colSpan={1}
          footerStyle={{ backgroundColor: "#f8f9fa" }}
        />
      </Row>
    </ColumnGroup>
  );

  return (
    <>
      <div className="p-grid">
        <div className="p-col-12 p-mb-12 p-md-12">
          <DataTable
            footerColumnGroup={footerGroup}
            value={orderItems}
            resizableColumns
            columnResizeMode="fit"
            showGridlines
            dataKey="productId"
            editMode="cell"
            className="editable-cells-table"
            emptyMessage={t("common:dataIsEmpty")}
          >
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "4rem" }}
              bodyStyle={{ padding: "0 0.75rem" }}
              field="no"
              header="#"
              body={indexBodyTemplate}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "10rem" }}
              bodyStyle={{ padding: "0 0.75rem" }}
              field="productId"
              header={t("common:product.id")}
              body={colIdBodyTemplate}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa" }}
              bodyStyle={{ padding: "0 0.75rem" }}
              field="productName"
              header={t("common:product.name")}
              body={colDescBodyTemplate}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "12rem" }}
              bodyStyle={{ padding: "0", textAlign: 'right' }}
              field="unitAmount"
              header={t("common:product.unitPriceDefault")}
              body={currencyBodyTemplate}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "12rem" }}
              bodyStyle={{ backgroundColor: "#d4ecd5", padding: "0", textAlign: 'right'}}
              key="unitAmountEdit"
              field="unitAmountEdit"
              header={t("common:product.unitPriceCustom")}
              body={currencyBodyTemplate}
              editor={(props) => inputCurrencyEditor(props, "unitAmountEdit")}
              editorValidator={currencyValidator}
              onEditorInit={onEditorInit}
              onEditorCancel={onEditorCancel}
              onEditorSubmit={onEditorSubmit}
              onBeforeEditorHide={onBeforeEditorHide}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "8rem" }}
              bodyStyle={{ backgroundColor: "#d4ecd5", padding: "0", textAlign: 'right' }}
              key="quantity"
              field="quantity"
              header={t("common:product.quantity")}
              body={numberBodyTemplate}
              editor={(props) => inputNumberEditor(props, "quantity")}
              editorValidator={positiveIntegerValidator}
              onEditorInit={onEditorInit}
              onEditorCancel={onEditorCancel}
              onEditorSubmit={onEditorSubmit}
              onBeforeEditorHide={onBeforeEditorHide}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "10rem" }}
              bodyStyle={{ padding: "0", textAlign: 'right' }}
              field="amount"
              header={t("common:order.itemTotal")}
              body={currencyBodyTemplate}
            />
            <Column
              headerStyle={{ backgroundColor: "#f8f9fa", width: "5rem" }}
              bodyStyle={{ textAlign: "center", padding: "0 0.75rem" }}
              body={actionBodyTemplate}
            />
          </DataTable>
        </div>
      </div>
    </>
  );
};
