import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import Moment from "react-moment";
import {Calendar} from "primereact/calendar";
import {useTranslation} from "react-i18next";
import {APInvoiceAgingService} from "../../service/accounting/APInvoiceAgingService";
import {InputSwitch} from "primereact/inputswitch";

var date = new Date();
var firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
var lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);

// DataGrid Tuổi hóa đơn phải thu.
export const PayableInvoiceAging = () => {
  const {t} = useTranslation('common');
  // const [invoices, setInvoices] = useState(null);
  const [loading, setLoading] = useState(false);
  // const toast = useRef(null);
  // const [dialog, setDialog] = useState(false);
  // const [submitted, setSubmitted] = useState(false);
  // const [parties, setParties] = useState(null);
  // const [selectedItem, setSelectedItem] = useState(null);
  // const [invoiceTypes, setInvoiceTypes] = useState(null);
  const [agingInvoices, setAgingInvoices] = useState(null);
  // const [value1, setValue1] = useState('');
  // const [value2, setValue2] = useState('');
  // const [value3, setValue3] = useState('');
  // const [value4, setValue4] = useState('');
  // const [value5, setValue5] = useState('');
  const [valueFromDate, setValueFromDate] = useState('');
  const [valueToDate, setValueToDate] = useState('');
  // const [selectedDate, setSelectedDate] = useState(null);
  const [switchValue, setSwitchValue] = useState(false);
  const dt = useRef(null);

  const [lazyParams, setLazyParams] = useState({
    first: 0,
    rows: 10,
    page: 0,
    sortField: "deliveryClusterId",
    sortOrder: -1
  });
  let loadLazyTimeout = null;
  const [totalRecords, setTotalRecords] = useState(0);
  // const enumService = new EnumService();


  const apInvoiceAgingService = new APInvoiceAgingService();
  useEffect(() => {
    loadLazyData();
    // const enumService = new EnumService();
    // const invoiceReceivableService = new InvoiceReceivableService();
    // invoiceReceivableService.getInvoices().then(data => {
    //   setInvoices(data);
    // });
    // enumService.getEnums({'enumTypeId': 'InvoiceType', "pageNoLimit": true}).then( data =>{
    //   setInvoiceTypes(data.enumList);
    // });
    // invoiceReceivableService.getListPartyId().then(data => {
    //   setParties(data.partyIdList);
    // });
    // invoiceReceivableService.getInvoices().then(data => {
    //   setInvoices(data);
    // });
    // enumService.getEnums({'enumTypeId': 'InvoiceType', "pageNoLimit": true}).then( data =>{
    //   setInvoiceTypes(data.enumList);
    // });
    // invoiceReceivableService.getListPartyId().then(data => {
    //   setParties(data.partyIdList);
    // });

    setValueFromDate(firstDay);
    setValueToDate(lastDay);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [lazyParams]);

  const loadLazyData = () => {
    setLoading(true);
    if (loadLazyTimeout) {
      clearTimeout(loadLazyTimeout);
    }
    loadLazyTimeout = setTimeout(() => {
      apInvoiceAgingService.getAPInvoiceAging(transformRequest()).then(data => {
        setTotalRecords(data.totalRecords);
        setAgingInvoices(data.invoiceList);
        setLoading(false);
      });
    }, Math.random() * 1000 + 250);
  }

  const transformRequest = () => {
    let params = {
      pageIndex: lazyParams.page,
      pageSize: lazyParams.rows
    }
    if(lazyParams.sortOrder){
      params['orderByField'] = (lazyParams.sortOrder === -1? "-":"") + lazyParams.sortField
    }
    if(lazyParams.filters){
      for (let [key, value] of Object.entries(lazyParams.filters)) {
        params[key] = value.value ;
      }
    }
    return params;
  }


  // const invoicesPayableTableHeader = (
  //   <div className="table-header">
  //     {t('acc.APinvoiceAging.title')}
  //   </div>
  // );

  const bodyTemplate = (data, props) => {
    return (
      <>
        <span className="p-column-title">{props.header}</span>
        {data[props.field]}
      </>
    );
  };

  // http://localhost:8080/#/accounting3/333
  // const linkTemplate = (data, props) => {
  //   let url = "/#/editInvoice/" + data[props.field];
  //   return (
  //     <>
  //       <span className="p-column-title">{props.header}</span>
  //       <a href={url}>{data[props.field]}</a>
  //     </>
  //   );
  // };

  const timeTemplate = (data, props) => {
    return (
      <>
        <span className="p-column-title">{props.header}</span>
        <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
      </>
    );
