import React, {useState, useEffect, useRef} from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
import {Toast} from 'primereact/toast';
import Moment from "react-moment";
import {Dialog} from "primereact/dialog";
import {Dropdown} from "primereact/dropdown";
import {Calendar} from "primereact/calendar";
import {getTokenCookie} from "../../TokenCookie";
import {InputNumber} from "primereact/inputnumber";
import {PaymentOutService} from "../../service/accounting/PaymentOutService";
import {useTranslation} from "react-i18next";
import { ServiceHandle } from '../../utilities';
import Const from "../../utilities/Const";

// DataGrid Thanh toán (chi tiền). Dialog thêm mới Thanh toán (Chi tiền).
export const PaymentOut = () => {
    const {t} = useTranslation('common');
    const [payments, setPayments] = useState(null);
    const [loading, setLoading] = useState(false);
    const toast = useRef(null);
    const [dialog, setDialog] = useState(false);
    // const [selectedItem, setSelectedItem] = useState(null);
    // const [submitted, setSubmitted] = useState(false);
    const [partiesFrom, setPartiesFrom] = useState(null);
    const [partiesTo, setPartiesTo] = useState(null);
    const [invoiceTypes, setInvoiceTypes] = useState(null);
    const [value1, setValue1] = useState(''); // party chi tien
    const [value2, setValue2] = useState(''); // party nhan tien
    const [value3, setValue3] = useState(''); // Ngay lap phieu
    const [value4, setValue4] = useState(''); // Phuong thuc thanh toan
    const [value5, setValue5] = useState(''); // So tien
    const dt = useRef(null);
    // const [selectedDate, setSelectedDate] = useState(null);

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();
    const paymentOutService = new PaymentOutService();

    useEffect(() => {
        loadLazyData();
        paymentOutService.getPayments().then(data => {
            setPayments(data);
        });
        paymentOutService.getPaymentTypes().then(data => {
            setInvoiceTypes(data);
        });
        paymentOutService.getListPartyIdFrom().then(data => {
            setPartiesFrom(data.partyIdList);
        });
        paymentOutService.getListPartyIdTo().then(data => {
            setPartiesTo(data.partyIdList);
        });
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [lazyParams]);

    const loadLazyData = () => {
        setLoading(true);
        if (loadLazyTimeout) {
            clearTimeout(loadLazyTimeout);
        }
        loadLazyTimeout = setTimeout(() => {
            paymentOutService.getPayments(transformRequest()).then(data => {
                setTotalRecords(data.totalRecords);
                setPayments(data.paymentList);
                setLoading(false);
            });
        }, Math.random() * 1000 + 250);
    }

    const transformRequest = () => {
        let params = {
            pageIndex: lazyParams.page,
            pageSize: lazyParams.rows
        }
        if(lazyParams.sortOrder){
            params['orderByField'] = (lazyParams.sortOrder === -1? "-":"") + lazyParams.sortField
        }
        if(lazyParams.filters){
            for (let [key, value] of Object.entries(lazyParams.filters)) {
                params[key] = value.value ;
            }
        }
        return params;
    }

    // const tableHeader = (
    //     <div className="table-header">
    //         {t('acc.paymentOut.title')}
    //     </div>
    // );

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    const timeTemplate = (data, props) => {
        return (
            <>
                <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
            </>
        );
    };

    const saveItem = () => {
        // setSubmitted(true);
        ServiceHandle.post(Const.API.Payments_Acc,
            {
                fromPartyId: value1.partyId,
                toPartyId: value2.partyId,
                effectiveDate: value3.toISOString(),
                paymentTypeEnumId: value4.enumId,
                amount: value5
            },
            {
                headers: {
                    moquiSessionToken: getTokenCookie()
                }
            },
        )
            .then(function (response) {
                // console.log(response);
            })
            .catch(function (error) {
                // console.log(error);
            });
        toast.current.show({severity: 'success', summary: t('acc.paymentOut.noticeHeader'), detail: t('acc.paymentOut.noticeSuccess'), life: 3000});
        setDialog(false);
    }

    const hideDialog = () => {
        // setSubmitted(false);
        setDialog(false);
    }

    const openNew = () => {
        setDialog(true);
    }

    const dialogFooter = (
        <>
            <Button label={t('acc.paymentIn.btnCancel')} icon="pi pi-times" className="p-button-text" onClick={hideDialog}/>
            <Button label={t('acc.paymentIn.btnSave')} icon="pi pi-check" className="p-button-text" onClick={saveItem}/>
        </>
    );

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    // const filterDate = (value, filter) => {
    //     if (filter.toISOString() == value) {
    //         return true;
    //     }
    //     if (value === undefined || value === null) {
    //         return false;
    //     }
    // }

    // const onDateChange = (e) => {
    //     //dt.current.filter(e.value.toISOString(), 'createdDate', 'custom');
    //     dt.current.filter(e.value, 'createdDate', 'custom');
    //     setSelectedDate(e.value);
    // }

    // const dateFilter = <Calendar value={selectedDate} onChange={onDateChange} dateFormat="dd/mm/yy" className="p-column-filter" placeholder="Ngày tạo" showClear filter/>;

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    return (
        <div>
            <div className="p-col-12">
                <div className="p-field p-grid">
                    <Toast ref={toast}/>
                    <div className="p-col">
                        <Button label={t('acc.paymentOut.addNew')} icon="pi pi-plus" className="p-button-success p-mr-2 p-mb-2" onClick={openNew}/>
                    </div>
                </div>
                <div className="card">
                        <DataTable ref={dt} value={payments} paginator className="p-datatable-customers"
                                   dataKey="paymentId" emptyMessage={t('acc.paymentOut.NoItemFound')} loading={loading}
                                   filters onFilter={onFilter}
                                   first={lazyParams.first} rows={lazyParams.rows} totalRecords={totalRecords}
                                   onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]} paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                                   currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi"
                                   lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                                   rowHover resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="paymentId" header={t('acc.paymentOut.paymentId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentTypeEnumId" header={t('acc.paymentOut.paymentTypeEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentMethodId" header={t('acc.paymentOut.paymentMethodId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentInstrumentEnumId" header={t('acc.paymentOut.paymentInstrumentEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="fromPartyId" header={t('acc.paymentOut.fromPartyId')} sortable body={bodyTemplate} filter/>
                        <Column field="toPartyId" header={t('acc.paymentOut.toPartyId')} sortable body={bodyTemplate} filter/>
                        <Column field="toPaymentMethodId" header={t('acc.paymentOut.toPaymentMethodId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentGatewayConfigId" header={t('acc.paymentOut.paymentGatewayConfigId')} sortable body={timeTemplate} filter/>
                    </DataTable>
                </div>
            </div>

            <Dialog visible={dialog} style={{width: '450px'}} header={t('acc.paymentOut.paymentOutAddHeader')} modal className="p-fluid" footer={dialogFooter} onHide={hideDialog}>
                <div className="p-field">
                    <label htmlFor="d1">{t('acc.paymentOut.paymentOutAddFromParty')}</label>
                    <Dropdown id="d1" options={partiesFrom} value={value1} onChange={(e) => setValue1(e.value)} appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="d2">{t('acc.paymentOut.paymentOutAddToParty')}</label>
                    <Dropdown id="d2" options={partiesTo} value={value2} onChange={(e) => setValue2(e.value)} appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="c1">{t('acc.paymentOut.paymentOutAddVoucherDate')}</label>
                    <Calendar id="c1" showIcon value={value3} onChange={(e) => setValue3(e.value)} appendTo={document.body}></Calendar>
                </div>
                <div className="p-field">
                    <label htmlFor="d4">{t('acc.paymentOut.paymentOutAddPaymentMethod')}</label>
                    <Dropdown id="d4" options={invoiceTypes} value={value4} onChange={(e) => setValue4(e.value)} optionLabel="description" appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="i1">{t('acc.paymentOut.paymentOutAddMoneyAmount')}</label>
                    <InputNumber id="i1" value={value5} keyfilter="num" onChange={(e) => setValue5(e.value)}/>
                </div>
            </Dialog>
        </div>
    )
}
