import React, { useCallback, useContext, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useHistory } from 'react-router-dom';

import { Steps } from 'primereact/steps';
import { Button } from 'primereact/button';

import { GeneralInformation } from './GeneralInformation';
import { ContactInformation } from './ContactInformation';

import { useForm } from 'react-hook-form';
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { ToastContext } from '../../../App';

import { SalesmanService } from '../../../service/SalesmanService';

export const SalesmanCreate = () => {
    const [t] = useTranslation('common');
    const toast = useContext(ToastContext);
    const history = useHistory();

    const schema = yup.object().shape({
        partyCode: yup.string().required(t('valid.validFieldRequire')),
        fullName: yup.string().required(t('valid.validFieldRequire')),
        username: yup.string().required(t('valid.validFieldRequire')),
        newPassword: yup.string().required(t('valid.validFieldRequire')),
        newPasswordVerify: yup.string().required(t('valid.validFieldRequire')),
            //.oneOf([yup.ref("newPassword")], t('valid.passwordMustMatch'))
        telecomNumber: yup.string().required(t('valid.validFieldRequire')),
        emailAddress: yup.string().required(t('valid.validFieldRequire'))
    });

    const defaultValues = {
        partyCode: '',
        fullName: '',
        gender: null, 
        birthDate: null,
        userGroupId: null, 
        productStoreId: null, 

        username: '',
        newPassword: '',
        newPasswordVerify: '',

        residentCountryGeoId: null, 
        residentStateProvinceGeoId: null, 
        residentDistrictId: null, 
        residentWardId: null, 
        residentTarAddress: '',

        currentCountryGeoId: null, 
        currentStateProvinceGeoId: null, 
        currentDistrictId: null, 
        currentWardId: null, 
        currentTarAddress: '',

        telecomNumber: '',
        emailAddress: '',
    }
    const { control, formState: { errors, isSubmitting }, handleSubmit, setError, clearErrors, setValue, getValues } = useForm({ defaultValues, resolver: yupResolver(schema) });

    const onSubmit = useCallback(async (data) => {
        
        let newPassword = getValues('newPassword');
        let newPasswordVerify = getValues('newPasswordVerify');

        if (newPassword !== newPasswordVerify) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: t('valid.passwordMustMatch'), life: 5000 });
            return;
        }

        try {
            const salesmanService = new SalesmanService();

            // {moment().format('DD/MM/YYYY')}
            let params = {
                ...data,
                gender: data.gender?.code, 
                productStoreId: data.productStoreId?.code,
                userGroupId: data.userGroupId?.code,
                // birthDate: moment(data.birthDate).format('DD/MM/YYYY'),

                residentCountryGeoId: data.residentCountryGeoId?.code,
                residentStateProvinceGeoId: data.residentStateProvinceGeoId?.code,
                residentDistrictId: data.residentDistrictId?.code,
                residentWardId: data.residentWardId?.code,

                currentCountryGeoId: data.currentCountryGeoId?.code,
                currentStateProvinceGeoId: data.currentStateProvinceGeoId?.code,
                currentDistrictId: data.currentDistrictId?.code,
                currentWardId: data.currentWardId?.code
                
            };
            let res = await salesmanService.createSaleEmployee(params);
            if (res.partyId) {
                history.push("/sales/personalcustomer/detail/" + res.partyId)
            }
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error.response.data.errors, life: 5000 });
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const validate = (name) => {
        let flag = true;
        try {
          yup.reach(schema, name).validateSync(control._formValues[name]);
          clearErrors(name);
        } catch (error) {
          setError(name, {
            type: "manual",
            message: error.message,
          });
          flag = false;
        }
    
        return flag;
    }

    const next = (e) => {
        let flag = true;

        flag = validate('partyCode') ? flag : false;
        flag = validate('fullName') ? flag : false;
        flag = validate('username') ? flag : false;
        flag = validate('newPassword') ? flag : false;
        flag = validate('newPasswordVerify') ? flag : false;

        if (activeIndex < 2 && flag) {
          setActiveIndex(activeIndex + 1);
        } 
    }
    
    const previous = (e) => {
        if (activeIndex > 0) {
            setActiveIndex(activeIndex - 1);
        }
    }

    const [activeIndex, setActiveIndex] = useState(0);
    const items = [
        { label: t('customer.generallInformation') },
        { label: t('customer.contactInfomation') },
        { label: t('customer.confirmation') }
    ];
    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <h5>{t('salesman.create')}</h5>
                    <Steps model={items} activeIndex={activeIndex} />

                    {activeIndex === 0 && <GeneralInformation control={control} errors={errors} />}
                    {activeIndex === 1 && <ContactInformation control={control} errors={errors} setValue={setValue} />}

                    <div className="p-grid">
                        <div className="p-col-12 p-md-6"></div>
                        <div className="p-col-12 p-md-6">
                            <div className="p-field p-grid">
                                <div className="p-col-12 p-md-6"></div>
                                <div className="p-col-12 p-md-3">
                                    <Button disabled={activeIndex === 0} label={t('previous')} className="p-button-primary p-mr-2 p-mb-2" onClick={(e) => previous(e)} />
                                </div>
                                <div className="p-col-12 p-md-3">
                                    {activeIndex === 0 && <Button disabled={activeIndex === 2} label={t('next')} className="p-button-primary p-mr-2 p-mb-2" onClick={(e) => next(e)} />}

                                    {activeIndex === 1 && <Button disabled={isSubmitting} label={t('save')} className="p-button-primary p-mr-2 p-mb-2" onClick={handleSubmit(onSubmit)} />}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
  );
};
