import React from 'react';

import { useTranslation } from 'react-i18next';

import { DropdownLabel, CalendarLabel } from '../../../components/Form';

export const DialogCommon = (props) => {

    const { options, cycles } = props;

    const { option, setOption } = props;
    const { cycle, setCycle } = props;
    const { fromDate, setFromDate } = props;
    const { toDate, setToDate } = props;

    const [ t ] = useTranslation('common');

    return (
        <>
            <div className="p-col-12 p-md-6 p-mb-0">
                <DropdownLabel id='option' name='option' label={t('report.option')} options={options}
                    value={option} onChange={(e) =>  setOption(e.value)} />

                <CalendarLabel id="fromDate" name="fromDate" label={t('report.fromDate')} monthNavigator
                    yearNavigator yearRange="2010:2030" value={fromDate} onChange={(e) => setFromDate(e.value)}/>
            </div>

            <div className="p-col-12 p-md-6 p-mb-0">
                <DropdownLabel id='cycle' name='cycle' label={t('report.cycle')} options={cycles}
                    value={cycle} onChange={(e) =>  setCycle(e.value)} />

                <CalendarLabel id="toDate" name="toDate" label={t('report.toDate')} monthNavigator
                    yearNavigator yearRange="2010:2030" value={toDate} onChange={(e) => setToDate(e.value)}/>
            </div>
        </>
    );
};
