import React, { useState, useEffect, useRef } from 'react';
import { classNames } from 'primereact/utils';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { CustomerService } from '../service/CustomerService';
import { Dropdown } from 'primereact/dropdown';
import { Calendar } from 'primereact/calendar';
import { MultiSelect } from 'primereact/multiselect';
import { ProgressBar } from 'primereact/progressbar';

export const SupplierProduct = () => {
    return (
        <div className="datatable-crud">
            <div className="card">
                <Toolbar left={leftContents} right={rightContents} />
                <DataTable ref={dt} value={assets} lazy  dataKey="assetId" 
                    selectionMode="single"
                    paginator first={lazyParams.first} rows={lazyParams.rows} totalRecords={totalRecords} onPage={onPage}
                    onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                    onFilter={onFilter} filters={lazyParams.filters} loading={loading}
                    rowsPerPageOptions={[10, 25, 50, 100]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords}">
                    <Column header="#" body={rowIndexBodyTemplate} headerStyle={{ width: "2rem" }} />
                    <Column field="facilityId" header={t('common:inventory.facility')} sortable filter  body={facilityBodyTemplate} filterElement={facilityFilter}/>
                    <Column field="assetId" header={t('common:inventory.assetId')} sortable filter body={assetIdBodyTemplate}/>
                    <Column field="productId" header={t('common:inventory.productId')} sortable filter body={productIdBodyTemplate}/>
                    <Column field="assetName" header={t('common:inventory.assetName')} sortable filter/>
                    {/* <Column field="hasQuantity" header={t('common:inventory.hasQuantity')} sortable filter/> */}
                    <Column field="quantityOnHandTotal" header={t('common:inventory.quantityOnHandTotal')}  sortable body={numberBodyTemplate}/>
                    <Column field="lotId" header={t('common:inventory.lotId')} sortable filter />
                    {/* <Column field="manufacturedDate" header={t('common:inventory.manufacturedDate')} sortable filter body={dateBodyTemplate}/> */}
                    {/* <Column field="expectedEndOfLife" header={t('common:inventory.expectedEndOfLife')} sortable filter body={dateBodyTemplate} /> */}
                    <Column field="receivedDate" header={t('common:inventory.receivedDate')} sortable filter body={dateBodyTemplate} />
                    <Column field="statusId" header={t('common:inventory.statusId')} sortable filter body={assetStatusBodyTemplate} filterElement={assetStatusFilter} />
                </DataTable>
            </div>
        </div>
    )
}
