import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from "react-i18next";
import PropTypes from 'prop-types'; 
import { classNames } from 'primereact/utils';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
import { InputTextarea } from 'primereact/inputtextarea';
import { InputNumber } from 'primereact/inputnumber';
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { Calendar } from 'primereact/calendar';


import { FacilityService } from '../../service/FacilityService';
import { EnumService } from '../../service/EnumService';
import { UomService } from '../../service/UomService';

export const FacilityInfo = (props) => {

    const {facilityId}=props

    let emptyFacility = {
        "facilityId": "",
        "facilityTypeEnumId": "",
        "parentFacilityId": "",
        "ownerPartyId": "",
        "facilityName": "",
        "facilitySize": 0,
        "facilitySizeUomId": "",
        "openedDate": "",
        "closedDate": "",
        "description": ""
    };

    const [facility, setFacility] = useState(emptyFacility);

    const [facilities, setFacilities] = useState(null);
    const [facilityTypes, setFacilityTypes] = useState([{}]);
    const [facilitySizeUoms, setFacilitySizeUoms] = useState([]);
    const [submitted, setSubmitted] = useState(false);
    const [saveLoading, setSaveLoading] = useState(false);

    const toast = useRef(null);
    const { t } = useTranslation();

    useEffect(() => {
        const facilityService = new FacilityService();
        const enumService = new EnumService();
        const uomService = new UomService();

        const getFacilityTypes = async() =>{
            enumService.getFacilityTypes().then(data => {setFacilityTypes(data.enumList)});
        }
    
        const getFacilitySizeUoms = async() =>{
            uomService.getAreaMeasure().then(data => {setFacilitySizeUoms(data.uomList)});
        }
    
        const getAllFacilities = async() =>{
            facilityService.getFacilities({"pageNoLimit": true}).then(data => setFacilities(data.facilityList));
        }

        const getFacility = async() =>{
            // const facilityService = new FacilityService();
            // if(facilityId){
            facilityService.getFacility(facilityId).then(data => {
                let _facility = {};
                // transform data
                Object.keys(emptyFacility).map(function(key, value) {
                    _facility[key] = data[key];
                    return value;
                    });
                setFacility(_facility);
                });
            // }else{
            //     setFacility(emptyFacility);
            // }
        }

        getFacilityTypes();
        getFacilitySizeUoms();
        getAllFacilities();
        getFacility();
        return ()  => {
            setFacilityTypes([{}]);
            setFacilitySizeUoms([{}]);
            setFacilities([{}]);
            setFacility(emptyFacility);
        }
            
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    // const getFacilityTypes = () =>{
    //     enumService.getFacilityTypes().then(data => {setFacilityTypes(data.enumList)});
    // }

    // const getFacilitySizeUoms = () =>{
    //     uomService.getAreaMeasure().then(data => {setFacilitySizeUoms(data.uomList)});
    // }

    // const getAllFacilities = () =>{
    //     facilityService.getFacilities({"pageNoLimit": true}).then(data => setFacilities(data.facilityList));
    // }
    
    const saveFacility = () => {
        const facilityService = new FacilityService();
        setSubmitted(true);
        setSaveLoading(true);
        if(props.facilityId){
            facilityService.updateFacility(props.facilityId, facility).then(data =>{
                setSaveLoading(false);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: t('common:facility.updated'), life: 3000 });
            });
        }else{
            facilityService.createFacility(facility).then(data =>{
                setSaveLoading(false);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: t('common:facility.created'), life: 3000 });
            });
        }
    }

    const onInputChange = (e, name) => {
        const val = (e.target && e.target.value) || '';
        let _facility = {...facility};
        _facility[`${name}`] = val;

        setFacility(_facility);
    }

    const onInputNumberChange = (e, name) => {
        const val = e.value || 0;
        let _facility = {...facility};
        _facility[`${name}`] = val;

        setFacility(_facility);
    }
 
    return (
        <div>
            <div className="card">
                <Toast ref={toast} />
                <div className="p-fluid p-formgrid p-grid">
                    <div className="p-field p-col-12 p-md-6">
                        <label htmlFor="facilityId">{t('common:facility.facilityId')}</label>
                        <InputText id="facilityId" value={facility.facilityId} onChange={(e) => onInputChange(e, 'facilityId')} disabled={facility.facilityId}/>
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label htmlFor="facilityName">{t('common:facility.facilityName')}</label>
                        <InputText id="facilityName" value={facility.facilityName} onChange={(e) => onInputChange(e, 'facilityName')} required className={classNames({ 'p-invalid': submitted && !facility.facilityName })} />
                        {submitted && !facility.facilityName && <small className="p-error">Name is required.</small>}
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label className="p-mb-3">{t('common:facility.facilityTypeEnumId')}</label>
                        <Dropdown id="facilityTypeEnumId" value={facility.facilityTypeEnumId} options={facilityTypes} optionValue="enumId" optionLabel="description"  onChange={(e) => onInputChange(e, 'facilityTypeEnumId')} showClear filter filterBy="description"/>
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label className="p-mb-3">{t('common:facility.parentFacilityId')}</label>
                        <Dropdown id="parentFacilityId" value={facility.parentFacilityId} options={facilities} optionValue="facilityId" optionLabel="facilityName"  onChange={(e) => onInputChange(e, 'parentFacilityId')} showClear filter filterBy="facilityName"/>
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label className="p-mb-3">{t('common:facility.ownerPartyId')}</label>
                        <InputText id="ownerPartyId" value={facility.ownerPartyId}  onChange={(e) => onInputChange(e, 'ownerPartyId')}/>
                    </div>

                    <div className="p-field p-col-12 p-md-3">
                        <label htmlFor="openedDate">{t('common:facility.openedDate')}</label>
                        <Calendar id="openedDate" value={new Date(facility?.openedDate)} onChange={(e) => onInputChange(e, 'openedDate')} showIcon></Calendar>
                    </div>

                    <div className="p-field p-col-12 p-md-3">
                        <label htmlFor="closedDate">{t('common:facility.closedDate')}</label>
                        <Calendar id="closedDate" value={new Date(facility?.closedDate)} onChange={(e) => onInputChange(e, 'closedDate')} showIcon></Calendar>
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label className="p-mb-3">{t('common:facility.facilitySize')}</label>
                        <div className="p-formgrid p-grid">
                            <div className="p-field p-col">
                                <InputNumber id="facilitySize" value={facility.facilitySize} onValueChange={(e) => onInputNumberChange(e, 'facilitySize')} />
                            </div>
                            <div className="p-field p-col">
                                <Dropdown id="facilitySizeUomId" value={facility.facilitySizeUomId} options={facilitySizeUoms} optionLabel="description" optionValue="uomId" onChange={(e) => onInputChange(e, 'facilitySizeUomId')} showClear filter filterBy="description"/>
                            </div>
                        </div>
                    </div>

                    <div className="p-field p-col-12 p-md-6">
                        <label htmlFor="description">{t('common:facility.description')}</label>
                        <InputTextarea id="description" value={facility.description} onChange={(e) => onInputChange(e, 'description')} required rows={3} cols={20} />
                    </div>
                    <div className="p-grid p-col-12 p-justify-end">
                        <span className="p-buttonset">
                            <Button label={t('common:facility.save')} icon="pi pi-check" loading={saveLoading} onClick={saveFacility} />
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
};

FacilityInfo.propTypes  = {
    facilityId: PropTypes.string.isRequired
};
