import React, { useState, useEffect } from "react";
import { Dropdown } from "primereact/dropdown";
import { Calendar } from "primereact/calendar";
import { useTranslation } from "react-i18next";
import { Controller } from "react-hook-form";
import { InputTextarea } from "primereact/inputtextarea";
import { classNames } from "primereact/utils";
import { PurchasingService } from "../service/PurchasingService";

export const PurchasingCreateOrderHeaderForm = (props) => {
  const { control, errors, setSupplierSelected} = props;

  const [t] = useTranslation("common");
  const [suppliers, setSuppliers] = useState([]);
  const [facilities, setFacilities] = useState([]);
  useEffect(() => {
    const purchasingService = new PurchasingService();
    const vendorRole = "Supplier,Vendor,VendorBillFrom";
    purchasingService
      .searchPartyList({ pageIndex: 0, roleTypeId: vendorRole })
      .then((data) => {
        setSuppliers(data.outList);
      });

    purchasingService
      .getFacilityList({ pageIndex: 0, facilityTypeEnumId: "FcTpWarehouse" })
      .then((data) => {
        setFacilities(data.outList);
      });
  }, []);

  const priorities = Array.from(Array(9).keys()).map((item) => ({
    value: item + 1,
    name: item + 1,
  }));

  const getFormErrorMessage = (name) => {
    return (
      errors[name] && <small className="p-error">{errors[name]?.message}</small>
    );
  };

  return (
    <div className="p-grid">
      <div className="p-col-12 p-md-6">
        <div className="p-fluid">
          <div className="p-field p-grid">
            <label
              htmlFor="supplier"
              className={classNames({
                "p-col-12 p-mb-2 p-md-3 p-mb-md-0": true,
                "p-error": errors.supplier,
                "required": true,
              })}
            >
              {t("common:homepage.purchasing.Supplier")}
            </label>
            <div className="p-col-12 p-md-6">
              <Controller
                name="supplier"
                control={control}
                render={({ field, fieldState }) => (
                  <Dropdown
                    id={field.name}
                    value={field.value}
                    onChange={(e) => {
                      field.onChange(e.value);
                      setSupplierSelected(e.value);
                    }}
                    options={suppliers}
                    optionLabel="name"
                    className={classNames({ "p-invalid": fieldState.invalid })}
                  />
                )}
              />
              {getFormErrorMessage("supplier")}
            </div>
          </div>
          <div className="p-field p-grid">
            <label
              htmlFor="originFacility"
              className={classNames({
                "p-col-12 p-mb-2 p-md-3 p-mb-md-0": true,
                "p-error": errors.originFacility,
              })}
            >
              {t("common:receiveToFacility")}
              <span style={{ color: "red" }}>&nbsp;*</span>
            </label>
            <div className="p-col-12 p-md-6">
              <Controller
                name="originFacility"
                control={control}
                render={({ field, fieldState }) => (
                  <Dropdown
                    id={field.name}
                    value={field.value}
                    onChange={(e) => field.onChange(e.value)}
                    options={facilities}
                    optionLabel="name"
                    className={classNames({ "p-invalid": fieldState.invalid })}
                  />
                )}
              />
              {getFormErrorMessage("originFacility")}
            </div>
          </div>
          <div className="p-field p-grid">
            <label
              htmlFor="priority"
              className={classNames({
                "p-col-12 p-mb-2 p-md-3 p-mb-md-0": true,
                "p-error": errors.priority,
              })}
            >
              {t("common:priority")}
              <span style={{ color: "red" }}>&nbsp;*</span>
            </label>
            <div className="p-col-12 p-md-6">
              <Controller
                name="priority"
                control={control}
                render={({ field, fieldState }) => (
                  <Dropdown
                    id={field.name}
                    value={field.value}
                    onChange={(e) => field.onChange(e.value)}
                    options={priorities}
                    optionLabel="name"
                    className={classNames({ "p-invalid": fieldState.invalid })}
                  />
                )}
              />
              {getFormErrorMessage("priority")}
            </div>
          </div>
        </div>
      </div>
      <div className="p-col-12 p-md-6">
        <div className="p-field p-grid">
          <label
            htmlFor="shipAfterDate"
            className={classNames({
              "p-col-12 p-mb-2 p-md-3 p-mb-md-0": true,
              "p-error": errors.shipAfterDate,
            })}
          >
            {t("common:order.shipAfterDate")}
            <span style={{ color: "red" }}>&nbsp;*</span>
          </label>
          <div className="p-col-12 p-md-6">
            <Controller
              name="shipAfterDate"
              control={control}
              render={({ field, fieldState }) => (
                <Calendar
                  id={field.name}
                  value={field.value}
                  onChange={(e) => field.onChange(e.value)}
                  dateFormat="dd/mm/yy"
                  mask="99/99/9999 99:99"
                  showTime
                  hideOnDateTimeSelect
                  showIcon
                  className={classNames({ "p-invalid": fieldState.invalid })}
                />
              )}
            />
            {getFormErrorMessage("shipAfterDate")}
          </div>
        </div>
        <div className="p-field p-grid">
          <label
            htmlFor="shipBeforeDate"
            className={classNames({
              "p-col-12 p-mb-2 p-md-3 p-mb-md-0": true,
              "p-error": errors.shipBeforeDate,
            })}
          >
            {t("common:order.shipBeforeDate")}
            <span style={{ color: "red" }}>&nbsp;*</span>
          </label>
          <div className="p-col-12 p-md-6">
            <Controller
              name="shipBeforeDate"
              control={control}
              render={({ field, fieldState }) => {
                return (
                  <Calendar
                    id={field.name}
                    value={field.value}
                    onChange={(e) => field.onChange(e.value)}
                    dateFormat="dd/mm/yy"
                    mask="99/99/9999 99:99"
                    showTime
                    hideOnDateTimeSelect
                    showIcon
                    className={classNames({ "p-invalid": fieldState.invalid })}
                  />
                );
              }}
            />
            {getFormErrorMessage("shipBeforeDate")}
          </div>
        </div>
        <div className="p-field p-grid">
          <label
            className="p-col-12 p-mb-2 p-md-3 p-mb-md-0"
            htmlFor="orderName"
          >
            {t("common:order.orderName")}
          </label>
          <div className="p-col-12 p-md-6">
            <Controller
              name="orderName"
              control={control}
              render={({ field }) => (
                <InputTextarea
                  id={field.name}
                  value={field.value}
                  onChange={(e) => field.onChange(e.target.value)}
                />
              )}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
