import {Column} from 'primereact/column';
import {DataTable} from 'primereact/datatable';
import {InputText} from 'primereact/inputtext';
import {Button} from 'primereact/button';
import {Dialog} from 'primereact/dialog';
import {confirmDialog} from 'primereact/confirmdialog';
import {Toast} from 'primereact/toast';
import React, {useEffect, useState, useCallback, useRef} from 'react';
import {useTranslation} from 'react-i18next';
import {useHistory, useParams} from 'react-router-dom';
import {useForm, Controller} from 'react-hook-form';
import {classNames} from 'primereact/utils';
import 'primeicons/primeicons.css';
import 'primeflex/primeflex.css';
import {DeliveryClusterService} from '../../../service/DeliveryClusterService';
import {Dropdown} from 'primereact/dropdown';
import {PartyService} from '../../../service/PartyService';
import {InputTextarea} from 'primereact/inputtextarea';

export const DeliveryClusterDetail = () => {
  let {id} = useParams();

  const dt = useRef(null);
  const toast = useRef(null);
  const {t} = useTranslation('common');
  const history = useHistory();
  const [deliveryCluster, setDeliveryCluster] = useState({});
  const [deliveryClusterCustomers, setDeliveryClusterCustomers] = useState([]);
  const [executor, setExecutor] = useState([]);
  const [fullNameShipper, setFullNameShipper] = useState('');
  const [deleteDialog, setDeleteDiglog] = useState(false);
  const [globalFilter, setGlobalFilter] = useState('');
  const [selectedCustomers, setSelectedCustomers] = useState([]);
  const [selectedCandidateCustomers, setSelectedCandidateCustomers] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [loading, setLoading] = useState(false);
  const [candidateCustLoading, setCandidateCustLoading] = useState(false);
  const [editFormClusterLoading, setEditFormClusterLoading] = useState(false);
  const [parties, setParties] = useState(null);
  // const [shipper, setShipper] = useState();

  const [customers, setCustomers] = useState([]);
  const [lazyParams, setLazyParams] = useState({
    first: 0,
    rows: 10,
    page: 0,
    sortField: null,
    sortOrder: null,
    filters: {},
  });

  const defaultValues = {
    clusterCode: '',
    shipper: '',
    clusterName: '',
    description: '',
  };
  const {
    control,
    formState: {errors},
    handleSubmit,
    setValue,
  } = useForm({defaultValues});

  const init = useCallback(async () => {
    const deliveryClusterService = new DeliveryClusterService();
    let dataLable = await deliveryClusterService.getDeliveryClustersDetail(id);

    setDeliveryCluster(dataLable.data.deliveryCluster);
    setValue('clusterCode', dataLable.data.deliveryCluster.deliveryClusterCode);
    setValue('clusterName', dataLable.data.deliveryCluster.deliveryClusterName);
    setValue('description', dataLable.data.deliveryCluster.description);
    setValue('shipper', dataLable.data.executor.partyId);

    setExecutor(dataLable.data.executor);
    let name = executor?.firstName ? executor.firstName + ' ' : '';
    name += executor?.middleName ? executor.middleName + ' ' : '';
    name += executor?.lastName ? executor.lastName + ' ' : '';
    setFullNameShipper(name);
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    init();
  }, [editFormClusterLoading]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    const deliveryClusterService = new DeliveryClusterService();

    const getDeliveryClusterCustomers = async () => {
      let dataTable = await deliveryClusterService.getDeliveryClusterCustomers(id, {pageNoLimit: true});
      setDeliveryClusterCustomers(dataTable.customerList);
    };

    getDeliveryClusterCustomers();
  }, [loading]); // eslint-disable-line react-hooks/exhaustive-deps

  // call shiper optionValue
  useEffect(() => {
    const partyService = new PartyService();
    partyService.search({roleTypeId: 'Shipper', pageNoLimit: true}).then((data) => {
      setParties(data.documentList);
    });
  }, []);

  useEffect(() => {
    const loadLazyData = async () => {
      const deliveryClusterService = new DeliveryClusterService();

      deliveryClusterService.getCandidateCustomers().then((data) => {
        setCustomers(data.customerList);
        setTotalRecords(data.totalRecords);
      });
    };

    loadLazyData();
  }, [lazyParams, candidateCustLoading]);

  const showconfirmDeleteCustomer = () => {
    confirmDialog({
      message: <p>{t('common:confirmDialogMessage')}</p>,
      header: <h6>{t('common:confirmDialogHeader')}</h6>,
      icon: 'pi pi-info-circle',
      acceptClassName: 'p-button-danger',
      accept: () => submitDeleteCustomer(),
    });
  };

  const getFormErrorMessage = (name) => {
    return errors[name] && <small className="p-error">{errors[name].message}</small>;
  };
  const submitDeleteCustomer = async () => {
    const deliveryClusterService = new DeliveryClusterService();

    let params = {};
    params.customers = selectedCustomers.map((item) => ({partyId: item.partyId}));

    const deleteDeliveryClusterCustomerDetail = await deliveryClusterService.deleteDeliveryClusterCustomers(id, params);
    if (deleteDeliveryClusterCustomerDetail) {
      toast.current.show({severity: 'info', summary: 'Confirmed', detail: 'Delete customer success', life: 3000});
      setLoading(!loading); // reload customer list
    }
  };

  const showConfirmDeleteDeliveryCluster = () => {
    confirmDialog({
      message: <p>{t('common:confirmDialogMessage')}</p>,
      header: <h6>{t('common:confirmDialogHeader')}</h6>,
      icon: 'pi pi-info-circle',
      acceptClassName: 'p-button-danger',
      accept: () => submitDeleteDeliveryCluster(),
    });
  };
  const onSort = (event) => {
    let _lazyParams = {...lazyParams, ...event};
    setLazyParams(_lazyParams);
  };

  const submitDeleteDeliveryCluster = () => {
    const deliveryClusterService = new DeliveryClusterService();

    const deleteDeliveryCluster = deliveryClusterService.deleteDeliveryCluster(id);
    if (deleteDeliveryCluster) {
      setDeleteDiglog(true);
    }
  };
  const shipperOptionTemplate = (option) => {
    return (
      <span>
        [{option.partyId}] {option.combinedName}
      </span>
    );
  };

  const openAddCustDialog = () => {
    setDialogCandidateCust(true);
    setSelectedCandidateCustomers([]);
    setCandidateCustLoading(!candidateCustLoading);
  };

  const header = (
    <div className="table-header">
      <h6 className="p-m-0">{t('common:logistics.deliveryClusterDetail.customerList')}</h6>
      <div>
        <Button
          label={t('homepage.logistics.addNew')}
          icon="pi pi-plus"
          className="p-mr-2 p-mb-2 p-button-sm"
          onClick={openAddCustDialog}
        />
        <Button
          label={t('delete')}
          icon="pi pi-times"
          className="p-mr-2 p-mb-2 p-button-sm"
          onClick={showconfirmDeleteCustomer}
          disabled={!selectedCustomers.length}
        />

        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText
            type="search"
            value={globalFilter}
            onChange={(e) => setGlobalFilter(e.target.value)}
            placeholder=""
          />
        </span>
      </div>
    </div>
  );

  const addressBodyTemplate = (rowData) => {
    let address = rowData.address1 + ', ' + rowData.wardName + ', ' + rowData.countryName;
    return (
      <React.Fragment>
        <span>{address}</span>
      </React.Fragment>
    );
  };

  const deleteDeliveryClusterSuccessDialogFooter = (
    <Button label="Ok" onClick={() => history.push('/logistics/poscluster')} />
  );

  // start Edit lable\
  const [dialogEditFormCluster, setDialogEditFormCluster] = useState(false);
  const [dialogCandidateCust, setDialogCandidateCust] = useState(false);
  const [position, setPosition] = useState('center');

  const dialogFuncMap = {
    displayBasic: setDialogEditFormCluster,
    displayBasic2: setDialogCandidateCust,
  };

  const onClick = (name, position) => {
    dialogFuncMap[`${name}`](true);

    if (position) {
      setPosition(position);
    }
  };

  const onHide = (name) => {
    dialogFuncMap[`${name}`](false);
  };

  const onSubmitEditFormCluster = (data) => {
    const deliveryClusterService = new DeliveryClusterService();

    // let clusterId = '';
    let params = {
      deliveryClusterCode: data.clusterCode,
      deliveryClusterName: data.clusterName,
      description: data.description,
      executorId: data.shipper,
      statusId: 'Y',
    };

    deliveryClusterService.updateDeliveryCluster(id, params).then(function (response) {
      setDialogEditFormCluster(false);
      setEditFormClusterLoading(!editFormClusterLoading);
      toast.current.show({
        severity: 'info',
        summary: 'Confirmed',
        detail: 'Update delivery cluster successs',
        life: 3000,
      });
    });
  };

  const onSubmitAddCandidateCust = async (data) => {
    const deliveryClusterService = new DeliveryClusterService();

    let params = {};
    params.customers = selectedCandidateCustomers.map((item) => ({partyId: item.partyId}));

    const updateDeliveryClusterCustomerDetail = await deliveryClusterService.addDeliveryClusterCustomers(id, params);
    if (updateDeliveryClusterCustomerDetail) {
      toast.current.show({severity: 'info', summary: 'Confirmed', detail: 'Add customers success', life: 3000});
      setDialogCandidateCust(false);
      setLoading(!loading); // reload customer list
    }
  };

  const renderFooter = (name) => {
    return (
      <div>
        <Button label={t('common:cancel')} icon="pi pi-times" onClick={() => onHide(name)} className="p-button-text" />
        <Button
          type="submit"
          label={t('common:save')}
          icon="pi pi-save"
          className="p-mt-2"
          onClick={handleSubmit(onSubmitEditFormCluster)}
          autoFocus
        />
      </div>
    );
  };

  const renderFooterTable = () => {
    return (
      <div>
        <Button
          label={t('common:cancel')}
          icon="pi pi-times"
          onClick={() => setDialogCandidateCust(false)}
          className="p-button-text"
        />
        <Button
          type="submit"
          label={t('common:save')}
          icon="pi pi-save"
          className="p-mt-2"
          // onClick={handleSubmit(onSubmitAddCandidateCust)}
          onClick={onSubmitAddCandidateCust}
          autoFocus
        />
      </div>
    );
  };

  return (
    <>
      <Toast ref={toast} />

      <div className="card">
        <div className="p-d-flex p-jc-between">
          <div>
            {' '}
            <h5>{t('logistics.deliveryClusterDetail.title')}</h5>
          </div>
          <div>
            <Button
              label={t('delete')}
              icon="pi pi-trash"
              className="p-mr-2 p-mb-2"
              onClick={showConfirmDeleteDeliveryCluster}
            />
            <Button
              label={t('edit')}
              icon="pi pi-user-edit"
              className="p-mr-2 p-mb-2"
              onClick={() => onClick('displayBasic')}
            />
            <Button label="Map" icon="pi pi-map" className="p-mb-2 " />
          </div>
        </div>

        <div className="p-grid">
          <div className="p-col-12 p-md-6">
            <label htmlFor="address13" className="p-col-fixed " style={{width: '100px'}}>
              {t('logistics.addDeliveryCluster.clusterCode')}
              {' : '}
            </label>
            <span>{deliveryCluster.deliveryClusterCode || ' '}</span>
          </div>
          <div className="p-col-12 p-md-6">
            <label htmlFor="countryName3" className="p-col-fixed " style={{width: '100px'}}>
              {t('logistics.addDeliveryCluster.clusterName')}
              {' : '}
            </label>
            <span>{deliveryCluster.deliveryClusterName || ' '}</span>
          </div>
        </div>
        <div className="p-grid">
          <div className="p-col-12 p-md-6">
            <label htmlFor="address13" className="p-col-fixed " style={{width: '100px'}}>
              {t('logistics.addDeliveryCluster.shipperName')}
              {' :'}
            </label>
            <span>
              [{deliveryCluster.executorId || ' '}] {fullNameShipper}
            </span>
          </div>
          <div className="p-col-12 p-md-6">
            <label htmlFor="countryName3" className="p-col-fixed " style={{width: '100px'}}>
              {t('logistics.addDeliveryCluster.description')}
              {' : '}
            </label>
            <span>{deliveryCluster.description || ' '}</span>
          </div>
        </div>

        <DataTable
          ref={dt}
          value={deliveryClusterCustomers}
          header={header}
          globalFilter={globalFilter}
          paginator
          rows={10}
          showGridlines
          selectionMode="single"
          emptyMessage={t('noRecord')}
          paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
          currentPageReportTemplate="Showing {first} to {last} of {totalRecords}"
          rowsPerPageOptions={[10, 25, 50, 100]}
          selection={selectedCustomers}
          onSelectionChange={(e) => setSelectedCustomers(e.value)}
        >
          <Column selectionMode="multiple" headerStyle={{width: '3em'}}></Column>
          <Column field="partyId" sortable header={t('logistics.deliveryClusterDetail.customerId')} />
          <Column field="toName" sortable header={t('logistics.deliveryClusterDetail.customerName')} />
          <Column sortable header={t('logistics.deliveryClusterDetail.address')} body={addressBodyTemplate} />
        </DataTable>
      </div>

      <Dialog
        visible={deleteDialog}
        showHeader={false}
        style={{width: '30vw'}}
        footer={deleteDeliveryClusterSuccessDialogFooter}
      >
        <div className="p-d-flex p-ai-center p-dir-col p-pt-6 p-px-3">
          <i className="pi pi-check-circle" style={{fontSize: '5rem', color: 'var(--green-500)'}}></i>
          <h5>{t('common:logistics.deliveryCluster.deleted')}</h5>
          <p style={{lineHeight: 1.5, textIndent: '1rem'}}></p>
        </div>
      </Dialog>

      <Dialog
        header={t('logistics.deliveryClusterDetail.deliveryClusterDetailEditTitle')}
        visible={dialogEditFormCluster}
        style={{width: '60vw'}}
        footer={renderFooter('displayBasic')}
        onHide={() => onHide('displayBasic')}
        className="card"
      >
        <form>
          <div className="p-col-12"></div>
          <div className="p-col-12">
            <div className="p-grid">
              <div className="p-col-12 p-md-6">
                <div className="p-field p-grid">
                  <label htmlFor="firstname3" className="p-col-fixed required" style={{width: '100px'}}>
                    {t('logistics.addDeliveryCluster.clusterCode')}
                  </label>
                  <div className="p-col-12 p-md-10">
                    <span className="p-float-label">
                      <Controller
                        name="clusterCode"
                        control={control}
                        rules={{required: t('common:valid.validFieldRequire')}}
                        render={({field, fieldState}) => (
                          <InputText
                            id={field.name}
                            value={field.value}
                            autoFocus
                            {...field}
                            className={classNames({'p-invalid': fieldState.invalid})}
                            style={{width: '100%'}}
                          />
                        )}
                      />
                    </span>
                    {getFormErrorMessage('clusterCode')}
                  </div>
                </div>
                <div className="p-field p-grid">
                  <label htmlFor="lastname3" className="p-col-fixed required" style={{width: '100px'}}>
                    {t('logistics.addDeliveryCluster.clusterName')}
                  </label>
                  <div className="p-col-12 p-md-10">
                    <span className="p-float-label">
                      <Controller
                        name="clusterName"
                        control={control}
                        rules={{required: t('common:valid.validFieldRequire')}}
                        render={({field, fieldState}) => (
                          <InputText
                            id={field.name}
                            {...field}
                            value={field.value}
                            className={classNames({'p-invalid': fieldState.invalid})}
                            style={{width: '100%'}}
                          />
                        )}
                      />
                    </span>
                    {getFormErrorMessage('clusterName')}
                  </div>
                </div>
              </div>
              <div className="p-col-12 p-md-6">
                <div className="p-field p-grid">
                  <label htmlFor="firstname3" className="p-col-fixed required" style={{width: '100px'}}>
                    {t('logistics.addDeliveryCluster.shipperName')}
                  </label>
                  <div className="p-col-12 p-md-10">
                    <span className="p-float-label">
                      <Controller
                        name="shipper"
                        control={control}
                        rules={{required: t('common:valid.validFieldRequire')}}
                        render={({field, fieldState}) => (
                          <Dropdown
                            id="shipper"
                            style={{width: '100%'}}
                            options={parties}
                            value={field.value}
                            optionValue="partyId"
                            optionLabel="combinedName"
                            itemTemplate={shipperOptionTemplate}
                            filter
                            showClear
                            filterBy="combinedName"
                            onChange={(e) => field.onChange(e.value)}
                            className={classNames({'p-invalid': fieldState.invalid})}
                          ></Dropdown>
                        )}
                      />
                    </span>
                    {getFormErrorMessage('shipper')}
                  </div>
                </div>
                <div className="p-field p-grid">
                  <label htmlFor="lastname3" className="p-col-fixed " style={{width: '100px'}}>
                    {t('logistics.addDeliveryCluster.description')}
                  </label>
                  <div className="p-col-12 p-md-10">
                    <span className="p-float-label">
                      <Controller
                        name="description"
                        control={control}
                        render={({field}) => (
                          <InputTextarea id={field.name} {...field} value={field.value} style={{width: '100%'}} />
                        )}
                      />
                    </span>
                    {getFormErrorMessage('description')}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      </Dialog>

      {/* Table*/}
      <Dialog
        header="Thêm mới đại lý"
        visible={dialogCandidateCust}
        style={{width: '80vw'}}
        footer={renderFooterTable}
        onHide={() => setDialogCandidateCust(false)}
      >
        <div className="p-grid table-demo">
          <div className="p-col-12">
            <div>
              <h6>{t('common:logistics.addDeliveryCluster.customerList')}</h6>

              <DataTable
                value={customers}
                dataKey="partyId"
                emptyMessage={t('logistics.addDeliveryCluster.emptyMessage')}
                showGridlines
                rowHover
                totalRecords={totalRecords}
                resizableColumns
                paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                currentPageReportTemplate="Showing {first} to {last} of {totalRecords}"
                onSort={onSort}
                sortField={lazyParams.sortField}
                paginator
                first={lazyParams.first}
                rows={lazyParams.rows}
                rowsPerPageOptions={[10, 25, 50, 100]}
                sortOrder={lazyParams.sortOrder}
                selection={selectedCandidateCustomers}
                onSelectionChange={(e) => setSelectedCandidateCustomers(e.value)}
              >
                <Column selectionMode="multiple" headerStyle={{width: '3em'}}></Column>
                <Column field="partyId" sortable header={t('logistics.addDeliveryCluster.customerCode')} />
                <Column field="toName" sortablesortable header={t('logistics.addDeliveryCluster.customerName')} />

                <Column body={addressBodyTemplate} header={t('logistics.addDeliveryCluster.address')} />
              </DataTable>
            </div>
          </div>
        </div>
      </Dialog>
    </>
  );
};
