import React, { useState, useRef } from 'react';
import { useTranslation } from "react-i18next";
import PropTypes from 'prop-types'; 
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';
import { InputText } from 'primereact/inputtext';

export const InventoryDetailHistoryList = (props) => {

    const { assetDetailList } = props;

    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    const dt = useRef(null);
    const { t } = useTranslation();

    const sourceBodyTemplate = (rowData) => {
        return (
            <div>
                <span>{rowData.assetReceiptId && `Receipt #${rowData.assetReceiptId}`} </span>
                <span>{rowData.assetReservationId && `Reservation #${rowData.assetReservationId}`} </span>
                <span>{rowData.orderId && `Order #${rowData.orderId}`} </span>
            </div>
        )
    }

    const header = (
        <div className="table-header">
            <h5 className="p-m-0">{t('common:inventory.detailList')}</h5>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const dateBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <span>{new Date(rowData.effectiveDate).toLocaleString()}</span>
            </React.Fragment>
        );
    }
    const numberBodyTemplate = (rowData, props) => {
        return (
            <React.Fragment>
                 <span> {rowData[props.field] != null && rowData[props.field].toLocaleString()} </span>
            </React.Fragment>
        );
    }

    return (
        <div className="datatable-crud">
            <Toast ref={toast} />

            <div className="card">
                <DataTable ref={dt} value={assetDetailList} selectionMode="single"
                    dataKey="assetId" paginator rows={10} rowsPerPageOptions={[5, 10, 25, 50, 100]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords}"
                    header={header} globalFilter={globalFilter}>
                    <Column field="effectiveDate" header={t('common:inventory.effectiveDate')} body={dateBodyTemplate} sortable ></Column>
                    <Column field="quantityOnHandDiff" header={t('common:inventory.quantityOnHandDiff')} sortable body={numberBodyTemplate}></Column>
                    <Column field="availableToPromiseDiff" header={t('common:inventory.availableToPromiseDiff')} sortable body={numberBodyTemplate}></Column>
                    <Column field="assetReceiptId" header={t('common:inventory.assetReceiptId')} sortable></Column>
                    <Column field="assetReservationId" header={t('common:inventory.assetReservationId')} sortable></Column>
                    <Column field="orderId" header={t('common:inventory.orderId')} sortable></Column>
                    <Column header="Source"  body={sourceBodyTemplate}></Column>
                </DataTable>
            </div>
           
        </div>
    );
}

InventoryDetailHistoryList.propTypes  = {
    assetDetailList: PropTypes.array.isRequired
};
