import React, { useState, useEffect, useRef, useCallback, useContext } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from "primereact/inputtext";

import { ProductService } from '../../../service/ProductService';

import { useTranslation } from 'react-i18next';

import { ToastContext } from "../../../App";

export const CategoryList = () => {
    const { t } = useTranslation('common');
    const toast = useContext(ToastContext);

    const [loading, setLoading] = useState(false);
    const [categories, setCategories] = useState(null);
    const [productCategoryIdFilter, setProductCategoryIdFilter] = useState(null);
    const [descriptionFilter, setDescriptionFilter] = useState(null);
    const [productCategoryFilter, setProductCategoryFilter] = useState(null);

    const [lazyParams, setLazyParams] = useState({ first: 0, rows: 10, page: 0, sortField: "productId", sortOrder: -1 });
    const [totalRecords, setTotalRecords] = useState(0);
    
    const dt = useRef(null);
    const typingTimeoutRef = useRef(null);

    const emptyMessage = <p style={{textAlign: 'center'}}>{t('common:noOrdersFound')}</p>;

    useEffect(() => {
        initData(lazyParams);

        document.title = t('common:title.productList');

        return () => {
            document.title = t('common:title.homepage');
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [lazyParams]);

    const getParams = useCallback((lazyParams) => {
        const params = {};
        const filters = lazyParams?.filters;

        if (lazyParams?.page && lazyParams?.page >= 0) params.pageIndex = lazyParams?.page;
        if (lazyParams?.rows) params.pageSize = lazyParams?.rows;
        if (lazyParams?.sortField && lazyParams?.sortOrder) params.orderByField = lazyParams?.sortOrder === 1 ? lazyParams?.sortField : "-" + lazyParams?.sortField;
        if (filters && Object.keys(filters).length !== 0) {
            for (const key in filters) {
                if (Object.hasOwnProperty.call(filters, key)) {
                    const filter = filters[key];
                    const matchMode = filter.matchMode;
                    const value = filter.value;
                    if (value == null || value === "") continue;
                    switch (matchMode) {
                        case "timestamprange":
                            params[key + "From"] = value[0].getTime(); //get beginning of the day
                            params[key + "Thru"] = value[1].getTime() + 86400000 - 1; //get end of the day
                            break;
                        case "contains":
                            params[key + "Term"] = value;
                            break;
                        case "in":
                            params[key + "s"] = value.join();
                            break;
                        case "equals":
                        case "not-equals":
                        case "greater":
                        case "greater-equals":
                        case "less":
                        case "less-equals":
                            params[key + "Str"] = value;
                            params[key + "Op"] = matchMode;
                            break;
                        case "is-not-null":
                        case "is-null":
                            params[key + "Op"] = matchMode;
                            break;
                        default:
                            break;
                    }
                }
            }
        }

        return params;
    }, []);

    const initData = useCallback(async (lazyParams) => {
        const productService = new ProductService();
        setLoading(true);

        try {
            let params = getParams(lazyParams);
            const data = await productService.getCategoryList(params);

            setCategories(data.categories);
            setTotalRecords(data.totalRecords);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error.response.data.errors, life: 5000 });
        } finally {
            setLoading(false);
        }
    }, [getParams, toast, t]);

    const bodyTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    }, []);

    const bodyIdTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                <a href={`#/sales/category/detail/${data[props.field]}`} style={{color: '#2196f3'}}>
                    {data[props.field]}
                </a>
            </>
        );
    }, []);

    const onFilterTextChange = useCallback((e) => {
        let obj = e.target;
        switch (obj.id) {
            case 'productCategoryId':
                setProductCategoryIdFilter(obj.value);
                break;
            case 'categoryName':
                setProductCategoryFilter(obj.value);
                break;
            case 'description':
                setDescriptionFilter(obj.value);
                break;
            default:
                break;
        }

        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }
        typingTimeoutRef.current = setTimeout(() => {
            dt.current.filter(obj.value, obj.id, 'contains');
        }, 400);
    }, []);

    const textFilter = useCallback((id, value) => <InputText type='text' id={id} value={value || ''} onChange={onFilterTextChange}></InputText>, [onFilterTextChange])

    const onPage = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e });
    }, [lazyParams]);

    const onFilter = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e, first: 0 });
    }, [lazyParams]);
    
    const onSort = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e});
    }, [lazyParams]);

    return (
        <div className="p-grid table-demo">

            <div className="p-col-12">
                <div className="card">
                    <h5>{t('title.categoryList')}</h5>

                    <DataTable 
                        value={categories} paginator className="p-datatable-customers" dataKey="productCategoryId"
                        ref={dt} filters={lazyParams.filters} lazy first={lazyParams.first} rows={lazyParams.rows}
                        totalRecords={totalRecords} rowsPerPageOptions={[5, 10, 20, 50, 100]} onPage={onPage} onFilter={onFilter} onSort={onSort}
                        sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder} rowHover 
                        paginatorTemplate="RowsPerPageDropdown CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink"
                        currentPageReportTemplate="{first} - {last} of {totalRecords}" 
                        emptyMessage={emptyMessage}
                        loading={loading} paginatorPosition="bottom"
                    >
                        <Column field="productCategoryId" header={t('category.id')} sortable body={bodyIdTemplate} filter filterElement={textFilter("productCategoryId", productCategoryIdFilter)}></Column>
                        <Column field="categoryName" header={t('category.name')} sortable filter body={bodyTemplate} filterElement={textFilter("categoryName", productCategoryFilter)}></Column>
                        <Column field="description" header={t('category.description')} sortable filter body={bodyTemplate} filterElement={textFilter("productName", descriptionFilter)}></Column>
                    </DataTable>
                </div>
            </div>
        </div>
    )
}
