import React, { useEffect, useCallback, useState } from 'react';

// import { useTranslation } from 'react-i18next';
import { Tree } from 'primereact/tree';
import { Button } from "primereact/button";

// import { ToastContext } from "../../../App";

import { ProductService } from '../../../service/ProductService';

export const CategoryTree = (props) => {
    // const { t } = useTranslation('common');
    // const toast = useContext(ToastContext);

    const { productCategoryId } = props;

    const [ categories, setCategories ] = useState(null);
    const [expandedKeys, setExpandedKeys] = useState({});

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        const productService = new ProductService();
        let params = {
            productCategoryId: productCategoryId
        }

        let data = await productService.getCategoryListRollup(params);

        setCategories(data.categoryList);
    }, [productCategoryId]);

    const expandAll = () => {
        let _expandedKeys = {};
        for (let cate of categories) {
          expandNode(cate, _expandedKeys);
        }
    
        setExpandedKeys(_expandedKeys);
      };
    
      const collapseAll = () => {
        setExpandedKeys({});
      };
    
      const expandNode = (cate, _expandedKeys) => {
        if (cate.children && cate.children.length) {
          _expandedKeys[cate.key] = true;
    
          for (let child of cate.children) {
            expandNode(child, _expandedKeys);
          }
        }
      };

    return (
        <>
            <div className="p-mb-4">
                <Button type="button" icon="pi pi-plus" onClick={expandAll} className="p-mr-2" />
                <Button type="button" icon="pi pi-minus" onClick={collapseAll} />
            </div>
            <Tree style={{height: "30rem"}} value={categories} expandedKeys={expandedKeys} onToggle={(e) => setExpandedKeys(e.value)}/>
        </>
    )
}
