import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import {useTranslation} from "react-i18next";
import {FacilityService} from "../../service/FacilityService";
import {Button} from "primereact/button";
import {useHistory} from "react-router-dom";

export const DateWithFacilityConfig = () => {
    const {t} = useTranslation('common');
    const dt = useRef(null);
    const history = useHistory();

    const [tableData, setTableData] = useState(null);
    // const [loading, setLoading] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    // let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();


    useEffect(() => {
        const facilityService = new FacilityService();
        facilityService.getFacilityRequireDateConfig().then(data => {
            setTableData(data.resultList);
            setTotalRecords(0); // fake
        });
    }, []);

    // const tableHeader = (
    //     <div className="table-header">
    //         <h3>{t('logistics.dateWithFacilityConfig.title')}</h3>
    //     </div>
    // );

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "invoiceId",
        sortOrder: -1
    });

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const openNew = () => {
        history.push("/accounting/receivable/newARinvoice");
    };

    return (
        <div>
            <div className="p-col-12">
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-9 p-md-9">
                            <h3>{t('logistics.dateWithFacilityConfig.title')}</h3>
                        </div>
                        <div className="p-col-3 p-md-3">
                            <div className="p-grid">
                                <Button label={t('logistics.dateWithFacilityConfig.btnAddNew')} icon="pi pi-plus" className="p-button-outlined" onClick={openNew} />&nbsp;&nbsp;
                                <Button label={t('logistics.dateWithFacilityConfig.btnDelete')} icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/>&nbsp;&nbsp;
                                <Button type="button" label={t('logistics.dateWithFacilityConfig.btnClearFilter')} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} />
                            </div>
                        </div>
                    </div>
                    <DataTable ref={dt} value={tableData} paginator className="p-datatable-customers"
                                   dataKey="facilityId" rowHover emptyMessage={t('logistics.dateWithFacilityConfig.emptyMessage')}
                                   selectionMode="single" onSelectionChange={(e) => setSelectedItem(e.value)}
                                   filters onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows}
                                   totalRecords={totalRecords} onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]}
                                   paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                                   currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi"
                                   lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                                   resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="facilityId" header={t('logistics.dateWithFacilityConfig.facilityCode')} body={bodyTemplate} filter/>
                        <Column field="facilityName" header={t('logistics.dateWithFacilityConfig.facilityName')} body={bodyTemplate} filter/>
                        <Column field="requireDate" header={t('logistics.dateWithFacilityConfig.dateManagement')} body={bodyTemplate} filter/>
                    </DataTable>
                </div>
            </div>
        </div>
    )
}
