import React, { useState, useEffect } from 'react';
import { useTranslation } from "react-i18next";
import { TabPanel, TabView } from 'primereact/tabview';
import { useParams } from 'react-router-dom';

import { OrderService } from '../../../service/OrderService';
import { SalesOrderPartDetailGeneral } from './SalesOrderPartDetailGeneral';
import { SalesOrderInfo } from './SalesOrderInfo';
import { SalesOrderPartShipmentList } from './SalesOrderPartShipmentList';


export const SalesOrderPartDetail = () => {

    const { orderId, orderPartSeqId} = useParams();

    let emptyOrderHeader = {
        "orderId": "",
        "enteredByPartyId": "",
        "_entity": "",
        "visitId": "",
        "productStoreId": "",
        "placedDate": null,
        "lastUpdatedStamp": null,
        "entryDate": null,
        "grandTotal": 0,
        "approvedDate": null,
        "currencyUomId": "",
        "statusId": ""
    };

    let emptyOrderPart = {
        "orderPartSeqId": "",
        "shipmentMethodEnumId": "",
        "postalContactMechId": "",
        "shipBeforeDate": null,
        "shipmentMethod": {},
        "vendor": {},
        "partTotal": 0,
        "customerPartyId": "",
        "customer": {},
        "lastUpdatedStamp": null,
        "facilityId": "",
        "facility": {},
        "priority": 0,
        "estimatedDeliveryDate": null,
        "vendorPartyId": "",
        "carrierPartyId": "",
        "carrier": {},
        "statusId": "",
        "status": {},
        "postal": {"postalAddress": {}},
        "payments": [{}],
        "items": []
    };

    const [orderHeader, setOrderHeader] = useState(emptyOrderHeader);
    const [orderPart, setOrderPart] = useState(emptyOrderPart);

    const { t } = useTranslation();
    const orderService = new OrderService();

    useEffect(() => {

        const getOrderDetail = async()=>{
            const _order = await orderService.getOrder(orderId);

            let _orderDetail = {
                orderId: _order.orderId,
                productStoreId: _order.productStoreId,
                enteredByPartyId: _order.enteredByPartyId,
                placedDate: _order.placedDate,
                entryDate: _order.entryDate,
                approvedDate: _order.approvedDate,
                currencyUomId: _order.currencyUomId,
                statusId: _order.statusId
            };
            setOrderHeader(_orderDetail);

            _order.parts.forEach(element => {
                if(element.orderPartSeqId === orderPartSeqId){
                    setOrderPart(element);
                }
            });
        }

        getOrderDetail();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    return (
        <div>
        <h5>{t('common:order.orderDetail')}</h5>
            {/* {orderDetail && <OrderStatusHistoryStep statusHistoryList={orderDetail.statusHistoryList} orderId={orderId} orderPartSeqId={orderPartSeqId}/>} */}
            <TabView>
                <TabPanel header={t('common:order.orderPartGeneral')}>
                    {orderHeader && <SalesOrderPartDetailGeneral orderHeader={orderHeader} orderPart={orderPart}/>}
                </TabPanel>
                <TabPanel header={t('common:order.orderInfo')}>
                    {orderHeader && <SalesOrderInfo orderHeader={orderHeader} orderPart={orderPart}/>}
                </TabPanel>
                <TabPanel header={t('common:order.shipment')}>
                    {orderHeader && <SalesOrderPartShipmentList orderId={orderId} orderPartSeqId={orderPartSeqId} productStoreId={orderHeader.productStoreId} facilityId={orderPart.facilityId}/>}
                </TabPanel>
                {/* <TabPanel header={t('common:order.orderItemList')}>
                    {orderDetail && <SalesOrderPartItemList orderPartItemList={orderDetail.items} orderId={orderId} orderPartSeqId={orderPartSeqId}/>}
                </TabPanel> */}
            </TabView>
        </div>
    );
}
