import React, { useState, useEffect, useCallback, useContext } from 'react';

import { useTranslation } from 'react-i18next';

import { CommonService } from '../../service/CommonService';

import { SalesLineChart } from './SalesLineChart';
import { SalesOrderInWeek } from './SalesOrderInWeek';
import { OrdersLineChart } from './OrdersLineChart';
import { SalesProductQuantityLineChart } from './SalesProductQuantityLineChart';
import { SalesPieChart } from './SalesPieChart';
import { ProductQuantityBarChart } from './ProductQuantityBarChart';
import { ProductValueBarChart } from './ProductValueBarChart';
import { StoreValueBarChart } from './StoreValueBarChart';
import { SalesmanValueBarChart } from './SalesmanValueBarChart';
import { StatisticSalesInWeek } from './StatisticSalesInWeek';

import { UserProfileContext } from '../../App';
import { ToastContext } from "../../App";

export const SalesDashboard = () => {
    const userProfile = useContext(UserProfileContext);
    const [ t ] = useTranslation('common');
    const toast = useContext(ToastContext);

    const [storeItems, setStoreItems] = useState([]);

    const height = '18rem';

    const options = [
        { name: t('report.thisDay'), code: 'day' },
        { name: t('report.thisWeek'), code: 'week' },
        { name: t('report.thisMonth'), code: 'month' },
        { name: t('report.thisQuater'), code: 'quarter' },
        { name: t('report.thisYear'), code: 'year' },
        { name: t('report.option'), code: 'option' }
    ]

    const cycles = [
        { name: t('report.day'), code: 'day' },
        { name: t('report.week'), code: 'week' },
        { name: t('report.month'), code: 'month' },
        { name: t('report.quater'), code: 'quarter' },
        { name: t('report.year'), code: 'year' }
    ]

    const init = useCallback(async () => {
        try {
            const commonService = new CommonService();
            let data = await commonService.getProductStores(userProfile.info.partyId);
            let values = [];
            data.productStoreList.forEach(element => {
                let value = {
                    name: element['productStoreId'],
                    code: element['productStoreId']
                }
                values.push(value);
            });

            values.push({
                name: t('all'),
                code: null
            });
            setStoreItems(values);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: 'Error Message', detail: error, life: 5000 });
        }
    }, [toast, userProfile.info.partyId, t]);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <div className="p-grid dashboard">
            <div className="p-col-12 p-md-8">
                <SalesLineChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
            <div className="p-col-12 p-md-4">
                <SalesOrderInWeek height={height} />
            </div>

            <div className="p-col-12 p-md-8">
                <OrdersLineChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
            <div className="p-col-12 p-md-4">
                <StatisticSalesInWeek height={height} />
            </div>

            <div className="p-col-12 p-md-8">
                <SalesProductQuantityLineChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
            <div className="p-col-12 p-md-4">
                <SalesPieChart options={options} cycles={cycles} height={height} />
            </div>

            <div className="p-col-12 p-md-6">
                <ProductQuantityBarChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
            <div className="p-col-12 p-md-6">
                <ProductValueBarChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>

            <div className="p-col-12 p-md-6">
                <StoreValueBarChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
            <div className="p-col-12 p-md-6">
                <SalesmanValueBarChart options={options} cycles={cycles} storeItems={storeItems} height={height} />
            </div>
        </div>
    );
};
