import React, { useCallback } from 'react';
import { NavLink } from 'react-router-dom'
import classNames from 'classnames';
import { CSSTransition } from 'react-transition-group';
import { Ripple } from 'primereact/ripple';
import { Tooltip } from 'primereact/tooltip';

const AppSubmenu = (props) => {

    
    const onMenuItemClick = (event, item, index) => {
        if (item.disabled) {
            event.preventDefault();
            return true;
        }

        if (props.root && props.onRootItemClick) {
            props.onRootItemClick({
                originalEvent: event,
                item: item
            });
        }

        //execute command
        if (item.command) {
            item.command({ originalEvent: event, item: item });
            //event.preventDefault();
        }

        switch(item.level){
            case 'module':
                props.setActiveModuleIndex(index);
                break;
            case 'feature':
                props.setActiveModuleFeatureIndex(index);
                break;
            case 'subFeature':
                props.setActiveSubModuleFeatureIndex(index);
                break;
            default:
        }

        if (!item.items) {
            if(props.menuMode !== 'static') {
                const ink = getInk(event.currentTarget);
                if (ink) {
                    removeClass(ink, 'p-ink-active');
                }
            }
        }

        if (props.onMenuItemClick) {
            props.onMenuItemClick({
                originalEvent: event,
                item: item
            });
        }
    };

    const getInk = (el) => {
        for (let i = 0; i < el.children.length; i++) {
            if (typeof el.children[i].className === 'string' && el.children[i].className.indexOf('p-ink') !== -1) {
                return el.children[i];
            }
        }
        return null;
    }

    const removeClass = (element, className) => {
        if (element.classList)
            element.classList.remove(className);
        else
            element.className = element.className.replace(new RegExp('(^|\\b)' + className.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
    }

    const onMenuItemMouseEnter = (item,index) => {
        if (props.root && props.menuActive && isHorizontalOrSlim() && !isMobile()) {
            switch(item.level){
                case 'module':
                    props.setActiveModuleIndex(index);
                    break;
                case 'feature':
                    props.setActiveModuleFeatureIndex(index);
                    break;
                case 'subFeature':
                    props.setActiveSubModuleFeatureIndex(index);
                    break;
                default:
            }
        }
    };

    const visible = (item) => {
        return typeof item.visible === "function" ? item.visible() : item.visible !== false;
    };

    const isHorizontalOrSlim = useCallback(() => {
        return (props.menuMode === 'horizontal' || props.menuMode === 'slim');
    }, [props.menuMode]);

    const isMobile = useCallback(() => {
        return window.innerWidth < 1025;
    }, []);

    const isHorizontal = () => {
        return props.menuMode === 'horizontal';
    }
    const isSlim = () => {
        return props.menuMode === 'slim';
    }

    const getLink = (item, index) => {
        const menuitemIconClassName = classNames('layout-menuitem-icon', item.icon);
        const badgeClassName = classNames('p-badge p-component p-badge-no-gutter', item.badgeClassName);
        const content = (
            <>
                <i className={menuitemIconClassName}></i>
                <span className="layout-menuitem-text">{item.label}</span>
                { item.badge && <span className={badgeClassName}>{item.badge}</span>}
                { item.items && <i className="pi pi-fw pi-angle-down layout-submenu-toggler"></i>}
                <Ripple />
            </>
        );
        const commonLinkProps = {
            'style': item.style,
            'className': classNames(item.className, 'p-ripple tooltip', { 'p-disabled': item.disabled, 'p-link': !item.to }),
            'target': item.target,
            'onClick': (e) => onMenuItemClick(e, item, index),
            'onMouseEnter': () => onMenuItemMouseEnter(item,index)
        }

        if (item.url) {
            return <a data-pr-tooltip={props.root && item.label} href={item.url} rel="noopener noreferrer" {...commonLinkProps}>{content}</a>
        }
        else if (!item.to) {
            return <a data-pr-tooltip={props.root && item.label} type="button" {...commonLinkProps}>{content}</a>
        }

        return <NavLink data-pr-tooltip={props.root && item.label} to={item.to} exact activeClassName="router-link-active" {...commonLinkProps}>{content}</NavLink>;
    };

    const getItems = () => {
        const transitionTimeout = props.root ? 0 : { enter: 1000, exit: 450 };
        return props.items.map((item, i) => {
            if (visible(item)) {
                let active;
                let activeIndex;
                switch(item.level){
                    case 'module':
                        active = props.activeModuleIndex === i;
                        activeIndex = props.activeModuleIndex;
                        break;
                    case 'feature':
                        active = props.activeModuleFeatureIndex === i;
                        activeIndex = props.activeModuleFeatureIndex;
                        break;
                    case 'subFeature':
                        active = props.activeSubModuleFeatureIndex === i;
                        activeIndex = props.activeSubModuleFeatureIndex;
                        break;
                    default: 
                }
                let styleClass = classNames(item.badgeStyleClass, { 'active-menuitem': active }, { 'layout-root-menuitem': props.root });
                const link = getLink(item, i);
                let tooltip = props.root && <div>
                    <span className="layout-menuitem-text" style={{ textTransform: 'uppercase' }}>{item.label}</span>
                </div>
                
                return (
                    

                    <li key={item.label || i} className={styleClass} role="menuitem" >
                        {link}
                        {tooltip}
                        <CSSTransition classNames="layout-submenu-container" timeout={transitionTimeout} in={item.items && (props.root && !((isHorizontal() || isSlim()) && !isMobile() && (!isSlim() || (isSlim() && activeIndex !== null))) ? true : active)} unmountOnExit>
                            <AppSubmenu items={visible(item) && item.items} onMenuItemClick={props.onMenuItemClick} menuMode={props.menuMode} menuActive={props.menuActive} parentMenuItemActive={active}
                                activeModuleFeatureIndex={props.activeModuleFeatureIndex}
                                activeSubModuleFeatureIndex={props.activeSubModuleFeatureIndex}
                                setActiveModuleFeatureIndex={props.setActiveModuleFeatureIndex}
                                setActiveSubModuleFeatureIndex={props.setActiveSubModuleFeatureIndex}
                            />
                        </CSSTransition>
                    </li>

                )
            }

            return null;
        })
    };

    if (!props.items) {
        return null;
    }

    const items = getItems();

    return (
        <>
            <ul role="menu" className={props.className}>{items}</ul>
            { isSlim() && props.root && <Tooltip target="li:not(.active-menuitem)>.tooltip" />}
        </>
    );

}

const AppMenu = (props) => {
    return <AppSubmenu items={props.model} className="layout-menu"
        menuActive={props.active} onRootItemClick={props.onRootMenuItemClick}
        root={true} menuMode={props.menuMode}
        parentMenuItemActive={true}
        activeModuleIndex={props.activeModuleIndex}
        activeModuleFeatureIndex={props.activeModuleFeatureIndex}
        activeSubModuleFeatureIndex={props.activeSubModuleFeatureIndex}
        setActiveModuleIndex={props.setActiveModuleIndex}
        setActiveModuleFeatureIndex={props.setActiveModuleFeatureIndex}
        setActiveSubModuleFeatureIndex={props.setActiveSubModuleFeatureIndex} />

}

export default AppMenu;
