import React from 'react';
import { useTranslation } from 'react-i18next';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { Controller, useForm } from 'react-hook-form';
import { Dropdown } from 'primereact/dropdown';
import { classNames } from 'primereact/utils';

import { Calendar } from 'primereact/calendar';
import { InputNumber } from 'primereact/inputnumber';

export const ProductVendorAdd = (props) => {
  const { display, handleHide, onSubmit, vendorParties, currencyUoms} = props;
  const { t } = useTranslation('common');

const msgError = t('valid.validFieldRequire');

  const defaultValues = {
    vendorPartyId: null,
    minQuantity: null,
    fromDate: null,
    thruDate: null,
    priceUomId: null,
    purchasePrice: null
  };
  const { control, formState: { errors, isSubmitting }, handleSubmit, reset } = useForm({ defaultValues });
  
  const renderFooterAdd = () => {
    return (
      <>
        <Button label={t("save")} icon="pi pi-check" disabled={isSubmitting}
            onClick={handleSubmit((data) => onSubmit(data, reset))}/>
      </>
    );
  }
  const handleHideDialog = () => {
    handleHide();
    reset();
  }
  const getFormErrorMessage = (name) => {
    return errors[name] && <small className="p-error">{errors[name]?.message}</small>
  };

  return (
    <>
      <Dialog header={t("product.addNewProductVendor")} visible={display} onHide={handleHideDialog}  
      breakpoints={{'960px': '75vw'}} style={{width: '60vw'}} footer={renderFooterAdd}>
        <form>
          <div className="p-grid">
            <div className="p-col-12">
              <div className="p-fluid">
                <div className="p-grid">
                  <div className="p-col-12 p-md-6">
                    <div className="p-field p-grid">
                      <label 
                        htmlFor="vendorPartyId" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0 required"
                      >
                        {t('vendor.label')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="vendorPartyId" 
                          control={control} 
                          rules={{ required: msgError }} 
                          render={({ field, fieldState }) => (
                            <Dropdown
                                className={classNames({ 'p-invalid': fieldState.invalid })}
                                id={field.name}
                                name={field.name}
                                value={field.value} 
                                options={vendorParties} 
                                onChange={(e) => {
                                    field.onChange(e.value);
                                }}
                                optionLabel="name"
                                optionValue="code"
                            />
                          )}
                        />
                        {getFormErrorMessage('vendorPartyId')}
                      </div>
                    </div>

                    <div className="p-field p-grid">
                      <label 
                        htmlFor="fromDate" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0 required"
                      >
                        {t('productPrice.fromDate')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="fromDate" 
                          control={control}
                          rules={{ required: msgError }} 
                          render={({ field, fieldState }) => (
                              <Calendar
                                className={classNames({ 'p-invalid': fieldState.invalid })}
                                inputId={field.name}
                                name={field.name}
                                value={field.value}
                                onChange={(e) => {
                                  field.onChange(e.value);
                                }}
                                showIcon
                                showButtonBar
                              />
                          )} />
                          {getFormErrorMessage("fromDate")}
                      </div>
                    </div>
                  
                    <div className="p-field p-grid">
                      <label 
                        htmlFor="thruDate" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0"
                      >
                        {t('productPrice.thruDate')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="thruDate" 
                          control={control} 
                          render={({ field }) => (
                              <Calendar
                                inputId={field.name}
                                name={field.name}
                                value={field.value}
                                onChange={(e) => {
                                  field.onChange(e.value);
                                }}
                                showIcon
                                showButtonBar
                              />
                          )} 
                        />
                      </div>
                    </div>
                  </div>
                  <div className="p-col-12 p-md-6">
                    <div className="p-field p-grid">
                      <label 
                        htmlFor="priceUomId" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0 required"
                      >
                        {t('acc.newApInvoice.currencyUOM')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="priceUomId" 
                          control={control} 
                          rules={{ required: msgError }} 
                          render={({ field, fieldState }) => (
                            <Dropdown
                                className={classNames({ 'p-invalid': fieldState.invalid })}
                                id={field.name}
                                name={field.name}
                                value={field.value} 
                                options={currencyUoms} 
                                onChange={(e) => {
                                    field.onChange(e.value);
                                }}
                                optionLabel="name"
                                optionValue="code"
                                filter 
                                showClear
                                filterBy="name"
                            />
                          )}
                        />
                        {getFormErrorMessage('priceUomId')}
                      </div>
                    </div>
                      
                    <div className="p-field p-grid">
                      <label 
                        htmlFor="minQuantity" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0 required"
                      >
                        {t('product.minQuantity')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="minQuantity" 
                          control={control} 
                          rules={{ required: msgError }}
                          render={({ field, fieldState }) => (
                            <InputNumber
                              className={classNames({ 'p-invalid': fieldState.invalid })}
                              id={field.name}
                              name={field.name}
                              value={field.value}
                              onValueChange={(e) => {
                                  field.onChange(e.value);
                              }}
                              showButtons
                              min={0}
                            />
                          )} 
                        />
                        {getFormErrorMessage("minQuantity")}
                      </div>
                    </div>
                      
                    <div className="p-field p-grid">
                      <label 
                        htmlFor="purchasePrice" 
                        className="p-col-12 p-mb-2 p-md-4 p-mb-md-0 required"
                      >
                        {t('product.purchasePrice')}
                      </label>
                      <div className="p-col-12 p-md-8">
                        <Controller 
                          name="purchasePrice" 
                          control={control}
                          rules={{ required: msgError }} 
                          render={({ field, fieldState }) => (
                            <InputNumber
                              className={classNames({ 'p-invalid': fieldState.invalid })}
                              id={field.name}
                              name={field.name}
                              value={field.value}
                              onValueChange={(e) => {
                                  field.onChange(e.value);
                              }}
                              showButtons
                              min={0}
                            />
                          )} 
                        />
                        {getFormErrorMessage("purchasePrice")}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      </Dialog>
    </>
  );
}
