import 'primeflex/primeflex.css';
import React, {useEffect, useState} from 'react';
import {Card} from 'primereact/card';
import {ShipmentService} from '../../../../service/ShipmentService';
import {useParams} from 'react-router-dom';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import Moment from 'react-moment';
import {useTranslation} from 'react-i18next';

import { Button } from 'primereact/button';

const ExportFacilityDetail = () => {
  let {id} = useParams();
  const [t] = useTranslation();

  const [shipmentDetail, setShipmentDetail] = useState({});
  const [shipmentContacts, setShipmentContacts] = useState({});
  const [shipmentItemSources, setShipmentItemSources] = useState({});

  const [orderId, setOrderId] = useState(null); // first order of shipment

  useEffect(() => {
    const shipmentService = new ShipmentService();
    shipmentService.getShipment(id).then((data) => {
      setShipmentDetail(data);
    });
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    const shipmentService = new ShipmentService();
    shipmentService.getShipmentContacts(id).then((data) => {
      setShipmentContacts(data);
    });
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    const shipmentService = new ShipmentService();
    shipmentService.getShipmentItemSourecs(id, {pageNoLimit: true}).then((data) => {
      setShipmentItemSources(data);
      setOrderId(data[0]?.orderId);
    });
  }, []);// eslint-disable-line react-hooks/exhaustive-deps

  const rowIndexBodyTemplate = (rowData, props) => {
    let index = parseInt(props.rowIndex + 1);
    return (
      <React.Fragment>
        <span>{index}</span>
      </React.Fragment>
    );
  };

  const quantityBodyTemplate = (rowData) => {  
    return <span>{rowData?.quantity}</span>;
  };

  return (
    <>
      <Card>
        <div className="p-d-flex p-jc-between">
          <div>
            {' '}
            <h5>{t('common:shipment.title')}</h5>
          </div>
          <div>
            <div className="p-d-flex">
              <div className="p-mr-2 p-order-3"><Button label="Button" icon="pi pi-check" /></div>
              <div className="p-mr-2 p-order-1"><Button label="Button" icon="pi pi-check" /></div>
              <div className="p-mr-2 p-order-2"><Button label="Button" icon="pi pi-check" /></div>
            </div>
          </div>
        </div>
  
        <div className="p-grid">
          {/* Lable left */}
          <div className="p-col-12 p-md-6">
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.oderId')}
              </label>
              <div className="p-col">
                <span>{orderId}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.customerId')}
              </label>
              <div className="p-col">
                <span>{shipmentDetail?.toParty?.partyId}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.customerName')}
              </label>
              <div className="p-col">
                <span>
                  {shipmentDetail?.toParty?.person?.lastName} {shipmentDetail?.toParty?.person?.firstName}
                  {shipmentDetail?.toParty?.organization?.organizationName}
                </span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.addressCustomer')}
              </label>
              <div className="p-col">
                <span></span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.phoneCustomer')}
              </label>
              <div className="p-col">
                <span>{shipmentContacts?.toContactInfo?.telecomNumber?.contactNumber}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.addressDelivery')}
              </label>
              <div className="p-col">
                <span>
                  {shipmentContacts?.toContactInfo?.postalAddress?.address1},
                  {shipmentContacts?.toContactInfo?.postalAddress?.wardId},
                  {shipmentContacts?.toContactInfo?.postalAddressStateGeo?.geoName},
                  {shipmentContacts?.toContactInfo?.postalAddressCountryGeo?.geoName}
                </span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.salesClerk')}
              </label>
              <div className="p-col">
                <span></span>
              </div>
            </div>
          </div>
          {/* Lable left */}
          {/* Lable right */}
          <div className="p-col-12 p-md-6">
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.expostId')}
              </label>
              <div className="p-col">
                <span>{shipmentDetail?.shipmentId}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.billType')}
              </label>
              <div className="p-col">
                <span>{shipmentDetail?.shipmentTypeEnumId}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.status')}
              </label>
              <div className="p-col">
                <span>{shipmentDetail?.statusId}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.fromFacility')}
              </label>
              <div className="p-col">
                <span>{shipmentContacts?.originFacility?.facilityName}</span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.adressFacility')}
              </label>
              <div className="p-col">
                <span></span>
              </div>
            </div>

            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.dateCreate')}
              </label>
              <div className="p-col">
                <Moment format="DD/MM/YYYY HH:mm">{shipmentDetail?.entryDate}</Moment>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.exportFacilityDate')}
              </label>
              <div className="p-col">
                <span></span>
              </div>
            </div>
            <div className="p-field p-grid">
              <label className="p-col-fixed" style={{width: '120px'}}>
                {t('common:shipment.deliveryDate')}
              </label>
              <div className="p-col">
                {/* <Moment  format="DD/MM/YYYY HH:mm">{shipmentContacts.shipment?.estimatedArrivalDate}</Moment> */}
              </div>
            </div>
          </div>
          {/* Lable right */}
        </div>
        <DataTable value={shipmentItemSources} showGridlines responsiveLayout="scroll">
          <Column header="STT" body={rowIndexBodyTemplate} headerStyle={{width: '4rem'}} />
          <Column field="shipmentId" header={t('common:shipment.commodityId')}></Column>
          <Column field="productId" header={t('common:shipment.commodityName')}></Column>
          <Column field="" header={t('common:shipment.commodityName')}></Column>
          <Column field="" header={t('common:shipment.unit')}></Column>
          <Column field="uom" header={t('common:shipment.specification')}></Column>
          <Column field="quantity" header={t('common:shipment.quantitySold')} body={quantityBodyTemplate}></Column>
          <Column field="" header={t('common:shipment.quantitySold')}></Column>
          <Column field="" header={t('common:shipment.exportQuantity')}></Column>
          <Column field="" header={t('common:shipment.actualDelivereduantity')}></Column>
          <Column field="" header={t('common:shipment.unitPrice')}></Column>
          <Column field="" header={t('common:shipment.djusted')}></Column>
          <Column field="" header={t('common:shipment.intoMoney')}></Column>
        </DataTable>
      </Card>
    </>
  );
};

export default ExportFacilityDetail;
