import React from 'react';
import { useTranslation } from 'react-i18next';

import { InputTextController } from '../../PromotionComponent/InputTextController';
import { DropdownController } from '../../PromotionComponent/DropdownController';
import { InputNumberController } from '../../PromotionComponent/InputNumberController';
import { CalendarController } from '../../PromotionComponent/CalendarController';

export const ProductInfo = (props) => {
  const {
    enumProductTypes,
    productCategories,
    quantityUoms, 
    currencyUoms, 
    errors, 
    control
  } = props;

  const { t } = useTranslation('common');
  const getFormErrorMessage = (name) => {
    return errors[name] && <small className="p-error">{errors[name]?.message}</small>
  };

  return (
    <>
      <div className="p-col-12 p-md-4">
        <DropdownController id='productTypeEnum' label={t('product.type')} checkErr  
          options={enumProductTypes} control={control} getFormErrorMessage={getFormErrorMessage} />
      </div>
      <div className="p-col-12 p-md-4">
        <InputTextController id='productId' label={t('product.id')}
          control={control} getFormErrorMessage={getFormErrorMessage} />
        <InputTextController id='productName' label={t('product.name')} checkErr
          control={control} getFormErrorMessage={getFormErrorMessage} />
        <DropdownController id='productCategory' label={t('product.productCategory')}   
          options={productCategories} control={control} getFormErrorMessage={getFormErrorMessage} />
        <CalendarController id='salesDiscontinuationDate' label={t('product.salesDiscontinuationDate')} 
          showIcon showButtonBar control={control} getFormErrorMessage={getFormErrorMessage}/>
      </div>
      <div className="p-col-12 p-md-4">
        <DropdownController id='quantityUom' label={t('product.baseUnit')} checkErr  
          options={quantityUoms} control={control} getFormErrorMessage={getFormErrorMessage} 
          filter showClear filterBy="name"/>
        <InputNumberController id='listPrice' label={t('product.listPrice')} checkErr
          control={control} getFormErrorMessage={getFormErrorMessage} showButtons min={0}/>
        <InputNumberController id='currentPrice' label={t('product.currentPrice')} checkErr
          control={control} getFormErrorMessage={getFormErrorMessage} showButtons min={0}/>
        <DropdownController id='currencyUom' label={t('acc.newApInvoice.currencyUOM')} checkErr
          options={currencyUoms} control={control} getFormErrorMessage={getFormErrorMessage} 
          filter showClear filterBy="name" />
      </div>     
    </>
  );
}
