import React, { useState, useEffect, useRef, useCallback, useContext } from 'react';
import { useHistory } from 'react-router-dom';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from "primereact/inputtext";
import { MultiSelect } from "primereact/multiselect";
import { Button } from 'primereact/button';

import { useTranslation } from 'react-i18next';

import { ToastContext } from "../../../App";
import { UserProfileContext } from "../../../App";

import { SalesmanService } from '../../../service/SalesmanService';

export const SalesmanList = () => {
    const { t } = useTranslation('common');
    const toast = useContext(ToastContext);
    const userProfile = useContext(UserProfileContext);
    const history = useHistory();

    const [loading, setLoading] = useState(false);
    
    const [lazyParams, setLazyParams] = useState({ first: 0, rows: 10, page: 0, sortField: "partyId", sortOrder: -1 });
    const [totalRecords, setTotalRecords] = useState(0);
    const [salesmans, setSalesmans] = useState(null);

    const [partyIdIdFilter, setPartyIdIdFilter] = useState(null);
    const [fullNameFilter, setFullNameFilter] = useState(null);
    const [postalAddressFilter, setPostalAddressFilter] = useState(null);
    const [emailAddressFilter, setEmailAddressFilter] = useState(null);
    const [telecomNumberFilter, setTelecomNumberFilter] = useState(null);
    // const [webAddressFilter, setWebAddressFilter] = useState(null);

    const genders = [
        {
            id: 'M',
            description: t('male') 
        },
        {
            id: 'F',
            description: t('female') 
        }
    ];

    const [selectedGender, setSelectedGender] = useState({
        matchMode: "in",
        value: [],
        id: "gender",
    });

    const dt = useRef(null);
    const typingTimeoutRef = useRef(null);
    const emptyMessage = <p style={{textAlign: 'center'}}>{t('noDataFound')}</p>;

    const getParams = useCallback((lazyParams) => {
        const params = {};
        const filters = lazyParams?.filters;

        if (lazyParams?.page && lazyParams?.page >= 0) params.pageIndex = lazyParams?.page;
        if (lazyParams?.rows) params.pageSize = lazyParams?.rows;
        if (lazyParams?.sortField && lazyParams?.sortOrder) params.orderByField = lazyParams?.sortOrder === 1 ? lazyParams?.sortField : "-" + lazyParams?.sortField;
        if (filters && Object.keys(filters).length !== 0) {
            for (const key in filters) {
                if (Object.hasOwnProperty.call(filters, key)) {
                    const filter = filters[key];
                    const matchMode = filter.matchMode;
                    const value = filter.value;
                    if (value == null || value === "") continue;
                    switch (matchMode) {
                        case "contains":
                            params[key] = value;
                            break;
                        case "in":
                            params[key + "s"] = value.join();
                            break;
                        default:
                            break;
                    }
                }
            }
        }

        return params;
    }, []);

    const initData = useCallback(async (lazyParams) => {
        let salesmanService = new SalesmanService();
        setLoading(true);

        try {
            let params = getParams(lazyParams);
            let data = await salesmanService.getSaleEmployeeList(params);   

            setTotalRecords(data.totalRecords);
            setSalesmans(data.salesmans);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error.response.data.errors, life: 5000 });
        } finally {
            setLoading(false);
        }
    }, [getParams, toast, t]);

    useEffect(() => {
        initData(lazyParams);

        document.title = t('title.SalesmanList');

        return () => {
            document.title = t('title.homepage');
        }
    }, [lazyParams, initData, t]);

    const onPage = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e });
    }, [lazyParams]);

    const onFilter = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e, first: 0 });
    }, [lazyParams]);
    
    const onSort = useCallback((e) => {
        setLazyParams({ ...lazyParams, ...e});
    }, [lazyParams]);

    const onFilterTextChange = useCallback((e) => {
        let obj = e.target;
        switch (obj.id) {
               
            case 'partyId':
                setPartyIdIdFilter(obj.value);
                break;
            case 'fullName':
                setFullNameFilter(obj.value);
                break;
            case 'postalAddress':
                setPostalAddressFilter(obj.value);
                break;
            case 'emailAddress':
                setEmailAddressFilter(obj.value);
                break;
            case 'telecomNumber':
                setTelecomNumberFilter(obj.value);
                break;
            case 'webAddress':
                // setWebAddressFilter(obj.value);
                break;
            default:
                break;
        }

        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }
        typingTimeoutRef.current = setTimeout(() => {
            dt.current.filter(obj.value, obj.id, 'contains');
        }, 400);
    }, []);
    const onMultiSelectHide = (id) => {
        switch (id) {
          case "gender":
            dt.current.filter(selectedGender.value, "gender", "in");
            break;
          default:
            console.log("not found ! " + id);
            break;
        }
    };

    const onMultiSelectFilterChange = (e) => {
        let obj = e.target;
        //console.log(e.target);
        switch (obj.id) {
          case "gender":
            setSelectedGender({ ...selectedGender, value: obj.value });
            break;
          default:
            console.log("not multi  found! ", obj);
            break;
        }
    };

    const getItemTemplate = (option) => (
        <span id={option.id}>{option.description}</span>
    );

    const getOptions = (id) => {
        switch (id) {
          case "gender":
            return genders;
          default:
            break;
        }
    };

    const multiSelectFilter = (id, value) => (
        <MultiSelect
          id={id}
          value={value}
          options={getOptions(id)}
          itemTemplate={getItemTemplate}
          onChange={onMultiSelectFilterChange}
          onHide={() => {
            onMultiSelectHide(id);
          }}
          optionLabel="description"
          optionValue="id"
          placeholder={t("common:all")}
          style={{ width: "100%" }}
        ></MultiSelect>
    );

    const textFilter = useCallback((id, value) => <InputText type='text' id={id} value={value || ''} onChange={onFilterTextChange}></InputText>, [onFilterTextChange]);

    const bodyIdTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                <a href={`#/sales/salesman/detail/${data.partyId}`} style={{color: '#2196f3'}}>
                    {data[props.field]}
                </a>
            </>
        );
    }, []);

    const bodyTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    }, []);

    const bodyGenderTemplate = useCallback((data, props) => {
        let gender = genders.find(elm => elm.id === data[props.field]);

        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {gender?.description}
            </>
        );
    }, []);
    
    return (
        <div className="p-grid table-demo">
            <div className="p-col-12">
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-8">
                            <h5>{t('title.SalesmanList')}</h5>
                        </div>
                        <div className="p-col-12 p-md-4">
                            <div className="p-grid">
                                <div className="p-col-12 p-md-8"></div>
                                <div className="p-col-12 p-md-4">
                                    {
                                        userProfile.roles.includes('ADMIN') && 
                                        <Button label={t('common:homepage.sales.Create')} icon="pi pi-plus" className="p-button-text" onClick={(e) => history.push("/sales/promotion/create")} />
                                    }
                                </div>
                            </div>
                        </div>
                    </div>

                    <DataTable 
                        value={salesmans} paginator className="p-datatable-customers" dataKey="productId"
                        ref={dt} filters={lazyParams.filters} lazy first={lazyParams.first} rows={lazyParams.rows}
                        totalRecords={totalRecords} rowsPerPageOptions={[5, 10, 20, 50, 100]} onPage={onPage} onFilter={onFilter} onSort={onSort}
                        sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder} rowHover 
                        paginatorTemplate="RowsPerPageDropdown CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink"
                        currentPageReportTemplate="{first} - {last} of {totalRecords}" 
                        emptyMessage={emptyMessage}
                        loading={loading} paginatorPosition="bottom"
                    >
                        <Column field="partyCode" header={t('salesman.id')} sortable body={bodyIdTemplate} filter 
                            filterElement={textFilter("partyCode", partyIdIdFilter)}></Column>
                        <Column field="fullName" header={t('salesman.fullName')} sortable filter body={bodyTemplate} 
                            filterElement={textFilter("fullName", fullNameFilter)}></Column>
                        <Column field="gender" header={t('salesman.gender')} sortable filter body={bodyGenderTemplate} 
                            filterElement={multiSelectFilter( "gender", selectedGender.value)}></Column>
                        
                        <Column field="postalAddress" header={t('salesman.postalAddress')} sortable filter body={bodyTemplate} 
                            filterElement={textFilter("postalAddress", postalAddressFilter)}></Column>
                        <Column field="emailAddress" header={t('salesman.emailAddress')} sortable filter body={bodyTemplate} 
                            filterElement={textFilter("emailAddress", emailAddressFilter)}></Column>
                        <Column field="telecomNumber" header={t('salesman.telecomNumber')} sortable filter body={bodyTemplate} 
                            filterElement={textFilter("telecomNumber", telecomNumberFilter)}></Column>
                        {/* <Column field="webAddress" header={t('salesman.webAddress')} sortable filter body={bodyTemplate} 
                            filterElement={textFilter("webAddress", webAddressFilter)}></Column> */}
                    </DataTable>
                </div>
            </div>
        </div>
    );
};
